import { type ZodObject, type ZodSchema, type ZodTuple } from "zod";
import { type ComponentType as ReactComponentType, type ReactNode } from 'react';
import {
    type FieldConfigItem,
  } from "@/components/ui/auto-form/types";

export type {
    AutoFormInputComponentProps,
    FieldConfigItem,
  } from "@/components/ui/auto-form/types";

// Enhanced prop value types that can accommodate React props, variables, and common data types
export type PropValue = 
  | ReactNode 
  | VariableReference 
  | Record<string, any> 
  | any[] 
  | string 
  | number 
  | boolean 
  | null 
  | undefined;

// Generic component props that allow for flexible but safer typing
export type ComponentProps<TProps extends Record<string, PropValue> = Record<string, PropValue>> = TProps;

// Enhanced ComponentLayer with generic prop typing
// Children can be:
// - ComponentLayer[] for nested components
// - string for text content
// - VariableReference for dynamic text content bound to a variable
export interface ComponentLayer<TProps extends Record<string, PropValue> = Record<string, PropValue>> {
    id: string;
    name?: string;
    type: string;
    props: ComponentProps<TProps>;
    children: ComponentLayer[] | string | VariableReference;
}

// Variable value types - more specific than before
// 'function' type variables reference a key in the FunctionRegistry
export type VariableValueType = 'string' | 'number' | 'boolean' | 'function';

// Type-safe variable values based on their type
// For function type, the value is the FunctionRegistry key
export type VariableValue<T extends VariableValueType> = 
  T extends 'string' ? string :
  T extends 'number' ? number :
  T extends 'boolean' ? boolean :
  T extends 'function' ? string :  // References functionRegistry key
  never;

// Enhanced Variable interface with generic typing
export interface Variable<T extends VariableValueType = VariableValueType> {
  id: string;
  name: string;
  type: T;
  defaultValue: VariableValue<T>;
}

// Variable reference marker for props
export interface VariableReference {
  __variableRef: string;
}

// Default variable binding configuration
export interface DefaultVariableBinding {
  propName: string;
  variableId: string;
  immutable?: boolean;
}

// Enhanced registry entry with better component typing
export interface RegistryEntry<T extends ReactComponentType<any>> {
  component?: T;
  schema: ZodObject<any> | ZodSchema<any>;
  from?: string;
  isFromDefaultExport?: boolean;
  defaultChildren?: ComponentLayer[] | string | VariableReference;
  defaultVariableBindings?: DefaultVariableBinding[];
  fieldOverrides?: Record<string, FieldConfigFunction>;
  /** 
   * If defined, this component can only be added as a child of the specified parent types.
   * Used to filter component options in the add popover and validate drag-and-drop.
   * Example: TabsTrigger has childOf: ["TabsList"]
   */
  childOf?: string[];
}

// Improved field config function type
export type FieldConfigFunction = (layer: ComponentLayer, allowVariableBinding?: boolean) => FieldConfigItem;

// Enhanced ComponentRegistry with better typing
export type ComponentRegistry = Record<string, RegistryEntry<ReactComponentType<any>>>;

// Type-safe layer change handler with registry awareness
export type LayerChangeHandler<TRegistry extends ComponentRegistry = ComponentRegistry> = 
  (layers: Array<ComponentLayer & {
    type: keyof TRegistry;
  }>) => void;

// Type-safe variable change handler  
export type VariableChangeHandler = (variables: Variable[]) => void;

// Helper types for extracting component props from registry
export type ExtractComponentProps<
  TRegistry extends ComponentRegistry,
  TComponentName extends keyof TRegistry
> = TRegistry[TComponentName] extends RegistryEntry<ReactComponentType<infer TProps>>
  ? TProps
  : never;

// Type-safe layer change handler with registry awareness
export type TypedLayerChangeHandler<TRegistry extends ComponentRegistry> = 
  (layers: Array<ComponentLayer & {
    type: keyof TRegistry;
  }>) => void;

// Utility function types for creating variables
export type CreateVariable = <T extends VariableValueType>(
  id: string,
  name: string,
  type: T,
  defaultValue: VariableValue<T>
) => Variable<T>;

// Utility to check if a value is a variable reference
export function isVariableReference(value: any): value is VariableReference {
  return typeof value === 'object' && value !== null && '__variableRef' in value;
}

// Type-safe variable creation helper
export const createVariable: CreateVariable = <T extends VariableValueType>(
  id: string,
  name: string,
  type: T,
  defaultValue: VariableValue<T>
): Variable<T> => ({
  id,
  name,
  type,
  defaultValue,
});

/**
 * Block definition for UI Builder.
 * Blocks are pre-built component compositions that can be inserted as templates.
 */
export interface BlockDefinition {
  /** Unique block name, e.g., "login-01" */
  name: string;
  /** Block category for grouping in UI, e.g., "login", "sidebar", "chart" */
  category: string;
  /** Human-readable description */
  description?: string;
  /** The ComponentLayer tree to insert when this block is selected */
  template: ComponentLayer;
  /** Optional preview image URL */
  thumbnail?: string;
  /** Required shadcn components for this block */
  requiredComponents?: string[];
}

/**
 * Block registry type - a record of block name to block definition
 */
export type BlockRegistry = Record<string, BlockDefinition>;

/**
 * Function definition for the function registry.
 * Describes a function that can be bound to component event handlers.
 */
export interface FunctionDefinition {
  /** Human-readable name for the function */
  name: string;
  /** Zod schema describing the function parameters (use z.tuple for ordered args, z.object for named params) */
  schema: ZodTuple<any, any> | ZodObject<any> | ZodSchema<any>;
  /** The actual function to call at runtime */
  fn: (...args: any[]) => any;
  /** Optional description shown in the UI */
  description?: string;
  /** 
   * Optional TypeScript type signature for code generation.
   * Use this when the Zod schema uses z.custom<T>() or other types 
   * that can't be automatically inferred at runtime.
   * @example "(e: React.FormEvent<HTMLFormElement>) => void"
   * @example "(data: { name: string; email: string }) => Promise<void>"
   */
  typeSignature?: string;
}

/**
 * Function registry type - a record of function ID to function definition.
 * Used to provide callable functions that can be bound to component event handlers.
 */
export type FunctionRegistry = Record<string, FunctionDefinition>;

import React from "react";

import { isPrimitiveComponent } from "@/lib/ui-builder/store/editor-utils";
import { hasLayerChildren } from "@/lib/ui-builder/store/layer-utils";
import type { ComponentRegistry, ComponentLayer, Variable, PropValue } from '@/components/ui/ui-builder/types';
import { isVariableReference } from '@/components/ui/ui-builder/types';
import { resolveVariableReferences, resolveChildrenVariableReference } from "@/lib/ui-builder/utils/variable-resolver";

export interface ServerLayerRendererProps<TRegistry extends ComponentRegistry = ComponentRegistry> {
  /** Optional CSS class for the root container */
  className?: string;
  /** The ComponentLayer tree to render */
  page: ComponentLayer;
  /** Registry mapping component types to their definitions */
  componentRegistry: TRegistry;
  /** Optional variable definitions */
  variables?: Variable[];
  /** Optional variable values to override defaults */
  variableValues?: Record<string, PropValue>;
}

/**
 * ServerLayerRenderer - An SSR-friendly renderer for UI Builder pages.
 * 
 * This component renders ComponentLayer trees without any client-side dependencies,
 * making it suitable for use in React Server Components (RSC), Static Site Generation (SSG),
 * and Server-Side Rendering (SSR).
 * 
 * Unlike LayerRenderer, this component:
 * - Uses no React hooks
 * - Does not access any Zustand stores
 * - Does not include editor functionality (selection, drag-and-drop)
 * - Is a pure functional component
 * 
 * @example
 * ```tsx
 * // In a Next.js Server Component (no "use client" needed)
 * import { ServerLayerRenderer } from '@/components/ui/ui-builder/server-layer-renderer';
 * 
 * export default async function Page() {
 *   const page = await fetchPageFromDatabase();
 *   return (
 *     <ServerLayerRenderer 
 *       page={page} 
 *       componentRegistry={myRegistry}
 *     />
 *   );
 * }
 * ```
 */
export function ServerLayerRenderer<TRegistry extends ComponentRegistry = ComponentRegistry>({
  className,
  page,
  componentRegistry,
  variables = [],
  variableValues,
}: ServerLayerRendererProps<TRegistry>): React.JSX.Element {
  return (
    <div className={className}>
      {renderLayer(page, componentRegistry, variables, variableValues)}
    </div>
  );
}

/**
 * Pure recursive function to render a ComponentLayer tree.
 * No hooks, no side effects - suitable for SSR/RSC.
 */
function renderLayer(
  layer: ComponentLayer,
  componentRegistry: ComponentRegistry,
  variables: Variable[],
  variableValues?: Record<string, PropValue>
): React.ReactNode {
  const componentDefinition = componentRegistry[layer.type as keyof typeof componentRegistry];

  if (!componentDefinition) {
    // Log error in development, but don't break rendering
    if (process.env.NODE_ENV !== 'production') {
      console.error(
        `[ServerLayerRenderer] Component definition not found in registry:`,
        { layerType: layer.type, layerId: layer.id, layerName: layer.name }
      );
    }
    return null;
  }

  // Determine if this is a primitive HTML element or a custom component
  let Component: React.ElementType | undefined = componentDefinition.component;
  let isPrimitive = false;

  if (isPrimitiveComponent(componentDefinition)) {
    Component = layer.type as keyof React.JSX.IntrinsicElements;
    isPrimitive = true;
  }

  if (!Component) {
    return null;
  }

  // Resolve variable references in props
  const resolvedProps = resolveVariableReferences(layer.props, variables, variableValues);

  // Filter out internal metadata props (e.g., __function_onClick, __function_onSubmit)
  // These are used internally for tracking function bindings but should not be passed to components
  const childProps: Record<string, PropValue> = {};
  for (const [key, value] of Object.entries(resolvedProps)) {
    if (!key.startsWith('__function_')) {
      childProps[key] = value;
    }
  }

  // Handle children rendering
  if (hasLayerChildren(layer) && layer.children.length > 0) {
    // Render child layers recursively
    childProps.children = layer.children.map((child) => (
      <React.Fragment key={child.id}>
        {renderLayer(child, componentRegistry, variables, variableValues)}
      </React.Fragment>
    ));
  } else if (isVariableReference(layer.children)) {
    // Resolve variable reference for children
    const resolvedChildren = resolveChildrenVariableReference(
      layer.children, 
      variables, 
      variableValues
    );
    childProps.children = resolvedChildren;
  } else if (typeof layer.children === "string") {
    // String children (text content)
    childProps.children = layer.children;
  }
  // Empty children array or undefined - no children prop needed

  // Render the component with data attributes for testing/debugging
  if (isPrimitive) {
    return (
      <Component 
        key={layer.id}
        id={layer.id} 
        data-testid={layer.id} 
        data-layer-id={layer.id} 
        {...childProps} 
      />
    );
  }

  // Custom components - render directly without error boundary/suspense
  // (Server components handle errors differently)
  return (
    <Component 
      key={layer.id}
      data-testid={layer.id} 
      data-layer-id={layer.id} 
      {...childProps} 
    />
  );
}

export default ServerLayerRenderer;

import React from "react";

import { type EditorConfig, RenderLayer } from "@/components/ui/ui-builder/internal/utils/render-utils";
import { DevProfiler } from "@/components/ui/ui-builder/internal/components/dev-profiler";

import type { Variable, ComponentLayer, ComponentRegistry, PropValue, FunctionRegistry } from '@/components/ui/ui-builder/types';

interface LayerRendererProps<TRegistry extends ComponentRegistry = ComponentRegistry> {
  className?: string;
  page: ComponentLayer;
  editorConfig?: EditorConfig;
  componentRegistry: TRegistry;
  /** Optional variable definitions */
  variables?: Variable[];
  /** Optional variable values to override defaults */
  variableValues?: Record<string, PropValue>;
  /** Optional function registry for resolving function-type variables */
  functionRegistry?: FunctionRegistry;
}

const LayerRenderer = React.memo<LayerRendererProps>(function LayerRenderer({
  className,
  page,
  editorConfig,
  componentRegistry,
  variables,
  variableValues,
  functionRegistry,
}) {

  return (
    <DevProfiler id="LayerRenderer" threshold={30}>
      <div className={className}>
        <RenderLayer 
          layer={page} 
          editorConfig={editorConfig} 
          componentRegistry={componentRegistry} 
          variables={variables}
          variableValues={variableValues}
          functionRegistry={functionRegistry}
        />
      </div>
    </DevProfiler>
  );
}) as <TRegistry extends ComponentRegistry = ComponentRegistry>(
  props: LayerRendererProps<TRegistry>
) => React.JSX.Element;

export default LayerRenderer;

/**
 * Learn more about the UI Builder:
 * https://github.com/olliethedev/ui-builder
