export default UIBuilder;

"use client";

import React, { useCallback, useState, useMemo } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Trash2, Plus, Edit2, Check, X } from "lucide-react";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import type { Variable, FunctionRegistry } from "@/components/ui/ui-builder/types";
import { cn } from "@/lib/utils";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";

const EMPTY_OBJECT = {};

interface VariablesPanelProps {
  className?: string;
}

export const VariablesPanel: React.FC<VariablesPanelProps> = ({
  className,
}) => {
  const { variables, addVariable, updateVariable, removeVariable } =
    useLayerStore();
  const [isAdding, setIsAdding] = useState(false);
  const [editingId, setEditingId] = useState<string | null>(null);
  const incrementRevision = useEditorStore((state) => state.incrementRevision);
  const allowVariableEditing = useEditorStore((state) => state.allowVariableEditing);
  const functionRegistry = useEditorStore((state) => state.functionRegistry);

  const handleAddVariable = useCallback(
    (name: string, type: Variable["type"], defaultValue: any) => {
      addVariable(name, type, defaultValue);
      incrementRevision();
    },
    [addVariable, incrementRevision]
  );

  const handleSetIsAdding = useCallback(() => {
    setIsAdding(true);
  }, []);

  const handleSetIsNotAdding = useCallback(() => {
    setIsAdding(false);
  }, []);

  const handleOnSave = useCallback(
    (id: string, updates: Partial<Omit<Variable, "id">>) => {
      updateVariable(id, updates);
      setEditingId(null);
    },
    [updateVariable]
  );

  const handleOnCancel = useCallback(() => {
    setEditingId(null);
  }, []);

  const handleOnDelete = useCallback(
    (id: string) => {
      removeVariable(id);
      incrementRevision();
    },
    [removeVariable, incrementRevision]
  );

  return (
    <div className={cn("flex flex-col gap-4 p-4", className)}>
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold">Variables</h3>
        {allowVariableEditing && (
          <Button size="sm" onClick={handleSetIsAdding} disabled={isAdding}>
            <Plus className="h-4 w-4 mr-2" />
            Add Variable
          </Button>
        )}
      </div>

      {isAdding && allowVariableEditing && (
        <AddVariableForm
          onSave={handleAddVariable}
          onCancel={handleSetIsNotAdding}
          functionRegistry={functionRegistry}
        />
      )}

      <div className="space-y-2">
        {variables.map((variable) => (
          <VariableCard
            key={variable.id}
            variable={variable}
            isEditing={editingId === variable.id}
            onEdit={setEditingId}
            onSave={handleOnSave}
            onCancel={handleOnCancel}
            onDelete={handleOnDelete}
            editVariables={allowVariableEditing}
            functionRegistry={functionRegistry}
          />
        ))}
      </div>

      {variables.length === 0 && !isAdding && (
        <div className="text-center text-muted-foreground py-8">
          {allowVariableEditing
            ? 'No variables defined. Click "Add Variable" to create one.'
            : "No variables defined."}
        </div>
      )}
    </div>
  );
};

interface AddVariableFormProps {
  onSave: (name: string, type: Variable["type"], defaultValue: any) => void;
  onCancel: () => void;
  functionRegistry: FunctionRegistry | undefined;
}

const AddVariableForm: React.FC<AddVariableFormProps> = ({
  onSave,
  onCancel,
  functionRegistry,
}) => {
  const [name, setName] = useState("");
  const [type, setType] = useState<Variable["type"]>("string");
  const [defaultValue, setDefaultValue] = useState("");
  const [errors, setErrors] = useState<{
    name?: string;
    defaultValue?: string;
  }>(EMPTY_OBJECT);

  // Get function options from the registry
  const functionOptions = useMemo(() => {
    if (!functionRegistry) return [];
    return Object.entries(functionRegistry).map(([id, def]) => ({
      id,
      name: def.name,
      description: def.description,
    }));
  }, [functionRegistry]);

  // Check if function type is available (only when functionRegistry has items)
  const hasFunctions = functionOptions.length > 0;

  const validateForm = useCallback(() => {
    const newErrors: { name?: string; defaultValue?: string } = {};

    if (!name.trim()) {
      newErrors.name = "Name is required";
    }

    if (type === 'function') {
      // For function type, defaultValue is the function ID
      if (!defaultValue) {
        newErrors.defaultValue = "Please select a function";
      }
    } else if (!defaultValue.trim()) {
      newErrors.defaultValue = "Preview value is required";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [name, defaultValue, type]);

  const handleSave = useCallback(() => {
    if (!validateForm()) return;

    let parsedValue: any = defaultValue;
    try {
      if (type === "number") {
        parsedValue = parseFloat(defaultValue) || 0;
      } else if (type === "boolean") {
        parsedValue = defaultValue.toLowerCase() === "true";
      }
      // For 'function' type, defaultValue is already the function ID (string)
    } catch (e) {
      console.warn("Error parsing variable value, keeping as string", e);
    }

    onSave(name, type, parsedValue);
  }, [name, type, defaultValue, onSave, validateForm]);

  const handleNameChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setName(e.target.value);
      if (errors.name) setErrors((prev) => ({ ...prev, name: undefined }));
    },
    [errors.name]
  );

  const handleTypeChange = useCallback(
    (value: string) => {
      setType(value as Variable["type"]);
      // Reset default value when switching types
      setDefaultValue("");
    },
    []
  );

  const handleDefaultValueChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setDefaultValue(e.target.value);
      if (errors.defaultValue)
        setErrors((prev) => ({ ...prev, defaultValue: undefined }));
    },
    [errors.defaultValue]
  );

  const handleFunctionSelect = useCallback(
    (value: string) => {
      setDefaultValue(value);
      if (errors.defaultValue)
        setErrors((prev) => ({ ...prev, defaultValue: undefined }));
    },
    [errors.defaultValue]
  );

  return (
    <Card>
      <CardHeader className="pb-3">
        <CardTitle className="text-sm">Add New Variable</CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        <div>
          <Label htmlFor="var-name">
            Name <span className="text-red-500">*</span>
          </Label>
          <Input
            id="var-name"
            value={name}
            onChange={handleNameChange}
            placeholder="variableName"
            className={errors.name ? "border-red-500" : ""}
          />
          {errors.name && (
            <p className="text-sm text-red-500 mt-1">{errors.name}</p>
          )}
        </div>

        <div>
          <Label htmlFor="var-type">Type</Label>
          <Select value={type} onValueChange={handleTypeChange}>
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="string">String</SelectItem>
              <SelectItem value="number">Number</SelectItem>
              <SelectItem value="boolean">Boolean</SelectItem>
              {hasFunctions && <SelectItem value="function">Function</SelectItem>}
            </SelectContent>
          </Select>
        </div>

        <div>
          {type === 'function' ? (
            <>
              <Label htmlFor="var-function">
                Function <span className="text-red-500">*</span>
              </Label>
              <Select value={defaultValue} onValueChange={handleFunctionSelect}>
                <SelectTrigger className={errors.defaultValue ? "border-red-500" : ""}>
                  <SelectValue placeholder="Select a function..." />
                </SelectTrigger>
                <SelectContent>
                  {functionOptions.map((fn) => (
                    <SelectItem key={fn.id} value={fn.id}>
                      <div className="flex flex-col">
                        <span>{fn.name}</span>
                        {fn.description && (
                          <span className="text-xs text-muted-foreground">{fn.description}</span>
                        )}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.defaultValue && (
                <p className="text-sm text-red-500 mt-1">{errors.defaultValue}</p>
              )}
            </>
          ) : (
            <>
              <Label htmlFor="var-default">
                Preview Value <span className="text-red-500">*</span>
              </Label>
              <Input
                id="var-default"
                value={defaultValue}
                onChange={handleDefaultValueChange}
                placeholder={getPlaceholderForType(type)}
                className={errors.defaultValue ? "border-red-500" : ""}
              />
              {errors.defaultValue && (
                <p className="text-sm text-red-500 mt-1">{errors.defaultValue}</p>
              )}
            </>
          )}
        </div>

        <div className="flex gap-2">
          <Button size="sm" onClick={handleSave}>
            <Check className="h-4 w-4 mr-2" />
            Save
          </Button>
          <Button size="sm" variant="outline" onClick={onCancel}>
            <X className="h-4 w-4 mr-2" />
            Cancel
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

interface VariableCardProps {
  variable: Variable;
  isEditing: boolean;
  onEdit: (id: string) => void;
  onSave: (id: string, updates: Partial<Omit<Variable, "id">>) => void;
  onCancel: () => void;
  onDelete: (id: string) => void;
  editVariables: boolean;
  functionRegistry: FunctionRegistry | undefined;
}

const VariableCard: React.FC<VariableCardProps> = ({
  variable,
  isEditing,
  onEdit,
  onSave,
  onCancel,
  onDelete,
  editVariables,
  functionRegistry,
}) => {
  const [name, setName] = useState(variable.name);
  const [type, setType] = useState(variable.type);
  const [defaultValue, setDefaultValue] = useState(
    String(variable.defaultValue)
  );
  const [errors, setErrors] = useState<{
    name?: string;
    defaultValue?: string;
  }>(EMPTY_OBJECT);

  // Get function options from the registry
  const functionOptions = useMemo(() => {
    if (!functionRegistry) return [];
    return Object.entries(functionRegistry).map(([id, def]) => ({
      id,
      name: def.name,
      description: def.description,
    }));
  }, [functionRegistry]);

  // Check if function type is available
  const hasFunctions = functionOptions.length > 0;

  // Get the display name for a function variable
  const getFunctionDisplayName = useCallback((funcId: string) => {
    if (!functionRegistry) return funcId;
    const fn = functionRegistry[funcId];
    return fn ? fn.name : funcId;
  }, [functionRegistry]);

  const validateForm = useCallback(() => {
    const newErrors: { name?: string; defaultValue?: string } = {};

    if (!name.trim()) {
      newErrors.name = "Name is required";
    }

    if (type === 'function') {
      if (!defaultValue) {
        newErrors.defaultValue = "Please select a function";
      }
    } else if (!defaultValue.trim()) {
      newErrors.defaultValue = "Preview value is required";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [name, defaultValue, type]);

  const handleSave = useCallback(() => {
    if (!validateForm()) return;

    let parsedValue: any = defaultValue;
    try {
      if (type === "number") {
        parsedValue = parseFloat(defaultValue) || 0;
      } else if (type === "boolean") {
        parsedValue = defaultValue.toLowerCase() === "true";
      }
      // For 'function' type, defaultValue is already the function ID (string)
    } catch (e) {
      console.warn("Error parsing variable value, keeping as string", e);
    }

    onSave(variable.id, { name, type, defaultValue: parsedValue });
  }, [name, type, defaultValue, onSave, validateForm, variable.id]);

  const handleNameChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setName(e.target.value);
      if (errors.name) setErrors((prev) => ({ ...prev, name: undefined }));
    },
    [errors.name]
  );

  const handleTypeChange = useCallback(
    (value: string) => {
      setType(value as Variable["type"]);
      // Reset default value when switching types
      setDefaultValue("");
    },
    []
  );

  const handleDefaultValueChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setDefaultValue(e.target.value);
      if (errors.defaultValue)
        setErrors((prev) => ({ ...prev, defaultValue: undefined }));
    },
    [errors.defaultValue]
  );

  const handleFunctionSelect = useCallback(
    (value: string) => {
      setDefaultValue(value);
      if (errors.defaultValue)
        setErrors((prev) => ({ ...prev, defaultValue: undefined }));
    },
    [errors.defaultValue]
  );

  const handleOnEdit = useCallback(() => {
    onEdit(variable.id);
  }, [onEdit, variable.id]);

  const handleOnDelete = useCallback(() => {
    onDelete(variable.id);
  }, [onDelete, variable.id]);

  if (isEditing) {
    return (
      <Card>
        <CardContent className="pt-4 space-y-3">
          <div>
            <Label htmlFor={`edit-name-${variable.id}`}>
              Name <span className="text-red-500">*</span>
            </Label>
            <Input
              id={`edit-name-${variable.id}`}
              value={name}
              onChange={handleNameChange}
              className={errors.name ? "border-red-500" : ""}
            />
            {errors.name && (
              <p className="text-sm text-red-500 mt-1">{errors.name}</p>
            )}
          </div>

          <div>
            <Label htmlFor={`edit-type-${variable.id}`}>Type</Label>
            <Select value={type} onValueChange={handleTypeChange}>
              <SelectTrigger>
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="string">String</SelectItem>
                <SelectItem value="number">Number</SelectItem>
                <SelectItem value="boolean">Boolean</SelectItem>
                {hasFunctions && <SelectItem value="function">Function</SelectItem>}
              </SelectContent>
            </Select>
          </div>

          <div>
            {type === 'function' ? (
              <>
                <Label htmlFor={`edit-function-${variable.id}`}>
                  Function <span className="text-red-500">*</span>
                </Label>
                <Select value={defaultValue} onValueChange={handleFunctionSelect}>
                  <SelectTrigger className={errors.defaultValue ? "border-red-500" : ""}>
                    <SelectValue placeholder="Select a function..." />
                  </SelectTrigger>
                  <SelectContent>
                    {functionOptions.map((fn) => (
                      <SelectItem key={fn.id} value={fn.id}>
                        <div className="flex flex-col">
                          <span>{fn.name}</span>
                          {fn.description && (
                            <span className="text-xs text-muted-foreground">{fn.description}</span>
                          )}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                {errors.defaultValue && (
                  <p className="text-sm text-red-500 mt-1">{errors.defaultValue}</p>
                )}
              </>
            ) : (
              <>
                <Label htmlFor={`edit-default-${variable.id}`}>
                  Preview Value <span className="text-red-500">*</span>
                </Label>
                <Input
                  id={`edit-default-${variable.id}`}
                  value={defaultValue}
                  onChange={handleDefaultValueChange}
                  placeholder={getPlaceholderForType(type)}
                  className={errors.defaultValue ? "border-red-500" : ""}
                />
                {errors.defaultValue && (
                  <p className="text-sm text-red-500 mt-1">{errors.defaultValue}</p>
                )}
              </>
            )}
          </div>

          <div className="flex gap-2">
            <Button size="sm" onClick={handleSave}>
              <Check className="h-4 w-4 mr-2" />
              Save
            </Button>
            <Button size="sm" variant="outline" onClick={onCancel}>
              <X className="h-4 w-4 mr-2" />
              Cancel
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardContent className="pt-4">
        <div className="flex items-center justify-between">
          <div className="flex-1">
            <div className="flex items-center gap-2">
              <span className="font-medium">{variable.name}</span>
              <span className="text-xs bg-muted px-2 py-1 rounded">
                {variable.type}
              </span>
            </div>
            <div className="text-sm text-muted-foreground mt-1">
              {variable.type === 'function' 
                ? getFunctionDisplayName(String(variable.defaultValue))
                : String(variable.defaultValue)}
            </div>
          </div>
          {editVariables && (
            <div className="flex gap-1">
              <Button size="sm" variant="ghost" onClick={handleOnEdit}>
                <Edit2 className="h-4 w-4" />
              </Button>
              <Button size="sm" variant="ghost" onClick={handleOnDelete}>
                <Trash2 className="h-4 w-4" />
              </Button>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

function getPlaceholderForType(type: Variable["type"]): string {
  switch (type) {
    case "string":
      return "Enter text...";
    case "number":
      return "0";
    case "boolean":
      return "true";
    case "function":
      return "Select a function...";
    default:
      return "";
  }
}

"use client";
import { useCallback, useEffect, useMemo, useState } from "react";
import {
  baseColors,
  type BaseColor,
  TAILWIND_V4_COLOR_KEYS,
} from "@/components/ui/ui-builder/internal/utils/base-colors";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { CheckIcon, InfoIcon, MoonIcon, SunIcon } from "lucide-react";
import { cn } from "@/lib/utils";
import {
  useLayerStore,
} from "@/lib/ui-builder/store/layer-store";
import type { ComponentLayer } from '@/components/ui/ui-builder/types';
import { Toggle } from "@/components/ui/toggle";

const RESET_THEME_PROPS = {
  style: undefined,
  "data-mode": undefined,
  "data-color-theme": undefined,
  "data-border-radius": undefined,
} as const;

export function TailwindThemePanel() {
  const {
    selectedPageId,
    updateLayer: updateLayerProps,
    findLayerById,
  } = useLayerStore();
  const selectedPageData = findLayerById(selectedPageId) as ComponentLayer;
  const [isCustomTheme, setIsCustomTheme] = useState(
    selectedPageData?.props["data-color-theme"] !== undefined
  );
  //if not isCustomTheme we delete the themeColors from the pageLayer
  useEffect(() => {
    if (!isCustomTheme) {
      updateLayerProps(selectedPageId, RESET_THEME_PROPS);
    }
  }, [isCustomTheme, selectedPageId, updateLayerProps]);

  const handleOnToggle = useCallback(() => {
    setIsCustomTheme(!isCustomTheme);
  }, [isCustomTheme]);

  return (
    <div className="flex flex-col gap-4 mt-4">
      <Toggle
        variant="outline"
        onPressedChange={handleOnToggle}
        aria-label="Toggle italic"
      >
        {isCustomTheme ? "Use Default Theme" : "Use Custom Theme"}
      </Toggle>
      {!isCustomTheme && (
        <span className="flex items-center gap-2">
          <InfoIcon className="size-4" /> Using Your Project&apos;s Theme
        </span>
      )}
      {selectedPageData && isCustomTheme && (
        <ThemePicker key={selectedPageId} isDisabled={!isCustomTheme} pageLayer={selectedPageData} />
      )}
    </div>
  );
}

function ThemePicker({
  className,
  isDisabled,
  pageLayer,
}: {
  className?: string;
  isDisabled: boolean;
  pageLayer: ComponentLayer;
}) {
  const { updateLayer: updateLayerProps } = useLayerStore();

  // Safely extract values with type checking
  const colorThemeValue = pageLayer.props?.["data-color-theme"];
  const modeValue = pageLayer.props?.["data-mode"];
  const borderRadiusValue = pageLayer.props?.borderRadius;

  const [colorTheme, setColorTheme] = useState<BaseColor["name"]>(
    (typeof colorThemeValue === 'string' ? colorThemeValue : "red") as BaseColor["name"]
  );
  const [borderRadius, setBorderRadius] = useState(
    typeof borderRadiusValue === 'number' ? borderRadiusValue : 0.5
  );
  const [mode, setMode] = useState<"light" | "dark">(
    (typeof modeValue === 'string' ? modeValue : "light") as "light" | "dark"
  );



  useEffect(() => {
    if (isDisabled) return;

    const colorThemeData = baseColors.find((color) => color.name === colorTheme);

    if (colorThemeData) {
      const colorDataWithBorder = {
        ...colorThemeData,
        cssVars: {
          ...colorThemeData.cssVars,
          [mode]: {
            ...colorThemeData.cssVars[mode],
            radius: `${borderRadius}rem`,
          },
        },
      } as const;

      const themeStyle = themeToStyleVars(colorDataWithBorder.cssVars[mode]);

      updateLayerProps(pageLayer.id, {
        style: themeStyle,
        "data-mode": mode,
        "data-color-theme": colorTheme,
        "data-border-radius": borderRadius,
      });
    }
  }, [pageLayer.id, updateLayerProps, colorTheme, borderRadius, mode, isDisabled]);

  const colorOptions = useMemo(() => baseColors.map((color: BaseColor) => {
    return (
      <ThemeColorOption
        key={color.name}
        color={color}
        colorTheme={colorTheme}
        mode={mode}
        onClick={setColorTheme}
      />
    );
  }), [colorTheme, mode]);
  const borderRadiusOptions = useMemo(() => [0.0, 0.15, 0.3, 0.5, 0.75, 1.0].map((radius) => {
    return (
     <ThemeBorderRadiusOption
      key={radius}
      radius={radius}
      borderRadius={borderRadius}
      onClick={setBorderRadius}
     />
    );
  }), [borderRadius, setBorderRadius]);

  const modeOptions = useMemo(() => (["light", "dark"] as const).map((modeOption) => {
    return (
      <ThemeModeOption
        key={modeOption}
        mode={mode}
        modeOption={modeOption}
        onClick={setMode}
      />
    );
  }), [mode, setMode]);

  return (
    <div
      className={cn(
        "flex flex-col gap-2",
        className,
        isDisabled && "opacity-30 pointer-events-none"
      )}
    >
      <Label className="mt-2">Colors</Label>
      <div className="flex gap-2 flex-wrap">{colorOptions}</div>
      <Label className="mt-2">Border Radius</Label>
      <div className="flex gap-2 flex-wrap">{borderRadiusOptions}</div>
      <Label className="mt-2">Mode</Label>
      <div className="flex gap-2">{modeOptions}</div>
    </div>
  );
}

function ThemeColorOption({ color, colorTheme, mode, onClick }: { color: BaseColor, colorTheme: string, mode: "light" | "dark", onClick: (name: BaseColor["name"]) => void }) {

  const handleOnClick = useCallback(() => {
    onClick(color.name);
  }, [onClick, color.name]);

  const style = useMemo(() => ({
    backgroundColor: `hsl(${
      color.activeColor[mode === "dark" ? "dark" : "light"]
    })`,
  }), [color.activeColor, mode]);

  return (
    <Button
        key={color.name}
        variant="outline"
        size="sm"
        className={cn(
          "gap-2",
          color.name === colorTheme && "border-primary border-2"
        )}
        onClick={handleOnClick}
      >
        <div
          className="size-4 rounded-full"
          style={style}
        >
          {color.name === colorTheme && <CheckIcon className="size-4" />}
        </div>
        {color.label}
      </Button>
  );
}

function ThemeBorderRadiusOption({ radius, borderRadius, onClick }: { radius: number, borderRadius: number, onClick: (radius: number) => void }) {

  const handleOnClick = useCallback(() => {
    onClick(radius);
  }, [onClick, radius]);

  const style = useMemo(() => ({
    borderRadius: `${radius}rem`,
  }), [radius]);

  return (
    <Button
    key={radius}
    variant="outline"
    size="sm"
    className={cn(
      "gap-2",
      radius === borderRadius && "border-primary border-2"
    )}
    onClick={handleOnClick}
  >
    <div className="size-6 rounded-sm bg-secondary overflow-hidden">
      <div
        className="size-10 ml-2 mt-2 border-2 border-secondary-foreground"
        style={style}
      />
    </div>
    {radius}
  </Button>
  );
}

function ThemeModeOption({modeOption, mode, onClick}: {modeOption: "light" | "dark", mode: "light" | "dark", onClick: (mode: "light" | "dark") => void}){

  const handleOnClick = useCallback(() => {
    onClick(modeOption);
  }, [onClick, modeOption]);

  return (
    <Button
        key={modeOption}
        variant="outline"
        size="sm"
        onClick={handleOnClick}
        className={cn(mode === modeOption && "border-2 border-primary")}
      >
        {modeOption === "light" ? (
          <SunIcon className="mr-1 -translate-x-1" />
        ) : (
          <MoonIcon className="mr-1 -translate-x-1" />
        )}
        {modeOption}
      </Button>
  );
}


function themeToStyleVars(
  colors:
    | BaseColor["cssVars"]["dark"]
    | BaseColor["cssVars"]["light"]
    | undefined
) {
  if (!colors) {
    return undefined;
  }

  const styleVariables: { [key: string]: string } = {};

  // Set base CSS variables (e.g., --foreground: "222.2 84% 4.9%")
  Object.entries(colors).forEach(([key, value]) => {
    styleVariables[`--${key}`] = value;
  });

  // Also set --color-* variables for Tailwind v4 compatibility
  // Tailwind v4's @theme block defines these at :root, but we need to override them
  // at the element level for custom themes to work
  TAILWIND_V4_COLOR_KEYS.forEach((key) => {
    const value = colors[key as keyof typeof colors];
    if (value) {
      styleVariables[`--color-${key}`] = `hsl(${value})`;
    }
  });

  // Set radius variables for Tailwind v4
  const radiusValue = (colors as { radius?: string }).radius;
  if (radiusValue) {
    styleVariables["--radius-lg"] = radiusValue;
    styleVariables["--radius-md"] = `calc(${radiusValue} - 2px)`;
    styleVariables["--radius-sm"] = `calc(${radiusValue} - 4px)`;
  }

  // Global overrides for text and border colors
  const globalOverrides = {
    color: `hsl(${colors.foreground})`,
    borderColor: `hsl(${colors.border})`,
    backgroundColor: `hsl(${colors.background})`,
  };

  return { ...styleVariables, ...globalOverrides };
}


import React, { useCallback, useMemo } from "react";
import { z } from "zod";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";
import {
  type ComponentRegistry,
  type ComponentLayer,
} from "@/components/ui/ui-builder/types";
import { Button } from "@/components/ui/button";
import AutoForm from "@/components/ui/auto-form";
import { generateFieldOverrides } from "@/lib/ui-builder/store/editor-utils";
import { addDefaultValues } from "@/lib/ui-builder/store/schema-utils";
import { getBaseType } from "@/components/ui/auto-form/helpers";
import { isVariableReference } from "@/lib/ui-builder/utils/variable-resolver";
import { resolveVariableReferences } from "@/lib/ui-builder/utils/variable-resolver";

interface PropsPanelProps {
  className?: string;
}

const PropsPanel: React.FC<PropsPanelProps> = React.memo(({ className }) => {
  const selectedLayerId = useLayerStore((state) => state.selectedLayerId);
  const findLayerById = useLayerStore((state) => state.findLayerById);
  const removeLayer = useLayerStore((state) => state.removeLayer);
  const duplicateLayer = useLayerStore((state) => state.duplicateLayer);
  const updateLayer = useLayerStore((state) => state.updateLayer);
  const addComponentLayer = useLayerStore((state) => state.addComponentLayer);
  const componentRegistry = useEditorStore((state) => state.registry);
  const selectedLayer = findLayerById(selectedLayerId);

  const handleAddComponentLayer = useCallback(
    (layerType: string, parentLayerId: string, addPosition?: number) => {
      addComponentLayer(layerType, parentLayerId, addPosition);
    },
    [addComponentLayer]
  );

  const handleDeleteLayer = useCallback(
    (layerId: string) => {
      removeLayer(layerId);
    },
    [removeLayer]
  );

  const handleDuplicateLayer = useCallback(() => {
    if (selectedLayer) {
      duplicateLayer(selectedLayer.id);
    }
  }, [selectedLayer, duplicateLayer]);

  const handleUpdateLayer = useCallback(
    (
      id: string,
      props: Record<string, any>,
      rest?: Partial<Omit<ComponentLayer, "props">>
    ) => {
      updateLayer(id, props, rest);
    },
    [updateLayer]
  );

  //first check if selectedLayer.type is a valid key in componentRegistry
  if (
    selectedLayer &&
    !componentRegistry[selectedLayer.type as keyof typeof componentRegistry]
  ) {
    return null;
  }

  return (
    <div className={className}>
      {selectedLayer && (
        <>
          <Title />
          <h3 className="text-base font-medium mb-4">
            Type: {selectedLayer.type.replaceAll("_", "")}
          </h3>
        </>
      )}

      {!selectedLayer && (
        <>
          <h2 className="text-xl font-semibold mb-2">Component Properties</h2>
          <p>No component selected</p>
        </>
      )}
      {selectedLayer && (
        <ComponentPropsAutoForm
          key={selectedLayer.id}
          componentRegistry={componentRegistry}
          selectedLayerId={selectedLayer.id}
          removeLayer={handleDeleteLayer}
          duplicateLayer={handleDuplicateLayer}
          updateLayer={handleUpdateLayer}
          addComponentLayer={handleAddComponentLayer}
        />
      )}
    </div>
  );
});
PropsPanel.displayName = "PropsPanel";
export default PropsPanel;

interface ComponentPropsAutoFormProps {
  selectedLayerId: string;
  componentRegistry: ComponentRegistry;
  removeLayer: (id: string) => void;
  duplicateLayer: (id: string) => void;
  updateLayer: (
    id: string,
    props: Record<string, any>,
    rest?: Partial<Omit<ComponentLayer, "props">>
  ) => void;
  addComponentLayer: (
    layerType: string,
    parentLayerId: string,
    addPosition?: number
  ) => void;
}

const EMPTY_ZOD_SCHEMA = z.object({});
const EMPTY_FORM_VALUES = {};

const ComponentPropsAutoForm: React.FC<ComponentPropsAutoFormProps> = ({
  selectedLayerId,
  componentRegistry,
  removeLayer,
  duplicateLayer,
  updateLayer,
  addComponentLayer,
}) => {
  const findLayerById = useLayerStore((state) => state.findLayerById);
  const revisionCounter = useEditorStore((state) => state.revisionCounter);
  const selectedLayer = findLayerById(selectedLayerId) as
    | ComponentLayer
    | undefined;
  const isPage = useLayerStore((state) => state.isLayerAPage(selectedLayerId));
  const allowPagesCreation = useEditorStore(
    (state) => state.allowPagesCreation
  );
  const allowPagesDeletion = useEditorStore(
    (state) => state.allowPagesDeletion
  );

  // Retrieve the appropriate schema from componentRegistry
  const { schema } = useMemo(() => {
    if (
      selectedLayer &&
      componentRegistry[selectedLayer.type as keyof typeof componentRegistry]
    ) {
      const registryEntry = componentRegistry[
        selectedLayer.type as keyof typeof componentRegistry
      ];
      if (registryEntry) {
        return registryEntry;
      }
    }
    return { schema: EMPTY_ZOD_SCHEMA }; // Fallback schema
  }, [selectedLayer, componentRegistry]);

  const handleDeleteLayer = useCallback(() => {
    removeLayer(selectedLayerId);
  }, [removeLayer, selectedLayerId]);

  const handleDuplicateLayer = useCallback(() => {
    duplicateLayer(selectedLayerId);
  }, [duplicateLayer, selectedLayerId]);

  // Memoize variables to avoid accessing store state on every render
  const variables = useLayerStore((state) => state.variables);

  const onParsedValuesChange = useCallback(
    (
      parsedValues: z.infer<typeof schema> & {
        children?: string | { layerType: string; addPosition: number };
      }
    ) => {
      const { children, ...dataProps } = parsedValues;

      // Preserve variable references by merging with original props
      const preservedProps: Record<string, any> = {};
      if (selectedLayer) {
        // Start with all original props to preserve any that aren't in the form update
        Object.assign(preservedProps, selectedLayer.props);

        // Then update only the props that came from the form, preserving variable references
        Object.keys(dataProps as Record<string, any>).forEach((key) => {
          const originalValue = selectedLayer.props[key];
          const newValue = (dataProps as Record<string, any>)[key];
          const fieldDef = ('shape' in schema && schema.shape) ? schema.shape[key] : undefined;
          const baseType = fieldDef
            ? getBaseType(fieldDef as z.ZodAny)
            : undefined;
          // If the original value was a variable reference and the variable still exists, preserve it
          // If the variable no longer exists, allow the user's new value to be saved
          if (isVariableReference(originalValue)) {
            const variableExists = variables.some(v => v.id === originalValue.__variableRef);
            if (variableExists) {
              // Keep the variable reference - the form should not override variable bindings
              preservedProps[key] = originalValue;
            } else {
              // Variable was deleted - use the new value from the form
              preservedProps[key] = newValue;
            }
          } else {
            // Handle date serialization
            if (
              baseType === "ZodDate" &&
              newValue instanceof Date
            ) {
              preservedProps[key] = newValue.toISOString();
            } else {
              preservedProps[key] = newValue;
            }
          }
        });
      }

      // Preserve children variable references only if the referenced variable still exists
      const originalChildren = selectedLayer?.children;
      const shouldPreserveChildrenBinding = isVariableReference(originalChildren) && 
        variables.some(v => v.id === originalChildren.__variableRef);

      if (shouldPreserveChildrenBinding) {
        // Keep the variable reference - the form should not override children variable bindings
        updateLayer(selectedLayerId, preservedProps);
      } else if (typeof children === "string") {
        updateLayer(selectedLayerId, preservedProps, { children: children });
      } else if (children && children.layerType) {
        updateLayer(selectedLayerId, preservedProps, {
          children: selectedLayer?.children,
        });
        addComponentLayer(
          children.layerType,
          selectedLayerId,
          children.addPosition
        );
      } else {
        updateLayer(selectedLayerId, preservedProps);
      }
    },
    [updateLayer, selectedLayerId, selectedLayer, addComponentLayer, schema, variables]
  );

  // Prepare values for AutoForm, converting enum values to strings as select elements only accept string values
  const formValues = useMemo(() => {
    if (!selectedLayer) return EMPTY_FORM_VALUES;

    // First resolve variable references to get display values
    const resolvedProps = resolveVariableReferences(
      selectedLayer.props,
      variables
    );

    const transformedProps: Record<string, any> = {};
    const schemaShape = ('shape' in schema && schema.shape) ? schema.shape as z.ZodRawShape : undefined; // Get shape from the memoized schema

    if (schemaShape) {
      for (const [key, value] of Object.entries(resolvedProps)) {
        const fieldDef = schemaShape[key];
        if (fieldDef) {
          const baseType = getBaseType(fieldDef as z.ZodAny);
          if (baseType === "ZodEnum") {
            // Convert enum value to string if it's not already a string
            transformedProps[key] =
              typeof value === "string" ? value : String(value);
          } else if (baseType === "ZodDate") {
            // Convert string to Date if necessary
            if (value instanceof Date) {
              transformedProps[key] = value;
            } else if (typeof value === "string" || typeof value === "number") {
              const date = new Date(value);
              transformedProps[key] = isNaN(date.getTime()) ? undefined : date;
            } else {
              transformedProps[key] = undefined;
            }
          } else {
            transformedProps[key] = value;
          }
        } else {
          transformedProps[key] = value;
        }
      }
    } else {
      // Fallback if schema shape isn't available: copy resolved props as is
      Object.assign(transformedProps, resolvedProps);
    }

    return { ...transformedProps, children: selectedLayer.children };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedLayer, schema, revisionCounter]); // Include revisionCounter to detect undo/redo changes

  const autoFormSchema = useMemo(() => {
    // Only pass ZodObject schemas to addDefaultValues, otherwise return the original schema
    if ('shape' in schema && typeof schema.shape === 'object') {
      try {
        return addDefaultValues(schema as any, formValues);
      } catch (error) {
        console.warn('Failed to add default values to schema:', error);
        return schema;
      }
    }
    return schema;
  }, [schema, formValues]);

  const autoFormFieldConfig = useMemo(() => {
    if (!selectedLayer) return undefined; // Or a default config if appropriate
    return generateFieldOverrides(componentRegistry, selectedLayer);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [componentRegistry, selectedLayer, selectedLayer?.props]);

  // Create a unique key that changes when we need to force re-render the form
  // This includes selectedLayerId and revisionCounter to handle both layer changes and undo/redo
  const formKey = useMemo(() => {
    return `${selectedLayerId}-${revisionCounter}`;
  }, [selectedLayerId, revisionCounter]);

  if (
    !selectedLayer ||
    !componentRegistry[selectedLayer.type as keyof typeof componentRegistry]
  ) {
    return null;
  }

  return (
    <AutoForm
      key={formKey} // Force re-render when layer changes or undo/redo occurs
      formSchema={autoFormSchema}
      values={formValues} // Use the memoized and transformed values
      onParsedValuesChange={onParsedValuesChange}
      fieldConfig={autoFormFieldConfig}
      className="space-y-4 mt-4"
    >
      {(!isPage || allowPagesCreation) && (
        <Button
          type="button"
          variant="secondary"
          className="mt-4 w-full"
          onClick={handleDuplicateLayer}
          data-testid={`button-Duplicate ,${isPage ? "Page" : "Component"}`}
        >
          Duplicate {isPage ? "Page" : "Component"}
        </Button>
      )}
      {(!isPage || allowPagesDeletion) && (
        <Button
          type="button"
          variant="destructive"
          className="mt-4 w-full"
          onClick={handleDeleteLayer}
          data-testid={`button-Delete ,${isPage ? "Page" : "Component"}`}
        >
          Delete {isPage ? "Page" : "Component"}
        </Button>
      )}
    </AutoForm>
  );
};

ComponentPropsAutoForm.displayName = "ComponentPropsAutoForm";

const nameForLayer = (layer: ComponentLayer) => {
  return layer.name || layer.type.replaceAll("_", "");
};

const Title = React.memo(() => {
  const selectedLayer = useLayerStore((state) => {
    const selectedLayerId = state.selectedLayerId;
    return selectedLayerId ? state.findLayerById(selectedLayerId) : null;
  });

  return (
    <h2 className="text-xl font-semibold mb-2">
      {selectedLayer ? nameForLayer(selectedLayer) : ""} Properties
    </h2>
  );
});

Title.displayName = "Title";


import React, {
  useCallback,
  useLayoutEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import isDeepEqual from "fast-deep-equal";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import type { ComponentLayer } from "@/components/ui/ui-builder/types";
import { isVariableReference } from "@/lib/ui-builder/utils/variable-resolver";
import { cn } from "@/lib/utils";
import {
  findAllParentLayersRecursive,
  hasLayerChildren,
} from "@/lib/ui-builder/store/layer-utils";
import { Plus } from "lucide-react";
import { useHeTree, type Id } from "he-tree-react";
import {
  TreeRowNode,
  TreeRowPlaceholder,
} from "@/components/ui/ui-builder/internal/components/tree-row-node";
import { DevProfiler } from "@/components/ui/ui-builder/internal/components/dev-profiler";
import { AddComponentsPopover } from "@/components/ui/ui-builder/internal/components/add-component-popover";
import { buttonVariants } from "@/components/ui/button";
import { DividerControl } from "@/components/ui/ui-builder/internal/components/divider-control";

interface LayersPanelProps {
  className?: string;
}

const LayersPanel: React.FC<LayersPanelProps> = ({ className }) => {
  const {
    selectedPageId,
    selectedLayerId,
    findLayerById,
    updateLayer,
    selectLayer,
  } = useLayerStore();

  const pageLayer = findLayerById(selectedPageId);

  const layers = useMemo(() => [pageLayer as ComponentLayer], [pageLayer]);

  if (!pageLayer) {
    return null;
  }

  return (
    <LayersTree
      className={className}
      layers={layers}
      selectedPageId={selectedPageId}
      selectedLayerId={selectedLayerId}
      updateLayer={updateLayer}
      selectLayer={selectLayer}
    />
  );
};

interface LayersTreeProps {
  className?: string;
  layers: ComponentLayer[];
  selectedPageId: string;
  selectedLayerId: string | null;
  updateLayer: (
    layerId: string,
    newProps: Record<string, any>,
    layerRest?: Partial<Omit<ComponentLayer, "props">>
  ) => void;
  selectLayer: (layerId: string) => void;
}

export const LayersTree: React.FC<LayersTreeProps> = React.memo(
  ({
    className,
    layers,
    selectedPageId,
    selectedLayerId,
    updateLayer,
    selectLayer,
  }) => {
    const [openIdsArray, setOpenIdsArray] = useState<Id[]>([]);

    const prevSelectedLayerId = useRef(selectedLayerId);

    const handleNodeToggle = useCallback((id: Id, open: boolean) => {
      setOpenIdsArray((prev) => {
        if (open) {
          return prev.includes(id) ? prev : [...prev, id];
        } else {
          return prev.filter((existingId) => existingId !== id);
        }
      });
    }, []);

    const handleChange = useCallback(
      (newLayers: unknown) => {
        if (Array.isArray(newLayers) && newLayers.length > 0) {
          const updatedPageLayer = newLayers[0] as ComponentLayer;

          // Validate the layer structure before updating
          if (!updatedPageLayer || !updatedPageLayer.id || updatedPageLayer.id !== selectedPageId) {
            console.error(
              "LayersTree onChange: Invalid layer structure - ID mismatch",
              { updatedPageLayer, selectedPageId }
            );
            return;
          }

          const updatedChildren = hasLayerChildren(updatedPageLayer)
            ? updatedPageLayer.children || []
            : [];

          // Only update if children actually changed
          const currentLayer = layers[0];
          const currentChildren = currentLayer && hasLayerChildren(currentLayer) ? currentLayer.children || [] : [];

          if (!isDeepEqual(currentChildren, updatedChildren)) {
            updateLayer(selectedPageId, {}, { children: updatedChildren });
          }
        } else {
          console.error(
            "LayersTree onChange: Invalid newLayers structure received",
            newLayers
          );
        }
      },
      [updateLayer, selectedPageId, layers]
    );

    const handleDragOpen = useCallback(
      (stat: { node: ComponentLayer; id: Id }) => {
        if (hasLayerChildren(stat.node)) {
          handleNodeToggle(stat.id, true);
        }
      },
      [handleNodeToggle]
    );

    const canNodeDrop = useCallback((layer: { node: ComponentLayer }) => {
      const isDroppable = hasLayerChildren(layer.node);
      return isDroppable;
    }, []);

    const renderNode = useCallback(
      ({ stat, attrs, isPlaceholder }: any) => {
        // Use node.id as key to ensure stable identity across tree operations
        const stableKey = isPlaceholder ? `placeholder-${attrs.key}` : stat.node.id;

        if (isPlaceholder) {
          return (
            <TreeRowPlaceholder
              key={stableKey}
              nodeAttributes={attrs}
            />
          );
        }

        // Find the original layer data (not processed) to preserve text children info
        const findOriginalLayer = (layers: ComponentLayer[], id: string): ComponentLayer | null => {
          for (const layer of layers) {
            if (layer.id === id) return layer;
            if (hasLayerChildren(layer)) {
              const found = findOriginalLayer(layer.children, id);
              if (found) return found;
            }
          }
          return null;
        };

        const originalNode = findOriginalLayer(layers, stat.node.id) || stat.node;

        return (
          <TreeRowNode
            key={stableKey}
            nodeAttributes={attrs}
            node={originalNode}
            id={stat.id}
            open={stat.open}
            draggable={stat.draggable}
            onToggle={handleNodeToggle}
            level={stat.level}
            selectedLayerId={selectedLayerId}
            selectLayer={selectLayer}
          />
        );
      },
      [
        handleNodeToggle,
        selectedLayerId,
        selectLayer,
        layers,
      ]
    );

    // Preprocess layers to ensure all nodes appear in tree, even those with string children
    const processedLayers = useMemo(() => {
      const processLayer = (layer: ComponentLayer): ComponentLayer => {
        const processed = { ...layer };

        if (hasLayerChildren(layer)) {
          // Recursively process array children
          processed.children = layer.children.map(processLayer);
        } else if (typeof layer.children === 'string') {
          // Convert string children to empty array for tree traversal
          // The actual text content is preserved in the original layer
          processed.children = [];
        } else if (isVariableReference(layer.children)) {
          // Convert variable reference children to empty array for tree traversal
          // The variable binding is preserved in the original layer
          processed.children = [];
        } else if (!layer.children) {
          // Ensure undefined/null children become empty arrays
          processed.children = [];
        }

        return processed;
      };

      return layers.map(processLayer);
    }, [layers]);

    const data = useMemo(() => {
      return {
        data: processedLayers,
        dataType: "tree" as const,
        childrenKey: "children",
        openIds: openIdsArray,
        dragOpen: true,
        onChange: handleChange,
        renderNodeBox: renderNode,
        onDragOpen: handleDragOpen,
        canDrop: canNodeDrop,
      };
    }, [
      processedLayers,
      openIdsArray,
      handleChange,
      renderNode,
      handleDragOpen,
      canNodeDrop,
    ]);

    const { renderTree, scrollToNode } = useHeTree<ComponentLayer>(data);

    useLayoutEffect(() => {
      if (selectedLayerId) {
        const parentLayers = findAllParentLayersRecursive(
          layers,
          selectedLayerId
        );
        const parentIds = parentLayers.map((layer) => layer.id);
        setOpenIdsArray((prevOpenIds) => {
          const newIds = [...prevOpenIds];
          let hasChanges = false;

          parentIds.forEach((id) => {
            if (!newIds.includes(id)) {
              newIds.push(id);
              hasChanges = true;
            }
          });

          return hasChanges ? newIds : prevOpenIds;
        });
      }
    }, [selectedLayerId, layers]);

    useLayoutEffect(() => {
      if (prevSelectedLayerId.current !== selectedLayerId) {
        prevSelectedLayerId.current = selectedLayerId;
        if (selectedLayerId) {
          scrollToNode(selectedLayerId);
        }
      }
    }, [scrollToNode, selectedLayerId]);

    const buttonClass = useMemo(() => {
      return cn(
        buttonVariants({ variant: "default", size: "sm" }),
        "cursor-pointer w-full"
      );
    }, []);

    return (
      <DevProfiler id="LayersPanel" threshold={40}>
        <div
          data-testid="layers-tree"
          className={cn(
            className,
            "flex flex-col size-full overflow-x-auto pl-4"
          )}
        >
          {layers.length > 0 ? (
            <>
              <DividerControl
                className="border-l border-dashed border-primary"
                addPosition={0}
                parentLayerId={selectedPageId}
              />
              {renderTree()}
              <div className="relative">
                <div className="w-[1px] h-4 absolute left-0 bottom-0 border-l border-dashed border-primary bg-background" />
              </div>
              <DividerControl
                className="border-l border-dashed border-primary"
                parentLayerId={selectedPageId}
              />
            </>
          ) : (
            <AddComponentsPopover
              parentLayerId={selectedPageId}
              className="w-full mt-4"
            >
              <div
                className={buttonClass}
              >
                <span className="sr-only">Add Component</span>
                <Plus className="h-5 w-5" />
                <span>Add Component</span>
              </div>
            </AddComponentsPopover>
          )}
        </div>
      </DevProfiler>
    );
  },
  (prevProps, nextProps) => {
    return (
      isDeepEqual(prevProps.layers, nextProps.layers) &&
      prevProps.selectedPageId === nextProps.selectedPageId &&
      prevProps.selectedLayerId === nextProps.selectedLayerId &&
      prevProps.className === nextProps.className
    );
  }
);

LayersTree.displayName = "LayersTree";

export default LayersPanel;

"use client";
import React, {
  useCallback,
  useMemo,
  useState,
  useRef,
  useEffect,
} from "react";
import { Plus, Crosshair, ZoomIn, ZoomOut, MousePointer } from "lucide-react";
import { countLayers, useLayerStore } from "@/lib/ui-builder/store/layer-store";
import type { ComponentLayer } from "@/components/ui/ui-builder/types";
import {
  TransformWrapper,
  TransformComponent,
  useControls,
} from "react-zoom-pan-pinch";

import LayerRenderer from "@/components/ui/ui-builder/layer-renderer";
import { DndContextProvider, useComponentDragContext } from "@/lib/ui-builder/context/dnd-context";
import { cn } from "@/lib/utils";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";
import { AddComponentsPopover } from "@/components/ui/ui-builder/internal/components/add-component-popover";
import { Button } from "@/components/ui/button";
import { ResizableWrapper } from "@/components/ui/ui-builder/internal/canvas/resizable-wrapper";
import AutoFrame from "@/components/ui/ui-builder/internal/canvas/auto-frame";
import { Tooltip, TooltipContent, TooltipTrigger, TooltipProvider } from "@/components/ui/tooltip";

// Static style objects to prevent recreation on every render
const WRAPPER_STYLE = {
  width: "100%",
  height: "100%",
} as const;

const CONTENT_STYLE = {
  width: "100%",
  height: "100%",
} as const;

const TRANSFORM_DIV_STYLE = {
  minHeight: "100vh",
  padding: "50px",
} as const;

const WHEEL_CONFIG = { step: 0.1 } as const;
const DOUBLE_CLICK_CONFIG = { disabled: false } as const;

const ZoomControls: React.FC<{ onPointerEventsToggle: (enabled: boolean) => void; pointerEventsEnabled: boolean }> = ({ 
  onPointerEventsToggle, 
  pointerEventsEnabled 
}) => {
  const { zoomIn, zoomOut, resetTransform } = useControls();

  const handleZoomIn = useCallback(() => zoomIn(), [zoomIn]);
  const handleZoomOut = useCallback(() => zoomOut(), [zoomOut]);
  const handleReset = useCallback(() => resetTransform(), [resetTransform]);
  const handleTogglePointerEvents = useCallback(() => {
    onPointerEventsToggle(!pointerEventsEnabled);
  }, [onPointerEventsToggle, pointerEventsEnabled]);

  return (
    <TooltipProvider>
      <div className="absolute bottom-24 md:bottom-4 right-4 z-[1000] flex shadow-lg rounded-full">
        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              data-testid="button-ZoomIn"
              variant="secondary"
              className="size-14 md:size-10 rounded-l-full rounded-r-none border-r border-border [&_svg]:size-7 [&_svg]:md:size-4"
              onClick={handleZoomIn}
            >
              <span className="sr-only">Zoom in</span>
              <ZoomIn className="text-secondary-foreground" />
            </Button>
          </TooltipTrigger>
          <TooltipContent side="top">
            <p>Zoom in</p>
          </TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              data-testid="button-ZoomOut"
              variant="secondary"
              className="size-14 md:size-10 rounded-none border-r border-border [&_svg]:size-7 [&_svg]:md:size-4"
              onClick={handleZoomOut}
            >
              <span className="sr-only">Zoom out</span>
              <ZoomOut className="text-secondary-foreground" />
            </Button>
          </TooltipTrigger>
          <TooltipContent side="top">
            <p>Zoom out</p>
          </TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              data-testid="button-Reset"
              variant="secondary"
              className="size-14 md:size-10 rounded-none border-r border-border [&_svg]:size-7 [&_svg]:md:size-4"
              onClick={handleReset}
            >
              <span className="sr-only">Reset</span>
              <Crosshair className="text-secondary-foreground" />
            </Button>
          </TooltipTrigger>
          <TooltipContent side="top">
            <p>Reset zoom and position</p>
          </TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              data-testid="button-PointerEvents"
              variant={pointerEventsEnabled ? "default" : "secondary"}
              className="size-14 md:size-10 rounded-r-full rounded-l-none [&_svg]:size-7 [&_svg]:md:size-4"
              onClick={handleTogglePointerEvents}
            >
              <span className="sr-only">{pointerEventsEnabled ? "Disable pointer events" : "Enable pointer events"}</span>
              <MousePointer className={pointerEventsEnabled ? "text-primary-foreground" : "text-secondary-foreground"} />
            </Button>
          </TooltipTrigger>
          <TooltipContent side="top">
            <p>{pointerEventsEnabled ? "Disable page interaction" : "Enable page interaction"}</p>
          </TooltipContent>
        </Tooltip>
      </div>
    </TooltipProvider>
  );
};

interface EditorPanelProps {
  className?: string;
}

const EditorPanel: React.FC<EditorPanelProps> = ({ className }) => {
  const {
    selectLayer,
    selectedLayerId,
    findLayerById,
    selectedPageId,
  } = useLayerStore();
  const previewMode = useEditorStore((state) => state.previewMode);
  const componentRegistry = useEditorStore((state) => state.registry);
  const selectedLayer = findLayerById(selectedLayerId) as ComponentLayer;
  const selectedPage = findLayerById(selectedPageId) as ComponentLayer;

  const onSelectElement = useCallback(
    (layerId: string) => {
      selectLayer(layerId);
    },
    [selectLayer]
  );

  return (
    <DndContextProvider>
      <EditorPanelContent
        className={className}
        selectedLayerId={selectedLayerId}
        selectedPageId={selectedPageId}
        selectedLayer={selectedLayer}
        selectedPage={selectedPage}
        previewMode={previewMode}
        componentRegistry={componentRegistry}
        autoZoomToSelected={false}
        onSelectElement={onSelectElement}
      />
    </DndContextProvider>
  );
};

export default EditorPanel;

interface EditorPanelContentProps {
  className?: string;
  selectedLayerId: string | null;
  selectedPageId: string;
  selectedLayer: ComponentLayer;
  selectedPage: ComponentLayer;
  previewMode: string;
  componentRegistry: any;
  autoZoomToSelected?: boolean;
  onSelectElement: (layerId: string) => void;
}

// Inner component that can access ComponentDragContext
const EditorPanelContent: React.FC<EditorPanelContentProps> = ({ 
  className,
  selectedPageId,
  selectedLayerId,
  selectedLayer,
  selectedPage,
  previewMode,
  componentRegistry,
  autoZoomToSelected,
  onSelectElement,
}) => {
  const { isDragging: isComponentDragging } = useComponentDragContext();
  const [resizing, setResizing] = useState(false);
  const [frameSize, setFrameSize] = useState<{ width: number; height: number }>({ 
    width: 1000, 
    height: 1000 
  });
  const [pointerEventsEnabled, setPointerEventsEnabled] = useState(true);
  const frameRef = useRef<HTMLIFrameElement>(null);

  const handleResizingChange = useCallback((isDragging: boolean) => {
    setResizing(isDragging);
  }, []);

  const handleSizeChange = useCallback((width: number, height: number) => {
    setFrameSize({ width, height });
  }, []);

  const handlePointerEventsToggle = useCallback((enabled: boolean) => {
    setPointerEventsEnabled(enabled);
  }, []);

  const layers = selectedPage.children;

  // Memoize totalLayers calculation separately to avoid recalculating on every render
  const totalLayers = useMemo(() => countLayers(layers), [layers]);

  const editorConfig = useMemo(
    () => ({
      zIndex: 1,
      totalLayers: totalLayers,
      selectedLayer: selectedLayer,
      onSelectElement: onSelectElement,
    }),
    [
      totalLayers,
      selectedLayer,
      onSelectElement,
    ]
  );

  const widthClass = useMemo(() => {
    if (previewMode === "responsive") {
      return "w-full";
    } else if (previewMode === "mobile") {
      return "w-[390px]";
    } else if (previewMode === "tablet") {
      return "w-[768px]";
    } else if (previewMode === "desktop") {
      return "w-[1440px]";
    } else {
      return "w-full";
    }
  }, [previewMode]);

  const heightClass = useMemo(() => {
    if (previewMode === "responsive") {
      return "";
    } else if (previewMode === "mobile") {
      // iPhone 13 / 14 viewport: 390844
      return "h-[844px]";
    } else if (previewMode === "tablet") {
      // iPad portrait viewport: 7681024
      return "h-[1024px]";
    } else if (previewMode === "desktop") {
      // MacBook Air 13" viewport: 1440900
      return "h-[900px]";
    } else {
      return "h-full";
    }
  }, [previewMode]);


  // Memoize ResizableWrapper props
  const resizableProps = useMemo(() => ({
    isResizable: previewMode === "responsive",
    onDraggingChange: handleResizingChange,
    onSizeChange: handleSizeChange
  }), [previewMode, handleResizingChange, handleSizeChange]);

  // Memoize AutoFrame props
  const autoFrameProps = useMemo(() => ({
    height: frameSize.height,
    className: cn("shadow-lg", widthClass, heightClass),
    pointerEventsEnabled: pointerEventsEnabled
  }), [frameSize.height, widthClass, heightClass, pointerEventsEnabled]);

  // Memoize LayerRenderer props
  const layerRendererProps = useMemo(() => ({
    className: "contents",
    page: selectedPage,
    editorConfig: editorConfig,
    componentRegistry: componentRegistry
  }), [selectedPage, editorConfig, componentRegistry]);

  const renderer = useMemo(
    () => (
      <ResizableWrapper {...resizableProps}>
        <div
          id="editor-panel-content"
          className={cn("overflow-visible ", widthClass)}
        >
          <AutoFrame {...autoFrameProps} ref={frameRef}>
            <LayerRenderer {...layerRendererProps} />
          </AutoFrame>
        </div>
      </ResizableWrapper>
    ),
    [resizableProps, widthClass, autoFrameProps, layerRendererProps]
  );


  // Use static objects for consistent styles (defined outside component would be better)
  const wrapperStyle = WRAPPER_STYLE;
  const contentStyle = CONTENT_STYLE;
  const transformDivStyle = TRANSFORM_DIV_STYLE;
  const wheelConfig = WHEEL_CONFIG;
  const doubleClickConfig = DOUBLE_CLICK_CONFIG;

  // Disable panning when either resizing the viewport OR dragging components
  const panningConfig = useMemo(() => ({ 
    disabled: resizing || isComponentDragging 
  }), [resizing, isComponentDragging]);

  return (
    <div
      id="editor-panel-container"
      className={cn(
        "flex flex-col relative size-full bg-fixed bg-[radial-gradient(hsl(var(--border))_1px,hsl(var(--primary)/0.05)_1px)] [background-size:16px_16px] will-change-auto",
        className
      )}
    >
      <TransformWrapper
        initialScale={0.8}
        initialPositionX={-30}
        initialPositionY={-30}
        minScale={0.1}
        maxScale={5}
        wheel={wheelConfig}
        doubleClick={doubleClickConfig}
        panning={panningConfig}
        centerOnInit={false}
        limitToBounds={false}
      >
        <ZoomControls 
          onPointerEventsToggle={handlePointerEventsToggle}
          pointerEventsEnabled={pointerEventsEnabled}
        />
        {autoZoomToSelected && <AutoZoomToSelected 
            selectedLayerId={selectedLayerId} 
            autoZoomToSelected={autoZoomToSelected} 
          /> }
        <TransformComponent
          wrapperStyle={wrapperStyle}
          contentStyle={contentStyle}
        >
          <div
            className={cn("relative", widthClass)}
            data-testid="transform-component"
            style={transformDivStyle}
          >
            {renderer}
          </div>
        </TransformComponent>
      </TransformWrapper>
      <AddComponentsPopover parentLayerId={selectedPageId} enableDragToCanvas>
        <Button
          variant="secondary"
          size="icon"
          className="absolute bottom-4 left-4 size-14 md:size-10 flex items-center rounded-full bg-secondary shadow-lg z-[1000] [&_svg]:size-7 [&_svg]:md:size-4"
        >
          <Plus className="text-secondary-foreground" />
        </Button>
      </AddComponentsPopover>
    </div>
  );
};

/* istanbul ignore next */

// Auto-zoom to selected element component
const AutoZoomToSelected: React.FC<{
  selectedLayerId: string | null;
  autoZoomToSelected: boolean;
}> = ({ selectedLayerId, autoZoomToSelected }) => {
  const { zoomToElement } = useControls();
  const previousSelectedLayerIdRef = useRef<string | null>(null);

  // Zoom to selected element when selection changes
  useEffect(() => {
    if (!selectedLayerId || !zoomToElement || !autoZoomToSelected) return;

    // Only zoom if the selected element is different from the previous one
    if (previousSelectedLayerIdRef.current === selectedLayerId) return;

    // Update the previous selected layer ID
    previousSelectedLayerIdRef.current = selectedLayerId;

    // Small delay to ensure DOM is updated after selection change
    const timeoutId = setTimeout(() => {
      // Try to find the selected element by data attribute or ID
      const selectedElement =
        document.querySelector(`[data-layer-id="${selectedLayerId}"]`) ||
        document.getElementById(`layer-${selectedLayerId}`);

      if (selectedElement) {
        zoomToElement(selectedElement as HTMLElement, undefined, 300); // 1.5x scale, 300ms animation
      }
    }, 100);

    return () => clearTimeout(timeoutId);
  }, [selectedLayerId, zoomToElement, autoZoomToSelected]);

  return null; // This component doesn't render anything
};



import React, { useCallback, useMemo } from "react";
import { z } from "zod";
import {
  useLayerStore,
} from "@/lib/ui-builder/store/layer-store";
import { Button } from "@/components/ui/button";
import AutoForm from "@/components/ui/auto-form";
import { addDefaultValues } from "@/lib/ui-builder/store/schema-utils";
import type { ComponentLayer } from "@/components/ui/ui-builder/types";

export const ConfigPanel = () => {
  const {
    selectedPageId,
    findLayerById,
    removeLayer,
    duplicateLayer,
    updateLayer,
    pages,
  } = useLayerStore();



  const selectedLayer = findLayerById(selectedPageId) as ComponentLayer;

  const handleDeleteLayer = useCallback(
    (layerId: string) => {
      removeLayer(layerId);
    },
    [removeLayer]
  );

  const handleDuplicateLayer = useCallback(() => {
    if (selectedLayer) {
      duplicateLayer(selectedLayer.id);
    }
  }, [selectedLayer, duplicateLayer]);

  const handleUpdateLayerProps = useCallback(
    (
      id: string,
      props: Record<string, any>,
      rest?: Omit<ComponentLayer, "props" | "children">
    ) => {
      updateLayer(id, props, rest);
    },
    [updateLayer]
  );

  return (
    <PageLayerForm
      selectedLayer={selectedLayer}
      removeLayer={handleDeleteLayer}
      duplicateLayer={handleDuplicateLayer}
      updateLayerProps={handleUpdateLayerProps}
      allowDelete={pages.length > 1}
    />
  );
};

interface PageLayerFormProps {
  selectedLayer: ComponentLayer;
  removeLayer: (id: string) => void;
  duplicateLayer: (id: string) => void;
  updateLayerProps: (
    id: string,
    props: Record<string, any>,
    rest?: Omit<ComponentLayer, "props" | "children">
  ) => void;
  allowDelete: boolean;
}

const PageLayerForm: React.FC<PageLayerFormProps> = ({
  selectedLayer,
  removeLayer,
  duplicateLayer,
  updateLayerProps,
  allowDelete,
}) => {

  const schema = useMemo(() => z.object({
    name: z.string().min(1, "Name is required"),
  }), []);

  const handleSetValues = useCallback(
    (data: Partial<z.infer<typeof schema>>) => {
      const { name } = data;

      // Merge the changed fields into the existing layer
      const mergedValues = { ...selectedLayer, name, props: { } };
      const { props, ...rest } = mergedValues;

      updateLayerProps(selectedLayer.id, props, rest);
    },
    [selectedLayer, updateLayerProps]
  );

  const formSchema = useMemo(() => addDefaultValues(schema, {
    name: selectedLayer.name,
  }), [selectedLayer, schema]);

  const values = useMemo(() => ({
    name: selectedLayer.name,
  }), [selectedLayer]);

  const fieldConfig = useMemo(() => ({
    name: {
      inputProps: {
        value: selectedLayer.name,
        // defaultValue: selectedLayer.name,
      },
    },
  }), [selectedLayer]);

  const handleDuplicateLayer = useCallback(() => {
    duplicateLayer(selectedLayer.id);
  }, [selectedLayer, duplicateLayer]);

  const handleRemoveLayer = useCallback(() => {
    removeLayer(selectedLayer.id);
  }, [selectedLayer, removeLayer]);

  return (
    <AutoForm
      formSchema={formSchema}
      onValuesChange={handleSetValues}
      values={values}
      fieldConfig={fieldConfig}
    >
      <Button
        type="button"
        variant="secondary"
        className="mt-4 w-full"
        onClick={handleDuplicateLayer}
      >
        Duplicate Page
      </Button>
      {allowDelete && (
        <Button
          type="button"
          variant="destructive"
          className="mt-4 w-full"
          onClick={handleRemoveLayer}
        >
          Delete Page
        </Button>
      )}
    </AutoForm>
  );
};

export default PageLayerForm;

