"use client";

import React, { type FC, memo, useMemo } from "react";
import ReactMarkdown, { type Components, type Options } from "react-markdown";
import remarkGfm from "remark-gfm";
import remarkMath from "remark-math";
import { CodeBlock } from "@/components/ui/ui-builder/components/codeblock";
import { cn } from "@/lib/utils";

interface MarkdownProps {
  className?: string;
  children: string;
}
export function Markdown({ children, className }: MarkdownProps) {
  const components = useMemo(() => {
    return {
      a({
        children,
        href,
        className,
      }: {
        children: React.ReactNode;
        href: string;
        className: string;
      }) {
        return (
          <a
            href={href}
            target="_blank"
            rel="noopener noreferrer"
            className={cn(className, "text-blue-500 hover:text-blue-600")}
          >
            {children}
          </a>
        );
      },
      img({
        src,
        alt,
        className,
      }: {
        src: string;
        alt: string;
        className: string;
      }) {
        return (
          <img src={src} alt={alt} className={cn(className, "w-full h-auto")} />
        );
      },
      code({ className, children, ...props }: { className: string; children: React.ReactNode; [key: string]: any }) {
        const match = /language-(\w+)/.exec(className || "");

        if (match) {
          return (
            <CodeBlock
              key={Math.random()}
              language={(match && match[1]) || ""}
              value={String(children).replace(/\n$/, "")}
              {...props}
            />
          );
        }

        return (
          <code className={cn(className, "whitespace-pre-wrap")} {...props}>
            {children}
          </code>
        );
      },
    };
  }, []);

  const remarkPlugins = useMemo(() => {
    return [remarkGfm, remarkMath];
  }, []);

  return (
    <MemoizedReactMarkdown
      className={cn(
        "prose break-words prose-headings:text-secondary-foreground prose-blockquote:text-secondary-foreground prose-strong:text-secondary-foreground prose-p:leading-relaxed prose-pre:p-0 prose-pre:m-1 prose-p:text-base prose-p:font-normal prose-p:my-1 prose-ul:my-1 prose-ol:my-1 prose-li:py-0 prose-li:my-0 max-w-none text-secondary-foreground prose-code:prose-headings:bg-secondary",
        className
      )}
      remarkPlugins={remarkPlugins}
      components={components as Components}
    >
      {children}
    </MemoizedReactMarkdown>
  );
}

const MemoizedReactMarkdown: FC<Options> = memo(
  ReactMarkdown,
  (prevProps, nextProps) =>
    prevProps.children === nextProps.children &&
    prevProps.className === nextProps.className
);

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import * as LucideIcons from "lucide-react";
import { cn } from "@/lib/utils";

export type LucideIconName = keyof typeof LucideIcons;
type LucideIconComponent = React.ComponentType<React.SVGProps<SVGSVGElement>>;

const iconVariants = cva("inline-flex", {
  variants: {
    size: {
      small: "h-4 w-4",
      medium: "h-6 w-6",
      large: "h-8 w-8",
    },
    color: {
      accent: "text-accent",
      accentForeground: "text-accent-foreground",
      primary: "text-primary",
      primaryForeground: "text-primary-foreground",
      secondary: "text-secondary",
      secondaryForeground: "text-secondary-foreground",
      destructive: "text-destructive",
      destructiveForeground: "text-destructive-foreground",
      muted: "text-muted",
      mutedForeground: "text-muted-foreground",
      background: "text-background",
      foreground: "text-foreground",
    },
    rotate: {
      none: "rotate-0",
      "90": "rotate-90",
      "180": "rotate-180",
      "270": "rotate-270",
    },
  },
  defaultVariants: {
    size: "medium",
    color: "primary",
    rotate: "none",
  },
});

export interface IconProps
  extends Omit<React.HTMLAttributes<SVGElement>, "color">,
    VariantProps<typeof iconVariants> {
  iconName: LucideIconName;
}

const Icon = React.forwardRef<SVGSVGElement, IconProps>(
  ({ className, iconName, size, color, rotate, ...props }, ref) => {
    const IconComponent = LucideIcons[iconName] as LucideIconComponent;

    if (!IconComponent) {
      console.error(`Icon "${iconName}" does not exist in lucide-react`);
      return null;
    }

    return (
      <IconComponent
        className={cn(iconVariants({ size, color, rotate, className }))}
        ref={ref}
        {...props}
      />
    );
  }
);

Icon.displayName = "Icon";

export const iconNames = Object.keys(LucideIcons)
  .filter((key) => (!key.startsWith("Lucide") && !key.endsWith("Icon")) && key !== "icons")
  .map((key) => key as LucideIconName) as [LucideIconName, ...LucideIconName[]];

export { Icon, iconVariants };

