        <div className="flex items-center space-x-1">
          <Button
            variant="ghost"
            size="icon"
            onClick={onCopy}
          >
            {isCopied ? <CheckIcon /> : <CopyIcon />}
            <span className="sr-only">Copy code</span>
          </Button>
        </div>
      </div>
      {highlighted}
    </div>
  );
});

