import { useTheme } from "next-themes";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
  DropdownMenuSeparator,
} from "@/components/ui/dropdown-menu";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
  CommandShortcut,
} from "@/components/ui/command";
import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils";
import { CodePanel } from "@/components/ui/ui-builder/components/code-panel";
import {
  type EditorStore,
  useEditorStore,
} from "@/lib/ui-builder/store/editor-store";
import type {
  ComponentRegistry,
  ComponentLayer,
} from "@/components/ui/ui-builder/types";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import {
  type KeyCombination,
  useKeyboardShortcuts,
} from "@/hooks/use-keyboard-shortcuts";
import { useStore } from "zustand";
import { toKeyboardShortcut } from "@/lib/ui-builder/shortcuts/shortcut-registry";

const Z_INDEX = 1000;

export interface NavBarProps {
  leftChildren?: React.ReactNode;
  rightChildren?: React.ReactNode;
  /** Whether to show the Export button. Defaults to true. */
  showExport?: boolean;
}

export function NavBar({ leftChildren, rightChildren, showExport = true }: NavBarProps = {}) {
  const selectedPageId = useLayerStore((state) => state.selectedPageId);
  const findLayerById = useLayerStore((state) => state.findLayerById);
  const componentRegistry = useEditorStore((state) => state.registry);
  const incrementRevision = useEditorStore((state) => state.incrementRevision);

  // Panel visibility state
  const showLeftPanel = useEditorStore((state) => state.showLeftPanel);
  const setShowLeftPanel = useEditorStore((state) => state.setShowLeftPanel);
  const showRightPanel = useEditorStore((state) => state.showRightPanel);
  const setShowRightPanel = useEditorStore((state) => state.setShowRightPanel);

  // Fix: Subscribe to temporal state changes using useStoreWithEqualityFn
  const pastStates = useStore(
    useLayerStore.temporal,
    (state) => state.pastStates
  );
  const futureStates = useStore(
    useLayerStore.temporal,
    (state) => state.futureStates
  );
  const { undo, redo } = useLayerStore.temporal.getState();

  const page = findLayerById(selectedPageId) as ComponentLayer;

  const canUndo = !!pastStates.length;
  const canRedo = !!futureStates.length;

  // **Lifted State for Modals**
  const [isPreviewModalOpen, setIsPreviewModalOpen] = useState(false);
  const [isExportModalOpen, setIsExportModalOpen] = useState(false);

  const handleUndo = useCallback(() => {
    undo();
    // Increment revision counter to trigger form revalidation
    incrementRevision();
  }, [undo, incrementRevision]);

  const handleRedo = useCallback(() => {
    redo();
    // Increment revision counter to trigger form revalidation
    incrementRevision();
  }, [redo, incrementRevision]);

  const keyCombinations = useMemo<KeyCombination[]>(
    () => [
      // Use shortcut registry for undo/redo
      toKeyboardShortcut('undo', handleUndo),
      toKeyboardShortcut('redo', handleRedo),
      // Debug shortcuts (not in registry)
      {
        keys: { metaKey: true, shiftKey: true },
        key: "9",
        handler: () => {
          const elements = document.querySelectorAll("*");
          elements.forEach((element) => {
            element.classList.add("animate-spin", "origin-center");
          });
        },
      },
      {
        keys: { metaKey: true, shiftKey: true },
        key: "0",
        handler: () => {
          const elements = document.querySelectorAll("*");
          elements.forEach((element) => {
            element.classList.remove("animate-spin", "origin-center");
          });
        },
      },
    ],
    [handleUndo, handleRedo]
  );

  useKeyboardShortcuts(keyCombinations);

  const handleOpenPreview = useCallback(() => {
    setIsPreviewModalOpen(true);
  }, []);
  const handleOpenExport = useCallback(() => {
    setIsExportModalOpen(true);
  }, []);

  const handleToggleLeftPanel = useCallback(() => {
    setShowLeftPanel(!showLeftPanel);
  }, [showLeftPanel, setShowLeftPanel]);

  const handleToggleRightPanel = useCallback(() => {
    setShowRightPanel(!showRightPanel);
  }, [showRightPanel, setShowRightPanel]);

  const style = useMemo(() => ({ zIndex: Z_INDEX }), []);

  return (
    <div
      className="flex items-center justify-between bg-background px-2 md:px-6 py-4 border-b"
      style={style}
    >
      <div className="flex items-center gap-2">
        {leftChildren}
        {leftChildren && <div className="hidden md:flex h-10 w-px bg-border"></div>}
        <div className="hidden md:contents">
        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              onClick={handleToggleLeftPanel}
              variant={showLeftPanel ? "secondary" : "outline"}
              size="icon"
              className="flex flex-col justify-center"
            >
              <span className="sr-only">Toggle Left Panel</span>
              <PanelLeft className="w-4 h-4" />
            </Button>
          </TooltipTrigger>
          <TooltipContent>
            {showLeftPanel ? "Hide" : "Show"} Left Panel
          </TooltipContent>
        </Tooltip>

        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              onClick={handleToggleRightPanel}
              variant={showRightPanel ? "secondary" : "outline"}
              size="icon"
              className="flex flex-col justify-center"
            >
              <span className="sr-only">Toggle Right Panel</span>
              <PanelRight className="w-4 h-4" />
            </Button>
          </TooltipTrigger>
          <TooltipContent>
            {showRightPanel ? "Hide" : "Show"} Right Panel
          </TooltipContent>
        </Tooltip>
        </div>
        <div className="hidden md:flex h-10 w-px bg-border"></div>
        <PagesPopover />
        <PreviewModeToggle />
      </div>

      <div className="w-full flex items-center justify-end gap-2">
        {/* Action Buttons for Larger Screens */}
        <div className="hidden md:flex space-x-2">
          <ActionButtons
            canUndo={canUndo}
            canRedo={canRedo}
            onUndo={handleUndo}
            onRedo={handleRedo}
            onOpenPreview={handleOpenPreview}
            onOpenExport={handleOpenExport}
            showExport={showExport}
          />
          <div className="h-10 flex w-px bg-border"></div>
        </div>

        <ModeToggle />

        {/* Dropdown for Smaller Screens */}
        <div className="flex md:hidden space-x-2">
          <div className="h-10 flex w-px bg-border"></div>
          <ResponsiveDropdown
            canUndo={canUndo}
            canRedo={canRedo}
            onUndo={handleUndo}
            onRedo={handleRedo}
            onOpenPreview={handleOpenPreview}
            onOpenExport={handleOpenExport}
            showExport={showExport}
          >
            {rightChildren}
          </ResponsiveDropdown>
        </div>
        {/* Right children - hidden on mobile (shown in dropdown instead) */}
        {rightChildren && (
          <div className="hidden md:flex items-center gap-2">
            <div className="h-10 w-px bg-border"></div>
            {rightChildren}
          </div>
        )}
      </div>

      {/* **Dialogs Controlled by NavBar State** */}
      <PreviewDialog
        isOpen={isPreviewModalOpen}
        onOpenChange={setIsPreviewModalOpen}
        page={page}
        componentRegistry={componentRegistry}
      />
      {showExport && (
        <CodeDialog
          isOpen={isExportModalOpen}
          onOpenChange={setIsExportModalOpen}
        />
      )}
    </div>
  );
}

/**
 * Reusable Action Buttons Component
 */
interface ActionButtonsProps {
  canUndo: boolean;
  canRedo: boolean;
  onUndo: () => void;
  onRedo: () => void;
  onOpenPreview: () => void;
  onOpenExport: () => void;
  showExport?: boolean;
}

const ActionButtons: React.FC<ActionButtonsProps> = ({
  canUndo,
  canRedo,
  onUndo,
  onRedo,
  onOpenPreview,
  onOpenExport,
  showExport = true,
}) => {
  return (
    <>
      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            onClick={onUndo}
            variant="secondary"
            size="icon"
            disabled={!canUndo}
            className="flex flex-col justify-center"
          >
            <span className="sr-only">Undo</span>
            <Undo className="w-4 h-4" />
          </Button>
        </TooltipTrigger>
        <TooltipContent className="flex items-center gap-2">
          Undo
          <CommandShortcut className="ml-0 text-sm leading-3">
            Z
          </CommandShortcut>
        </TooltipContent>
      </Tooltip>

      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            onClick={onRedo}
            variant="secondary"
            size="icon"
            disabled={!canRedo}
            className="flex flex-col justify-center"
          >
            <span className="sr-only">Redo</span>
            <Redo className="w-4 h-4" />
          </Button>
        </TooltipTrigger>
        <TooltipContent className="flex items-center gap-2">
          Redo
          <CommandShortcut className="ml-0 text-sm leading-3">
            ++Z
          </CommandShortcut>
        </TooltipContent>
      </Tooltip>

      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            onClick={onOpenPreview}
            variant="secondary"
            size="icon"
            className="flex flex-col justify-center"
          >
            <span className="sr-only">Preview</span>
            <Eye className="w-4 h-4" />
          </Button>
        </TooltipTrigger>
        <TooltipContent className="flex items-center gap-2">
          Preview
          <CommandShortcut className="ml-0 text-sm leading-3">
            ++P
          </CommandShortcut>
        </TooltipContent>
      </Tooltip>

      {showExport && (
        <Tooltip>
          <TooltipTrigger asChild>
            <Button
              onClick={onOpenExport}
              variant="secondary"
              size="icon"
              className="flex flex-col justify-center"
            >
              <span className="sr-only">Export</span>
              <FileUp className="w-4 h-4" />
            </Button>
          </TooltipTrigger>
          <TooltipContent className="flex items-center gap-2">
            Export Code
            <CommandShortcut className="ml-0 text-sm leading-3">
              ++E
            </CommandShortcut>
          </TooltipContent>
        </Tooltip>
      )}
    </>
  );
};

/**
 * Dropdown containing Action Buttons for Smaller Screens
 */
interface ResponsiveDropdownProps {
  canUndo: boolean;
  canRedo: boolean;
  onUndo: () => void;
  onRedo: () => void;
  onOpenPreview: () => void;
  onOpenExport: () => void;
  showExport?: boolean;
  /** Content to render at the bottom of the dropdown (for rightChildren on mobile) */
  children?: React.ReactNode;
}

const ResponsiveDropdown: React.FC<ResponsiveDropdownProps> = ({
  canUndo,
  canRedo,
  onUndo,
  onRedo,
  onOpenPreview,
  onOpenExport,
  showExport = true,
  children,
}) => {
  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="icon">
          <span className="sr-only">Actions</span>
          <MoreVertical className="w-4 h-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" style={style}>
        <DropdownMenuItem
          className="gap-2"
          onClick={onUndo}
          disabled={!canUndo}
        >
          <Undo className="w-4 h-4" />
          Undo
          <span className="ml-auto text-xs text-muted-foreground">Z</span>
        </DropdownMenuItem>
        <DropdownMenuItem
          className="gap-2"
          onClick={onRedo}
          disabled={!canRedo}
        >
          <Redo className="w-4 h-4" />
          Redo
          <span className="ml-auto text-xs text-muted-foreground">++Z</span>
        </DropdownMenuItem>
        <DropdownMenuSeparator />
        <DropdownMenuItem className="gap-2" onClick={onOpenPreview}>
          <Eye className="w-4 h-4" />
          Preview
          <span className="ml-auto text-xs text-muted-foreground">++P</span>
        </DropdownMenuItem>
        {showExport && (
          <DropdownMenuItem className="gap-2" onClick={onOpenExport}>
            <FileUp className="w-4 h-4" />
            Export
            <span className="ml-auto text-xs text-muted-foreground">++E</span>
          </DropdownMenuItem>
        )}
        {children && (
          <>
            <DropdownMenuSeparator />
            <div className="p-2">
              {children}
            </div>
          </>
        )}
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

/**
 * Preview Dialog Component
 */
interface PreviewDialogProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  page: ComponentLayer;
  componentRegistry: ComponentRegistry;
}

const PreviewDialog: React.FC<PreviewDialogProps> = ({
  isOpen,
  onOpenChange,
  page,
  componentRegistry,
}) => {
  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);

  const shortcuts = useMemo(
    () => [
      {
        keys: { metaKey: true, shiftKey: true },
        key: "p",
        handler: () => {
          onOpenChange(true);
        },
      },
    ],
    [onOpenChange]
  );

  useKeyboardShortcuts(shortcuts);

  return (
    <Dialog open={isOpen} onOpenChange={onOpenChange}>
      <DialogTrigger />
      <DialogContentWithZIndex
        className="max-w-[calc(100dvw)] max-h-[calc(100dvh)] overflow-auto p-0 gap-0"
        style={style}
      >
        <DialogHeader>
          <DialogTitle className="py-3 bg-yellow-600 text-center">
            <span className="text-lg font-semibold">Page Preview</span>
          </DialogTitle>
        </DialogHeader>
        <LayerRenderer
          className="w-full h-full flex flex-col overflow-x-hidden"
          page={page}
          componentRegistry={componentRegistry}
        />
      </DialogContentWithZIndex>
    </Dialog>
  );
};

