function ModeToggle() {
  const { setTheme } = useTheme();

  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);

  const handleSetLightTheme = useCallback(() => {
    setTheme("light");
  }, [setTheme]);
  const handleSetDarkTheme = useCallback(() => {
    setTheme("dark");
  }, [setTheme]);
  const handleSetSystemTheme = useCallback(() => {
    setTheme("system");
  }, [setTheme]);

  return (
    <DropdownMenu>
      <Tooltip>
        <DropdownMenuTrigger asChild>
          <TooltipTrigger asChild>
            <Button variant="outline" size="icon">
              <SunIcon className="h-[1.2rem] w-[1.2rem] rotate-0 scale-100 transition-all dark:-rotate-90 dark:scale-0" />
              <MoonIcon className="absolute h-[1.2rem] w-[1.2rem] rotate-90 scale-0 transition-all dark:rotate-0 dark:scale-100" />
              <span className="sr-only">Toggle theme</span>
            </Button>
          </TooltipTrigger>
        </DropdownMenuTrigger>
        <TooltipContent>Toggle theme</TooltipContent>
      </Tooltip>
      <DropdownMenuContent align="end" style={style}>
        <DropdownMenuItem onClick={handleSetLightTheme}>Light</DropdownMenuItem>
        <DropdownMenuItem onClick={handleSetDarkTheme}>Dark</DropdownMenuItem>
        <DropdownMenuItem onClick={handleSetSystemTheme}>
          System
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

function PagesPopover() {
  const { pages, selectedPageId, addPageLayer, selectPage } = useLayerStore();
  const [open, setOpen] = useState(false);
  const [inputValue, setInputValue] = useState("");
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const [selectedPage, setSelectedPage] = useState<string | null>(
    selectedPageId
  );
  const [textInputValue, setTextInputValue] = useState("");
  const allowPagesCreation = useEditorStore(
    (state) => state.allowPagesCreation
  );

  const selectedPageData = useMemo(() => {
    return pages.find((page) => page.id === selectedPageId);
  }, [pages, selectedPageId]);

  const handleSelect = useCallback(
    (pageId: string) => {
      setSelectedPage(pageId);
      selectPage(pageId);
      setOpen(false);
    },
    [selectPage, setOpen]
  );

  const handleAddPageLayer = useCallback(
    (pageName: string) => {
      addPageLayer(pageName);
      setTextInputValue("");
    },
    [addPageLayer, setTextInputValue]
  );

  const handleSubmit = useCallback(
    (e: React.FormEvent<HTMLFormElement>) => {
      e.preventDefault();
      handleAddPageLayer(textInputValue);
    },
    [handleAddPageLayer, textInputValue]
  );

  const handleTextInputChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setTextInputValue(e.target.value);
    },
    [setTextInputValue]
  );

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLInputElement>) => {
      if (e.key === "Enter") {
        e.preventDefault();
        handleAddPageLayer(textInputValue);
      }
    },
    [handleAddPageLayer, textInputValue]
  );

  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);

  const textInputForm = (
    <form className="w-full" onSubmit={handleSubmit}>
      <div className="w-full flex items-center space-x-2">
        <Input
          className="w-full flex-grow"
          placeholder="New page name..."
          value={textInputValue}
          onChange={handleTextInputChange}
          onKeyDown={handleKeyDown}
        />
        <Button type="submit" variant="secondary">
          <PlusIcon className="w-4 h-4" />
        </Button>
      </div>
    </form>
  );
  return (
    <div className="relative flex justify-center">
      <Popover open={open} onOpenChange={setOpen}>
        <Tooltip>
          <PopoverTrigger asChild>
            <TooltipTrigger asChild>
              <Button
                variant="outline"
                size="default"
                className="max-w-30 overflow-hidden"
              >
                {selectedPageData?.name}
              </Button>
            </TooltipTrigger>
          </PopoverTrigger>
          <TooltipContent>Select page</TooltipContent>
        </Tooltip>
        <PopoverContent className="w-[300px] p-0" style={style}>
          <Command>
            <CommandInput
              placeholder="Select page or create new..."
              value={inputValue}
              onValueChange={setInputValue}
            />
            <CommandList>
              <CommandEmpty>
                No pages found
                {allowPagesCreation && textInputForm}
              </CommandEmpty>
              {pages.map((page) => (
                <PageItem
                  key={page.id}
                  selectedPageId={selectedPageId}
                  page={page}
                  onSelect={handleSelect}
                />
              ))}
              <CommandSeparator />
              {allowPagesCreation && (
                <CommandGroup heading="Create new page">
                  <CommandItem>{textInputForm}</CommandItem>
                </CommandGroup>
              )}
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>
    </div>
  );
}

const PageItem = ({
  selectedPageId,
  page,
  onSelect,
}: {
  selectedPageId: string;
  page: ComponentLayer;
  onSelect: (pageId: string) => void;
}) => {
  const handleSelect = useCallback(() => {
    onSelect(page.id);
  }, [onSelect, page.id]);

  return (
    <CommandItem
      value={page.name}
      onSelect={handleSelect}
      className={cn(selectedPageId === page.id && "font-bold")}
    >
      {selectedPageId === page.id ? (
        <CheckIcon className="w-4 h-4 mr-2" />
      ) : null}
      {page.name}
    </CommandItem>
  );
};

const DialogContentWithZIndex = forwardRef<
  React.ElementRef<typeof DialogContent>,
  React.ComponentPropsWithoutRef<typeof DialogContent>
>(({ className, children, ...props }, ref) => {
  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);
  return (
    <DialogPortal>
      <DialogOverlay style={style} />
      <DialogContent
        ref={ref}
        className={cn(
          "fixed left-[50%] top-[50%] grid w-full max-w-lg translate-x-[-50%] translate-y-[-50%] gap-4 border bg-background p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-left-1/2 data-[state=open]:slide-in-from-top-[48%] sm:rounded-lg",
          className
        )}
        {...props}
      >
        {children}
        <DialogClose className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-accent data-[state=open]:text-muted-foreground">
          <X className="h-4 w-4 rounded-full p-1" />
          <span className="sr-only">Close</span>
        </DialogClose>
      </DialogContent>
    </DialogPortal>
  );
});

DialogContentWithZIndex.displayName = "DialogContentWithZIndex";

const PreviewModeToggle = () => {
  const { previewMode, setPreviewMode } = useEditorStore();

  const handleSelect = useCallback((mode: EditorStore["previewMode"]) => {
    setPreviewMode(mode);
  }, [setPreviewMode]);

  const style = useMemo(() => ({ zIndex: Z_INDEX + 1 }), []);

  const previewModeIcon = useMemo(() => {
    return {
      mobile: <Smartphone className="h-4 w-4" />,
      tablet: <Tablet className="h-4 w-4" />,
      desktop: <Monitor className="h-4 w-4" />,
      responsive: <Maximize className="h-4 w-4" />,
    }[previewMode];
  }, [previewMode]);

  const handleSelectMobile = useCallback(() => {
    handleSelect("mobile");
  }, [handleSelect]);
  const handleSelectTablet = useCallback(() => {
    handleSelect("tablet");
  }, [handleSelect]);
  const handleSelectDesktop = useCallback(() => {
    handleSelect("desktop");
  }, [handleSelect]);
  const handleSelectResponsive = useCallback(() => {
    handleSelect("responsive");
  }, [handleSelect]);

  return (
    <DropdownMenu>
      <Tooltip>
        <DropdownMenuTrigger asChild>
          <TooltipTrigger asChild>
            <Button variant="outline" size="icon">
              {previewModeIcon}
              <span className="sr-only">Select screen size</span>
            </Button>
          </TooltipTrigger>
        </DropdownMenuTrigger>
        <TooltipContent>Select screen size</TooltipContent>
      </Tooltip>
      <DropdownMenuContent align="end" style={style}>
        <DropdownMenuItem
          onSelect={handleSelectMobile}
          className={
            previewMode === "mobile"
              ? "bg-secondary text-secondary-foreground"
              : ""
          }
        >
          <Smartphone className="mr-2 h-4 w-4" />
          <span>Mobile</span>
        </DropdownMenuItem>
        <DropdownMenuItem
          onSelect={handleSelectTablet}
          className={
            previewMode === "tablet"
              ? "bg-secondary text-secondary-foreground"
              : ""
          }
        >
          <Tablet className="mr-2 h-4 w-4" />
          <span>Tablet</span>
        </DropdownMenuItem>
        <DropdownMenuItem
          onSelect={handleSelectDesktop}
          className={
            previewMode === "desktop"
              ? "bg-secondary text-secondary-foreground"
              : ""
          }
        >
          <Monitor className="mr-2 h-4 w-4" />
          <span>Desktop</span>
        </DropdownMenuItem>
        <DropdownMenuItem
          onSelect={handleSelectResponsive}
          className={
            previewMode === "responsive"
              ? "bg-secondary text-secondary-foreground"
              : ""
          }
        >
          <Maximize className="mr-2 h-4 w-4" />
          <span>Responsive</span>
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

import React, { useState, useEffect, useCallback } from "react"
import { Button } from "@/components/ui/button"
import { Check, X as XIcon } from "lucide-react"
import { Input } from "@/components/ui/input"

interface NameEditProps {
  initialName: string
  onSave: (newName: string) => void
  onCancel: () => void
}

export const NameEdit: React.FC<NameEditProps> = ({ initialName, onSave, onCancel }) => {
  const [newName, setNewName] = useState(initialName)

  const handleSave = useCallback(() => {
    if (newName.trim()) {
      onSave(newName.trim())
    }
  }, [newName, onSave])

  const handleCancel = useCallback(() => {
    setNewName(initialName)
    onCancel()
  }, [initialName, onCancel])

  const handleChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    setNewName(e.target.value)
  }, [])

  // Handle Enter key for saving and Escape key for canceling
  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLInputElement>) => {
      if (e.key === "Enter") {
        handleSave()
      } else if (e.key === "Escape") {
        handleCancel()
      }
    },
    [handleSave, handleCancel]
  )

  useEffect(() => {
    setNewName(initialName)
  }, [initialName])

  return (
    <div className="flex items-center">
      <Input
        type="text"
        value={newName}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        autoFocus
      />
      <Button
        variant="ghost"
        size="icon"
        onClick={handleSave}
        className="ml-1"
        aria-label="Save rename"
      >
        <Check className="h-4 w-4" />
      </Button>
      <Button
        variant="ghost"
        size="icon"
        onClick={handleCancel}
        className="ml-1"
        aria-label="Cancel rename"
      >
        <XIcon className="h-4 w-4" />
      </Button>
    </div>
  )
}

"use client";

import { Command as CommandPrimitive, useCommandState } from "cmdk";
import { X } from "lucide-react";
import * as React from "react";
import { forwardRef, useEffect, useMemo, useCallback } from "react";

import { Badge } from "@/components/ui/badge";
import {
  Command,
  CommandGroup,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import { cn } from "@/lib/utils";
import { useDebounce } from "@/hooks/use-debounce";

const EMPTY_ARRAY: any[] = [];

export interface Option {
  value: string;
  label: string;
  disable?: boolean;
  /** fixed option that can't be removed. */
  fixed?: boolean;
  /** Group the options by providing key. */
  [key: string]: string | boolean | undefined;
}
interface GroupOption {
  [key: string]: Option[];
}

interface MultipleSelectorProps {
  value?: Option[];
  defaultOptions?: Option[];
  /** manually controlled options */
  options?: Option[];
  placeholder?: string;
  /** Loading component. */
  loadingIndicator?: React.ReactNode;
  /** Empty component. */
  emptyIndicator?: React.ReactNode;
  /** Debounce time for async search. Only work with `onSearch`. */
  delay?: number;
  /**
   * Only work with `onSearch` prop. Trigger search when `onFocus`.
   * For example, when user click on the input, it will trigger the search to get initial options.
   **/
  triggerSearchOnFocus?: boolean;
  /** async search */
  onSearch?: (value: string) => Promise<Option[]>;
  /**
   * sync search. This search will not showing loadingIndicator.
   * The rest props are the same as async search.
   * i.e.: creatable, groupBy, delay.
   **/
  onSearchSync?: (value: string) => Option[];
  onChange?: (options: Option[]) => void;
  /** Limit the maximum number of selected options. */
  maxSelected?: number;
  /** When the number of selected options exceeds the limit, the onMaxSelected will be called. */
  onMaxSelected?: (maxLimit: number) => void;
  /** Hide the placeholder when there are options selected. */
  hidePlaceholderWhenSelected?: boolean;
  disabled?: boolean;
  /** Group the options base on provided key. */
  groupBy?: string;
  className?: string;
  badgeClassName?: string;
  /**
   * First item selected is a default behavior by cmdk. That is why the default is true.
   * This is a workaround solution by add a dummy item.
   *
   * @reference: https://github.com/pacocoursey/cmdk/issues/171
   */
  selectFirstItem?: boolean;
  /** Allow user to create option when there is no option matched. */
  creatable?: boolean;
  /** Props of `Command` */
  commandProps?: React.ComponentPropsWithoutRef<typeof Command>;
  /** Props of `CommandInput` */
  inputProps?: Omit<
    React.ComponentPropsWithoutRef<typeof CommandPrimitive.Input>,
    "value" | "placeholder" | "disabled"
  >;
  /** hide the clear all button. */
  hideClearAllButton?: boolean;
}

export interface MultipleSelectorRef {
  selectedValue: Option[];
  input: HTMLInputElement;
  focus: () => void;
  reset: () => void;
}

function transToGroupOption(options: Option[], groupBy?: string) {
  if (options.length === 0) {
    return {};
  }
  if (!groupBy) {
    return {
      "": options,
    };
  }

  const groupOption: GroupOption = {};
  options.forEach((option) => {
    const key = (option[groupBy] as string) || "";
    if (!groupOption[key]) {
      groupOption[key] = [];
    }
    const group = groupOption[key];
    if (group) {
      group.push(option);
    }
  });
  return groupOption;
}

function removePickedOption(groupOption: GroupOption, picked: Option[]) {
  const cloneOption = JSON.parse(JSON.stringify(groupOption)) as GroupOption;

  for (const [key, value] of Object.entries(cloneOption)) {
    cloneOption[key] = value.filter(
      (val) => !picked.find((p) => p.value === val.value)
    );
  }
  return cloneOption;
}

function isOptionsExist(groupOption: GroupOption, targetOption: Option[]) {
  for (const [, value] of Object.entries(groupOption)) {
    if (
      value.some((option) => targetOption.find((p) => p.value === option.value))
    ) {
      return true;
    }
  }
  return false;
}

/**
 * The `CommandEmpty` of shadcn/ui will cause the cmdk empty not rendering correctly.
 * So we create one and copy the `Empty` implementation from `cmdk`.
 *
 * @reference: https://github.com/hsuanyi-chou/shadcn-ui-expansions/issues/34#issuecomment-1949561607
 **/
const CommandEmpty = forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof CommandPrimitive.Empty>
>(({ className, ...props }, forwardedRef) => {
  const render = useCommandState((state) => state.filtered.count === 0);

  if (!render) return null;

  return (
    <div
      ref={forwardedRef}
      className={cn("py-6 text-center text-sm", className)}
      cmdk-empty=""
      role="presentation"
      {...props}
    />
  );
});

CommandEmpty.displayName = "CommandEmpty";

const MultipleSelector = React.forwardRef<
  MultipleSelectorRef,
  MultipleSelectorProps
>(
  (
    {
      value,
      onChange,
      placeholder,
      defaultOptions: arrayDefaultOptions = EMPTY_ARRAY,
      options: arrayOptions,
      delay,
      onSearch,
      onSearchSync,
      loadingIndicator,
      emptyIndicator,
      maxSelected = Number.MAX_SAFE_INTEGER,
      onMaxSelected,
      hidePlaceholderWhenSelected,
      disabled,
      groupBy,
      className,
      badgeClassName,
      selectFirstItem = true,
      creatable = false,
      triggerSearchOnFocus = false,
      commandProps,
      inputProps,
      hideClearAllButton = false,
    }: MultipleSelectorProps,
    ref: React.Ref<MultipleSelectorRef>
  ) => {
    const inputRef = React.useRef<HTMLInputElement>(null);
    const [open, setOpen] = React.useState(false);
    const [onScrollbar, setOnScrollbar] = React.useState(false);
    const [isLoading, setIsLoading] = React.useState(false);
    const dropdownRef = React.useRef<HTMLDivElement>(null); // Added this

    const [selected, setSelected] = React.useState<Option[]>(
      value || EMPTY_ARRAY
    );
    const [options, setOptions] = React.useState<GroupOption>(
      transToGroupOption(arrayDefaultOptions, groupBy)
    );
    const [inputValue, setInputValue] = React.useState("");
    const debouncedSearchTerm = useDebounce(inputValue, delay || 500);

    const selectedValue = useMemo(() => {
      return {
        selectedValue: [...selected],
        input: inputRef.current as HTMLInputElement,
        focus: () => inputRef?.current?.focus(),
        reset: () => setSelected([]),
      };
    }, [selected]);

    React.useImperativeHandle(ref, () => selectedValue, [selectedValue]);

    const handleClickOutside = (event: MouseEvent | TouchEvent) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(event.target as Node) &&
        inputRef.current &&
        !inputRef.current.contains(event.target as Node)
      ) {
        setOpen(false);
        inputRef.current.blur();
      }
    };

    const handleUnselect = React.useCallback(
      (option: Option) => {
        const newOptions = selected.filter((s) => s.value !== option.value);
        setSelected(newOptions);
        onChange?.(newOptions);
      },
      [onChange, selected]
    );

    const handleKeyDown = React.useCallback(
      (e: React.KeyboardEvent<HTMLDivElement>) => {
        const input = inputRef.current;
        if (input) {
          if (e.key === "Delete" || e.key === "Backspace") {
            if (input.value === "" && selected.length > 0) {
              const lastSelectOption = selected[selected.length - 1];
              // If last item is fixed, we should not remove it.
              if (lastSelectOption && !lastSelectOption.fixed) {
                handleUnselect(lastSelectOption);
              }
            }
          }
          // This is not a default behavior of the <input /> field
          if (e.key === "Escape") {
            input.blur();
          }
        }
      },
      [handleUnselect, selected]
    );

    useEffect(() => {
      if (open) {
        document.addEventListener("mousedown", handleClickOutside);
        document.addEventListener("touchend", handleClickOutside);
      } else {
        document.removeEventListener("mousedown", handleClickOutside);
        document.removeEventListener("touchend", handleClickOutside);
      }

      return () => {
        document.removeEventListener("mousedown", handleClickOutside);
        document.removeEventListener("touchend", handleClickOutside);
      };
    }, [open]);

    useEffect(() => {
      if (value) {
        setSelected(value);
      }
    }, [value]);

    useEffect(() => {
      /** If `onSearch` is provided, do not trigger options updated. */
      if (!arrayOptions || onSearch) {
        return;
      }
      const newOption = transToGroupOption(
        arrayOptions || EMPTY_ARRAY,
        groupBy
      );
      if (JSON.stringify(newOption) !== JSON.stringify(options)) {
        setOptions(newOption);
      }
    }, [arrayDefaultOptions, arrayOptions, groupBy, onSearch, options]);

    useEffect(() => {
      /** sync search */

      const doSearchSync = () => {
        const res = onSearchSync?.(debouncedSearchTerm);
        setOptions(transToGroupOption(res || [], groupBy));
      };

      const exec = async () => {
        if (!onSearchSync || !open) return;

        if (triggerSearchOnFocus) {
          doSearchSync();
        }

        if (debouncedSearchTerm) {
          doSearchSync();
        }
      };

      void exec();
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [debouncedSearchTerm, groupBy, open, triggerSearchOnFocus]);

    useEffect(() => {
      /** async search */

      const doSearch = async () => {
        setIsLoading(true);
        const res = await onSearch?.(debouncedSearchTerm);
        setOptions(transToGroupOption(res || [], groupBy));
        setIsLoading(false);
      };

      const exec = async () => {
        if (!onSearch || !open) return;

        if (triggerSearchOnFocus) {
          await doSearch();
        }

        if (debouncedSearchTerm) {
          await doSearch();
        }
      };

      void exec();
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [debouncedSearchTerm, groupBy, open, triggerSearchOnFocus]);

    const handleCommandKeyDown = useCallback(
      (e: React.KeyboardEvent<HTMLDivElement>) => {
        handleKeyDown(e);
        commandProps?.onKeyDown?.(e);
      },
      [handleKeyDown, commandProps]
    );

    const handleBadgeGroupClick = useCallback(() => {
      if (disabled) return;
      inputRef?.current?.focus();
    }, [disabled]);

    const targetOption = useMemo(() => {
      return [{ value: inputValue, label: inputValue }];
    }, [inputValue]);

    const handleCommandItemMouseDown = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        e.preventDefault();
        e.stopPropagation();
      },
      []
    );

    const handleCommandItemSelect = useCallback(
      (value: string) => {
        if (selected.length >= maxSelected) {
          onMaxSelected?.(selected.length);
          return;
        }
        setInputValue("");
        const newOptions = [...selected, { value, label: value }];
        setSelected(newOptions);
        onChange?.(newOptions);
      },
      [maxSelected, onMaxSelected, selected, onChange]
    );

    const CreatableItem = () => {
      if (!creatable) return undefined;
      if (
        isOptionsExist(options, targetOption) ||
        selected.find((s) => s.value === inputValue)
      ) {
        return undefined;
      }

      const Item = (
        <CommandItem
          value={inputValue}
          className="cursor-pointer"
          onMouseDown={handleCommandItemMouseDown}
          onSelect={handleCommandItemSelect}
        >
          {`Create "${inputValue}"`}
        </CommandItem>
      );

      // For normal creatable
      if (!onSearch && inputValue.length > 0) {
        return Item;
      }

      // For async search creatable. avoid showing creatable item before loading at first.
      if (onSearch && debouncedSearchTerm.length > 0 && !isLoading) {
        return Item;
      }

      return undefined;
    };

    const EmptyItem = React.useCallback(() => {
      if (!emptyIndicator) return undefined;

      // For async search that showing emptyIndicator
      if (onSearch && !creatable && Object.keys(options).length === 0) {
        return (
          <CommandItem value="-" disabled>
            {emptyIndicator}
          </CommandItem>
        );
      }

      return <CommandEmpty>{emptyIndicator}</CommandEmpty>;
    }, [creatable, emptyIndicator, onSearch, options]);

    const selectables = React.useMemo<GroupOption>(
      () => removePickedOption(options, selected),
      [options, selected]
    );

    /** Avoid Creatable Selector freezing or lagging when paste a long string. */
    const commandFilter = React.useCallback(() => {
      if (commandProps?.filter) {
        return commandProps.filter;
      }

      if (creatable) {
        return (value: string, search: string) => {
          return value.toLowerCase().includes(search.toLowerCase()) ? 1 : -1;
        };
      }
      // Using default filter in `cmdk`. We don't have to provide it.
      return undefined;
    }, [creatable, commandProps?.filter]);

    const handlePrimitiveInputValueChange = useCallback(
      (value: string) => {
        setInputValue(value);
        inputProps?.onValueChange?.(value);
      },
      [inputProps]
    );

    const handlePrimitiveInputBlur = useCallback(
      (event: React.FocusEvent<HTMLInputElement, Element>) => {
        if (!onScrollbar) {
          setOpen(false);
        }
        inputProps?.onBlur?.(event);
      },
      [onScrollbar, inputProps]
    );

    const handlePrimitiveInputFocus = useCallback(
      (event: React.FocusEvent<HTMLInputElement, Element>) => {
        setOpen(true);
        if (triggerSearchOnFocus) {
          onSearch?.(debouncedSearchTerm);
        }
        inputProps?.onFocus?.(event);
      },
      [triggerSearchOnFocus, debouncedSearchTerm, inputProps, onSearch]
    );

    const handleClearAllButtonClick = useCallback(() => {
      setSelected(selected.filter((s) => s.fixed));
      onChange?.(selected.filter((s) => s.fixed));
    }, [selected, onChange]);

    const handleCommandListMouseLeave = useCallback(() => {
      setOnScrollbar(false);
    }, []);

    const handleCommandListMouseEnter = useCallback(() => {
      setOnScrollbar(true);
    }, []);

    const handleCommandListMouseUp = useCallback(() => {
      inputRef?.current?.focus();
    }, []);

    const handleCommandGroupItemMouseDown = useCallback(
      (e: React.MouseEvent<HTMLDivElement>) => {
        e.preventDefault();
        e.stopPropagation();
      },
      []
    );

    const handleCommandGroupItemSelect = useCallback(
      (option: Option) => {
        if (selected.length >= maxSelected) {
          onMaxSelected?.(selected.length);
          return;
        }
        setInputValue("");
        const newOptions = [...selected, option];
        setSelected(newOptions);
        onChange?.(newOptions);
      },
      [maxSelected, onMaxSelected, selected, onChange]
    );

    return (
      <Command
        ref={dropdownRef}
        {...commandProps}
        onKeyDown={handleCommandKeyDown}
        className={cn(
          "h-auto overflow-visible bg-transparent",
          commandProps?.className
        )}
        shouldFilter={
          commandProps?.shouldFilter !== undefined
            ? commandProps.shouldFilter
            : !onSearch
        } // When onSearch is provided, we don't want to filter the options. You can still override it.
        filter={commandFilter()}
      >
        <div
          className={cn(
            "min-h-10 rounded-md border border-input text-sm ring-offset-background focus-within:ring-2 focus-within:ring-ring focus-within:ring-offset-2",
            {
              "px-3 py-2": selected.length !== 0,
              "cursor-text": !disabled && selected.length !== 0,
            },
            className
          )}
          onClick={handleBadgeGroupClick}
        >
          <div className="relative flex flex-wrap gap-1">
            {selected.map((option) => {
              return (
                <OptionBadge
                  key={option.value}
                  option={option}
                  onUnselect={handleUnselect}
                  badgeClassName={badgeClassName}
                  disabled={disabled}
                />
              );
            })}
            {/* Avoid having the "Search" Icon */}
            <CommandPrimitive.Input
              {...inputProps}
              ref={inputRef}
              value={inputValue}
              disabled={disabled}
              onValueChange={handlePrimitiveInputValueChange}
              onBlur={handlePrimitiveInputBlur}
              onFocus={handlePrimitiveInputFocus}
              placeholder={
                hidePlaceholderWhenSelected && selected.length !== 0
                  ? ""
                  : placeholder
              }
              className={cn(
                "flex-1 bg-transparent outline-none placeholder:text-muted-foreground",
                {
                  "w-full": hidePlaceholderWhenSelected,
                  "px-3 py-2": selected.length === 0,
                  "ml-1": selected.length !== 0,
                },
                inputProps?.className
              )}
            />
            <button
              type="button"
              onClick={handleClearAllButtonClick}
              className={cn(
                "absolute right-0 h-6 w-6 p-0",
                (hideClearAllButton ||
                  disabled ||
                  selected.length < 1 ||
                  selected.filter((s) => s.fixed).length === selected.length) &&
                  "hidden"
              )}
            >
              <X />
            </button>
          </div>
        </div>
        <div className="relative">
          {open && (
            <CommandList
              className="absolute top-1 z-10 w-full rounded-md border bg-popover text-popover-foreground shadow-md outline-none animate-in"
              onMouseLeave={handleCommandListMouseLeave}
              onMouseEnter={handleCommandListMouseEnter}
              onMouseUp={handleCommandListMouseUp}
            >
              {isLoading ? (
                <>{loadingIndicator}</>
              ) : (
                <>
                  {EmptyItem()}
                  {CreatableItem()}
                  {!selectFirstItem && (
                    <CommandItem value="-" className="hidden" />
                  )}
                  {Object.entries(selectables).map(([key, dropdowns]) => (
                    <CommandGroup
                      key={key}
                      heading={key}
                      className="h-full overflow-auto"
                    >
                      <>
                        {dropdowns.map((option) => {
                          return (
                            <CommandGroupItem
                              key={option.value}
                              option={option}
                              onSelect={handleCommandGroupItemSelect}
                              onMouseDown={handleCommandGroupItemMouseDown}
                            />
                          );
                        })}
                      </>
                    </CommandGroup>
                  ))}
                </>
              )}
            </CommandList>
          )}
        </div>
      </Command>
    );
  }
);

MultipleSelector.displayName = "MultipleSelector";
export default MultipleSelector;

const OptionBadge = ({
  option,
  onUnselect,
  badgeClassName,
  disabled,
}: {
  option: Option;
  onUnselect: (option: Option) => void;
  badgeClassName?: string;
  disabled?: boolean;
}) => {
  const handleUnselect = useCallback(() => {
    onUnselect(option);
  }, [onUnselect, option]);

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLButtonElement>) => {
      if (e.key === "Enter") {
        onUnselect(option);
      }
    },
    [onUnselect, option]
  );

  const handleMouseDown = useCallback(
    (e: React.MouseEvent<HTMLButtonElement>) => {
      e.preventDefault();
      e.stopPropagation();
    },
    []
  );

  return (
    <Badge
      key={option.value}
      className={cn(
        "data-[disabled]:bg-muted-foreground data-[disabled]:text-muted data-[disabled]:hover:bg-muted-foreground",
        "data-[fixed]:bg-muted-foreground data-[fixed]:text-muted data-[fixed]:hover:bg-muted-foreground",
        badgeClassName
      )}
      data-fixed={option.fixed}
      data-disabled={disabled || undefined}
    >
      {option.label}
      <button
        className={cn(
          "ml-1 rounded-full outline-none ring-offset-background focus:ring-2 focus:ring-ring focus:ring-offset-2",
          (disabled || option.fixed) && "hidden"
        )}
        onKeyDown={handleKeyDown}
        onMouseDown={handleMouseDown}
        onClick={handleUnselect}
      >
        <X className="h-3 w-3 text-muted-foreground hover:text-foreground" />
      </button>
    </Badge>
  );
};

const CommandGroupItem = ({
  option,
  onSelect,
  onMouseDown,
}: {
  option: Option;
  onSelect: (option: Option) => void;
  onMouseDown: (e: React.MouseEvent<HTMLDivElement>) => void;
}) => {
  const handleSelect = useCallback(() => {
    onSelect(option);
  }, [onSelect, option]);

  return (
    <CommandItem
      key={option.value}
      value={option.value}
      disabled={option.disable}
      onMouseDown={onMouseDown}
      onSelect={handleSelect}
      className={cn(
        "cursor-pointer",
        option.disable && "cursor-default text-muted-foreground"
      )}
    >
      {option.label}
    </CommandItem>
  );
};

import React, { useMemo, useState } from "react";
import { Plus, Trash, Copy } from "lucide-react";
import { buttonVariants } from "@/components/ui/button";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";
import { AddComponentsPopover } from "@/components/ui/ui-builder/internal/components/add-component-popover";
import { cn } from "@/lib/utils";
import { hasAnyChildrenField, hasChildrenFieldOfTypeString } from "@/lib/ui-builder/store/schema-utils";
import { useGlobalLayerActions } from "@/lib/ui-builder/hooks/use-layer-actions";

interface MenuProps {
  layerId: string;
}

export const LayerMenu: React.FC<MenuProps> = ({ layerId }) => {
  const selectedLayer = useLayerStore((state) => state.findLayerById(layerId));
  const isLayerAPage = useLayerStore((state) => state.isLayerAPage(layerId));
  const [popoverOpen, setPopoverOpen] = useState(false);
  const componentRegistry = useEditorStore((state) => state.registry);
  const allowPagesCreation = useEditorStore(
    (state) => state.allowPagesCreation
  );
  const allowPagesDeletion = useEditorStore(
    (state) => state.allowPagesDeletion
  );

  // Use global layer actions
  const { handleDuplicate, handleDelete } = useGlobalLayerActions(layerId);

  // Check permissions for page operations
  const canDuplicate = !isLayerAPage || allowPagesCreation;
  const canDelete = !isLayerAPage || allowPagesDeletion;

  const buttonVariantsValues = useMemo(() => {
    return buttonVariants({ variant: "ghost", size: "sm" });
  }, []);

  const buttonClass = "p-2 h-6 w-6 cursor-pointer [&_svg]:size-3 bg-blue-500 rounded-lg text-white hover:bg-white hover:text-black";

  const canRenderAddChild = useMemo(() => {
    if (!selectedLayer) return false;

    const componentDef =
      componentRegistry[selectedLayer.type as keyof typeof componentRegistry];
    if (!componentDef) return false;

    // Safely check if schema has shape property (ZodObject) and children field
    const canAddChildren =
      "shape" in componentDef.schema &&
      hasAnyChildrenField(componentDef.schema) && !hasChildrenFieldOfTypeString(componentDef.schema);

    return canAddChildren;
  }, [selectedLayer, componentRegistry]);

  return (
    <>
      <div
        className={cn(
          "flex flex-nowrap overflow-hidden transition-all duration-200 ease-in-out rounded-lg bg-blue-500 text-white",
        )}
      >
        {canRenderAddChild && (
          <AddComponentsPopover
            parentLayerId={layerId}
            className="flex-shrink w-min inline-flex"
            onOpenChange={setPopoverOpen}
          >
            <div className={cn(buttonVariantsValues, buttonClass, popoverOpen ? "bg-white text-black" : "")}>
              <span className="sr-only">Add Component</span>
              <Plus className="size-3" />
            </div>
          </AddComponentsPopover>
        )}
        {canDuplicate && (
          <div
            className={cn(buttonVariantsValues, buttonClass)}
            onClick={handleDuplicate}
            data-testid="duplicate-button"
          >
            <span className="sr-only">
              Duplicate {isLayerAPage ? "Page" : "Component"}
            </span>
            <Copy className="size-3" data-testid="duplicate-icon" />
          </div>
        )}
        {canDelete && (
          <div
            className={cn(buttonVariantsValues, buttonClass)}
            onClick={handleDelete}
            data-testid="delete-button"
          >
            <span className="sr-only">
              Delete {isLayerAPage ? "Page" : "Component"}
            </span>
            <Trash className="size-3" data-testid="delete-icon" />
          </div>
        )}
      </div>
    </>
  );
};

"use client";

import React, { useMemo } from "react";
import { Plus, Trash, Copy, Scissors, ClipboardPaste, CopyPlus } from "lucide-react";
import {
  ContextMenuItem,
  ContextMenuSeparator,
  ContextMenuShortcut,
} from "@/components/ui/context-menu";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";
import { hasAnyChildrenField, hasChildrenFieldOfTypeString } from "@/lib/ui-builder/store/schema-utils";
import { SHORTCUTS } from "@/lib/ui-builder/shortcuts/shortcut-registry";
import { useGlobalLayerActions } from "@/lib/ui-builder/hooks/use-layer-actions";
import { AddComponentsPopover } from "@/components/ui/ui-builder/internal/components/add-component-popover";

export interface LayerMenuItemsProps {
  layerId: string;
  /** Callback when an action is performed - useful for closing menus */
  onAction?: () => void;
  /** Whether to show the "Add Child" option */
  showAddChild?: boolean;
}

/**
 * Shared menu items for layer context menu.
 * Renders menu items with shortcuts for copy, cut, paste, duplicate, and delete actions.
 */
export const LayerMenuItems: React.FC<LayerMenuItemsProps> = ({
  layerId,
  onAction,
  showAddChild = true,
}) => {
  const selectedLayer = useLayerStore((state) => state.findLayerById(layerId));
  const isLayerAPage = useLayerStore((state) => state.isLayerAPage(layerId));
  const componentRegistry = useEditorStore((state) => state.registry);
  const allowPagesCreation = useEditorStore((state) => state.allowPagesCreation);
  const allowPagesDeletion = useEditorStore((state) => state.allowPagesDeletion);

  const {
    canPaste,
    handleCopy,
    handleCut,
    handlePaste,
    handleDelete,
    handleDuplicate,
  } = useGlobalLayerActions(layerId);

  // Check permissions for page operations
  const canDuplicate = !isLayerAPage || allowPagesCreation;
  const canDelete = !isLayerAPage || allowPagesDeletion;
  const canCut = canDelete; // Cut is only possible if we can delete

  // Check if component can have children added
  const canRenderAddChild = useMemo(() => {
    if (!showAddChild) return false;
    if (!selectedLayer) return false;

    const componentDef = componentRegistry[selectedLayer.type as keyof typeof componentRegistry];
    if (!componentDef) return false;

    // Safely check if schema has shape property (ZodObject) and children field
    const canAddChildren =
      "shape" in componentDef.schema &&
      hasAnyChildrenField(componentDef.schema) &&
      !hasChildrenFieldOfTypeString(componentDef.schema);

    return canAddChildren;
  }, [showAddChild, selectedLayer, componentRegistry]);

  const handleCopyClick = () => {
    handleCopy();
    onAction?.();
  };

  const handleCutClick = () => {
    handleCut();
    onAction?.();
  };

  const handlePasteClick = () => {
    handlePaste();
    onAction?.();
  };

  const handleDuplicateClick = () => {
    handleDuplicate();
    onAction?.();
  };

  const handleDeleteClick = () => {
    handleDelete();
    onAction?.();
  };

  return (
    <>
      {canRenderAddChild && (
        <>
          <AddComponentsPopover parentLayerId={layerId}>
            <ContextMenuItem
              onSelect={(e) => {
                // Prevent the menu from closing so the popover can open
                e.preventDefault();
              }}
            >
              <Plus className="mr-2 h-4 w-4" />
              Add Child
            </ContextMenuItem>
          </AddComponentsPopover>
          <ContextMenuSeparator />
        </>
      )}

      <ContextMenuItem onSelect={handleCopyClick}>
        <Copy className="mr-2 h-4 w-4" />
        Copy
        <ContextMenuShortcut>{SHORTCUTS.copy.shortcutDisplay}</ContextMenuShortcut>
      </ContextMenuItem>

      {canCut && (
        <ContextMenuItem onSelect={handleCutClick}>
          <Scissors className="mr-2 h-4 w-4" />
          Cut
          <ContextMenuShortcut>{SHORTCUTS.cut.shortcutDisplay}</ContextMenuShortcut>
        </ContextMenuItem>
      )}

      <ContextMenuItem onSelect={handlePasteClick} disabled={!canPaste}>
        <ClipboardPaste className="mr-2 h-4 w-4" />
        Paste
        <ContextMenuShortcut>{SHORTCUTS.paste.shortcutDisplay}</ContextMenuShortcut>
      </ContextMenuItem>

      <ContextMenuSeparator />

      {canDuplicate && (
        <ContextMenuItem onSelect={handleDuplicateClick}>
          <CopyPlus className="mr-2 h-4 w-4" />
          Duplicate
          <ContextMenuShortcut>{SHORTCUTS.duplicate.shortcutDisplay}</ContextMenuShortcut>
        </ContextMenuItem>
      )}

      {canDelete && (
        <ContextMenuItem
          onSelect={handleDeleteClick}
          className="text-destructive focus:text-destructive"
        >
          <Trash className="mr-2 h-4 w-4" />
          Delete
          <ContextMenuShortcut>{SHORTCUTS.delete.shortcutDisplay}</ContextMenuShortcut>
        </ContextMenuItem>
      )}
    </>
  );
};

export default LayerMenuItems;

"use client";

import React, { useCallback, useMemo } from "react";
import {
  ContextMenu,
  ContextMenuContent,
  ContextMenuTrigger,
} from "@/components/ui/context-menu";
import { LayerMenuItems } from "@/components/ui/ui-builder/internal/components/layer-menu-items";
import { useGlobalLayerActions } from "@/lib/ui-builder/hooks/use-layer-actions";
import {
  type KeyCombination,
  useKeyboardShortcuts,
} from "@/hooks/use-keyboard-shortcuts";
import { SHORTCUTS } from "@/lib/ui-builder/shortcuts/shortcut-registry";

export interface LayerContextMenuProps {
  layerId: string;
  /** Whether the layer is currently selected */
  isSelected: boolean;
  /** Callback to select the layer */
  onSelect: (layerId: string) => void;
  /** Children to wrap with context menu */
  children: React.ReactNode;
  /** Additional class name for the trigger */
  className?: string;
  /** Style for the trigger */
  style?: React.CSSProperties;
}

/**
 * Context menu wrapper for layers.
 * Wraps children with a right-click context menu that provides layer actions.
 * If the layer is not selected, right-clicking will select it first.
 */
export const LayerContextMenu: React.FC<LayerContextMenuProps> = ({
  layerId,
  isSelected,
  onSelect,
  children,
  className,
  style,
}) => {
  const {
    handleCopy,
    handleCut,
    handlePaste,
    handleDelete,
    handleDuplicate,
    canPaste,
  } = useGlobalLayerActions(layerId);

  // Handle context menu open - select the layer if not selected
  const handleContextMenuOpen = useCallback(
    (open: boolean) => {
      if (open && !isSelected) {
        onSelect(layerId);
      }
    },
    [isSelected, layerId, onSelect]
  );

  // Check if the active element is an input or textarea
  const isInputFocused = useCallback(() => {
    const activeElement = document.activeElement;
    if (!activeElement) return false;

    const tagName = activeElement.tagName.toLowerCase();
    const isContentEditable = activeElement.getAttribute('contenteditable') === 'true';

    return tagName === 'input' || tagName === 'textarea' || isContentEditable;
  }, []);

  // Register keyboard shortcuts only when this layer is selected
  const keyCombinations = useMemo<KeyCombination[]>(() => {
    if (!isSelected) return [];

    return [
      {
        keys: SHORTCUTS.copy.keys,
        key: SHORTCUTS.copy.key,
        handler: (e) => {
          if (isInputFocused()) return;
          e.preventDefault();
          handleCopy();
        },
      },
      {
        keys: SHORTCUTS.cut.keys,
        key: SHORTCUTS.cut.key,
        handler: (e) => {
          if (isInputFocused()) return;
          e.preventDefault();
          handleCut();
        },
      },
      {
        keys: SHORTCUTS.paste.keys,
        key: SHORTCUTS.paste.key,
        handler: (e) => {
          if (isInputFocused()) return;
          if (!canPaste) return;
          e.preventDefault();
          handlePaste();
        },
      },
      {
        keys: SHORTCUTS.duplicate.keys,
        key: SHORTCUTS.duplicate.key,
        handler: (e) => {
          if (isInputFocused()) return;
          e.preventDefault();
          handleDuplicate();
        },
      },
      {
        keys: SHORTCUTS.delete.keys,
        key: SHORTCUTS.delete.key,
        handler: (e) => {
          if (isInputFocused()) return;
          e.preventDefault();
          handleDelete();
        },
      },
    ];
  }, [isSelected, canPaste, handleCopy, handleCut, handlePaste, handleDuplicate, handleDelete, isInputFocused]);

  useKeyboardShortcuts(keyCombinations);

  return (
    <ContextMenu onOpenChange={handleContextMenuOpen}>
      <ContextMenuTrigger
        className={className}
        style={style}
        asChild
      >
        {children}
      </ContextMenuTrigger>
      <ContextMenuContent className="w-56">
        <LayerMenuItems layerId={layerId} />
      </ContextMenuContent>
    </ContextMenu>
  );
};

export default LayerContextMenu;

import React from "react";
import type { FallbackProps } from "react-error-boundary";

export function ErrorFallback({ error }: FallbackProps) {
    // Call resetErrorBoundary() to reset the error boundary and retry the render.

    // Handle error as unknown type (react-error-boundary v5+)
    // Check for null/undefined first, then instanceof Error, then convert to string
    const errorMessage = error == null 
      ? undefined 
      : error instanceof Error 
        ? error.message 
        : String(error);
    const errorStack = error instanceof Error ? error.stack : undefined;

    return (
      <div className="p-4 border border-red-500 bg-red-100 text-red-700 rounded flex-grow w-full">
        <h3 className="font-bold mb-2">Component Error</h3>
        <p>Error: {errorMessage || "Unknown error"}</p>
        <details className="mt-2">
          <summary className="cursor-pointer">Stack trace</summary>
          <pre className="mt-2 text-xs whitespace-pre-wrap">{errorStack}</pre>
        </details>
      </div>
    );
  }

"use client";
import React, {
  useCallback,
  useEffect,
  useRef,
  useState,
  useLayoutEffect,
  useMemo,
  useContext,
} from "react";
import { useTransformEffect } from "react-zoom-pan-pinch";
import type { ComponentLayer } from "@/components/ui/ui-builder/types";
import { LayerMenu } from "@/components/ui/ui-builder/internal/components/layer-menu";
import { DragHandle as ComponentDragHandle } from "@/components/ui/ui-builder/internal/dnd/drag-handle";
import { DragHandleContext } from "@/components/ui/ui-builder/internal/canvas/resizable-wrapper";
import { useDndContext } from "@/lib/ui-builder/context/dnd-context";
import { cn } from "@/lib/utils";
import {
  offset,
  useFloating,
  autoUpdate,
  shift,
  limitShift,
} from "@floating-ui/react";
import { getScrollParent } from "@/lib/ui-builder/utils/get-scroll-parent";
import { useFrame } from "@/components/ui/ui-builder/internal/canvas/auto-frame";
import { LayerContextMenu } from "@/components/ui/ui-builder/internal/components/layer-context-menu";

const style: React.CSSProperties = {
  display: "contents",
};

interface ElementSelectorProps {
  layer: ComponentLayer;
  isSelected: boolean;
  zIndex: number;
  totalLayers: number;
  onSelectElement: (layerId: string) => void;
  children: React.ReactNode;
  isPageLayer?: boolean;
}

/**
 * Element selector that measures elements using offset properties (relative to parent)
 * instead of getBoundingClientRect (relative to viewport). This works correctly with
 * zoom/pan transformations since the overlays are rendered inside the canvas.
 */
export const ElementSelector: React.FC<ElementSelectorProps> = ({
  layer,
  isSelected,
  zIndex,
  onSelectElement,
  children,
  isPageLayer = false,
}) => {
  const [boundingRect, setBoundingRect] = useState<{
    top: number;
    left: number;
    bottom: number;
    right: number;
    width: number;
    height: number;
  } | null>(null);

  const dndContext = useDndContext();

  const { refs, floatingStyles } = useFloating({
    placement: "top-start",
    whileElementsMounted: autoUpdate,
    middleware: [
      offset(-2), // Negative offset to overlap like the sticky label
      shift({
        crossAxis: true, // Allow sliding along the cross axis
        limiter: limitShift(),
      }),
    ],
  });

  // Check if this specific layer is being dragged
  const isBeingDragged = dndContext.activeLayerId === layer.id;

  const handleClick = useCallback(
    (e: React.MouseEvent) => {
      e.stopPropagation();
      e.preventDefault();
      onSelectElement(layer.id);
    },
    [layer.id, onSelectElement]
  );

  // Block all pointer events from passing through
  const handlePointerEvent = useCallback((e: React.MouseEvent) => {
    e.stopPropagation();
    e.preventDefault();
  }, []);


  const overlayStyle = useMemo(() => {
    if (!boundingRect) return { display: "none" };
    return {
      position: "fixed" as const,
      top: boundingRect.top,
      left: boundingRect.left,
      width: boundingRect.width,
      height: boundingRect.height,
      zIndex: zIndex,
      boxSizing: "border-box" as const,
      pointerEvents: "auto" as const, // Ensure overlay captures all pointer events
    } as React.CSSProperties;
  }, [boundingRect, zIndex]);

  const handleBoundingRectChange = useCallback(
    (
      rect: {
        top: number;
        left: number;
        bottom: number;
        right: number;
        width: number;
        height: number;
      } | null
    ) => {
      setBoundingRect(rect);
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [layer.id]
  );

  return (
    <>
      <MeasureRange debug={false} onChange={handleBoundingRectChange}>
        {children}
      </MeasureRange>

      {boundingRect && (
        <LayerContextMenu
          layerId={layer.id}
          isSelected={isSelected}
          onSelect={onSelectElement}
        >
          <div
            onClick={handleClick}
            onMouseDown={handlePointerEvent}
            onMouseUp={handlePointerEvent}
            onDoubleClick={handlePointerEvent}
            ref={refs.setReference}
            className={cn(
              "fixed box-border hover:border-blue-300 hover:border-2 hover:bg-blue-300/20 hover:shadow-md hover:shadow-blue-500/20 cursor-default",
              isBeingDragged
                ? "border-2 border-green-500 border-dashed shadow-lg opacity-70 bg-orange-50/20"
                : isSelected
                ? "border-2 border-blue-500 hover:border-blue-500 hover:bg-transparent  hover:shadow-none"
                : ""
            )}
            style={overlayStyle}
          ></div>
        </LayerContextMenu>
      )}

      {/* Floating UI element - positioned outside the overlay */}
      {isSelected && (
        <div className="z-[9999]" ref={refs.setFloating} style={floatingStyles}>
          <div className="flex items-center bg-blue-500">
            {/* Drag handle for non-page layers */}
            {!isPageLayer && isSelected && (
              <ComponentDragHandle layerId={layer.id} layerType={layer.type} />
            )}

            {!isBeingDragged && (
              <>
                <span className="text-xs text-white px-[1px] whitespace-nowrap cursor-default">
                  {layer.name
                    ?.toLowerCase()
                    .startsWith(layer.type.toLowerCase())
                    ? layer.type.replaceAll("_", "")
                    : `${layer.name} (${layer.type.replaceAll("_", "")})`}
                </span>
                <div className="w-px h-4 bg-white/20 ml-1" />
                <LayerMenu layerId={layer.id} />
              </>
            )}
          </div>
        </div>
      )}
    </>
  );
};

export interface MeasureRangeProps {
  /** Called after every size/position change. */
  onChange?: (
    rect: DOMRectReadOnly & { bottom: number; right: number }
  ) => void;
  /** Children you want to watch (anything renderable). */
  children: React.ReactNode;
  debug?: boolean;
}

const MIN_SIZE = 5;

const MIN_CHANGE_THRESHOLD = 0;

export const MeasureRange: React.FC<MeasureRangeProps> = ({
  onChange,
  children,
  debug = false,
}) => {
  // A wrapper that DOESN'T affect layout thanks to display: contents
  const wrapperRef = useRef<HTMLSpanElement>(null);
  const [rect, setRect] = useState<DOMRectReadOnly | null>(null);

  // Performance optimizations
  const rafIdRef = useRef<number | null>(null);
  const lastMeasurementRef = useRef<DOMRectReadOnly | null>(null);
  const elementsRef = useRef<Element[]>([]);
  const transformUpdatePendingRef = useRef(false);

  // Get dragging context to respond to ResizableWrapper changes
  const { dragging } = useContext(DragHandleContext);

  // Get iframe context if we're running inside an AutoFrame
  const frameContext = useFrame();

  // Parent container observer to track ResizableWrapper resizing
  const parentObserverRef = useRef<ResizeObserver | null>(null);

  // Throttled measurement function using RAF
  const measureElements = useCallback(() => {
    if (rafIdRef.current) {
      cancelAnimationFrame(rafIdRef.current);
    }

    rafIdRef.current = requestAnimationFrame(() => {
      const wrapper = wrapperRef.current;
      if (!wrapper) {
        console.warn(`[MeasureRange] No wrapper found`);
        return;
      }

      const elements = elementsRef.current;
      if (elements.length === 0) {
        return;
      }

      try {
        // Use getBoundingClientRect for universal compatibility (works with SVGs, HTML elements, etc.)
        const elementRects = elements.map((el) => el.getBoundingClientRect());
        if (elementRects.length === 0) return;

        // Find the bounds of all elements
        const minTop = Math.min(...elementRects.map((rect) => rect.top));
        const minLeft = Math.min(...elementRects.map((rect) => rect.left));
        const maxBottom = Math.max(...elementRects.map((rect) => rect.bottom));
        const maxRight = Math.max(...elementRects.map((rect) => rect.right));

        // Use viewport coordinates directly for fixed positioning
        const newRect = {
          top: minTop,
          left: minLeft,
          width: maxRight - minLeft,
          height: maxBottom - minTop,
          bottom: maxBottom,
          right: maxRight,
        } as DOMRectReadOnly & { bottom: number; right: number };

        // Only update if measurements actually changed (avoid unnecessary re-renders)
        const hasChanged =
          !lastMeasurementRef.current ||
          Math.abs(lastMeasurementRef.current.top - newRect.top) >
            MIN_CHANGE_THRESHOLD ||
          Math.abs(lastMeasurementRef.current.left - newRect.left) >
            MIN_CHANGE_THRESHOLD ||
          Math.abs(lastMeasurementRef.current.width - newRect.width) >
            MIN_CHANGE_THRESHOLD ||
          Math.abs(lastMeasurementRef.current.height - newRect.height) >
            MIN_CHANGE_THRESHOLD;

        if (hasChanged) {
          lastMeasurementRef.current = newRect;
          setRect(newRect);
          if (onChange) {
            const adjustedWidth = Math.max(newRect.width, MIN_SIZE);
            const adjustedHeight = Math.max(newRect.height, MIN_SIZE);
            onChange({
              ...newRect,
              width: adjustedWidth,
              height: adjustedHeight,
              bottom: newRect.top + adjustedHeight,
              right: newRect.left + adjustedWidth,
            });
          }
        }

        transformUpdatePendingRef.current = false;
      } catch (error) {
        console.warn("Error measuring elements:", error);
      }
    });
  }, [onChange]);

  // Handle transform updates
  useTransformEffect(
    useCallback(() => {
      // Avoid duplicate updates if one is already pending
      if (transformUpdatePendingRef.current) return;

      transformUpdatePendingRef.current = true;
      measureElements();
    }, [measureElements])
  );

  // Re-measure when dragging state changes (for ResizableWrapper)
  useEffect(() => {
    measureElements();
  }, [dragging, measureElements]);

  // Cache elements and set up ResizeObservers (without transform state dependency)
  useLayoutEffect(() => {
    const wrapper = wrapperRef.current;
    if (!wrapper) return;

    // Cache all element nodes (HTML, SVG, etc.) and filter out text/comment nodes
    // Also filter out elements that are not actually rendered (like script, style, etc.)
    const elements = Array.from(wrapper.childNodes).filter(
      (node): node is Element => {
        if (node.nodeType !== Node.ELEMENT_NODE) return false;

        // Check if element is actually rendered (has layout)
        const element = node as Element;
        const rect = element.getBoundingClientRect();
        return rect.width > 0 || rect.height > 0;
      }
    );

    elementsRef.current = elements;

    if (elements.length === 0) return;

    // Set up ResizeObservers only once (not on every transform change)
    const observers = elements.map((el) => {
      const ro = new ResizeObserver(measureElements);
      ro.observe(el);
      return ro;
    });

    // Set up parent container ResizeObserver to track ResizableWrapper changes
    const parentContainer =
      wrapper.closest("#editor-panel-container") ||
      wrapper.closest('[data-testid="transform-component"]') ||
      wrapper.closest(".relative");
    if (parentContainer && parentContainer instanceof HTMLElement) {
      parentObserverRef.current = new ResizeObserver(measureElements);
      parentObserverRef.current.observe(parentContainer);
    }

    // Set up scroll listeners for various containers
    const scrollListeners: Array<() => void> = [];

    // Determine which document/window to use - iframe context if available, otherwise parent
    const targetDocument = frameContext.document || document;
    const targetWindow = frameContext.window || window;

    // Listen to multiple potential scroll containers
    const potentialScrollContainers = [
      // Editor panel container (in parent document)
      document.getElementById("editor-panel-container"),
      // Transform component (zoom/pan container)
      wrapper.closest('[data-testid="transform-component"]'),
      // Any iframe that might contain our content
      document.querySelector("iframe"),
      // Canvas containers
      wrapper.closest(".relative"),
      wrapper.closest("[data-zoom-pan-pinch]"),
      // Traditional scroll parent
      elements.length > 0 ? getScrollParent(elements[0] as HTMLElement) : null,
      // Iframe-specific containers if we're in an iframe
      ...(frameContext.document
        ? [
            frameContext.document.getElementById("frame-root"),
            frameContext.document.body,
          ]
        : []),
    ].filter(Boolean) as HTMLElement[];

    // Add scroll listeners to all potential containers
    potentialScrollContainers.forEach((container) => {
      if (
        container &&
        container !== document.body &&
        container !== targetDocument.body
      ) {
        const scrollHandler = () => {
          measureElements();
        };
        container.addEventListener("scroll", scrollHandler, { passive: true });
        scrollListeners.push(() =>
          container.removeEventListener("scroll", scrollHandler)
        );
      }
    });

    // Listen to both parent window and iframe window events
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    const setupWindowListeners = (win: Window, _: string) => {
      const windowScrollHandler = () => {
        measureElements();
      };
      const windowResizeHandler = () => {
        measureElements();
      };

      win.addEventListener("scroll", windowScrollHandler, { passive: true });
      win.addEventListener("resize", windowResizeHandler, { passive: true });

      scrollListeners.push(() =>
        win.removeEventListener("scroll", windowScrollHandler)
      );
      scrollListeners.push(() =>
        win.removeEventListener("resize", windowResizeHandler)
      );
    };

    // Always listen to parent window
    setupWindowListeners(window, "Parent");

    // Also listen to iframe window if available and different from parent
    if (frameContext.window && frameContext.window !== window) {
      setupWindowListeners(frameContext.window, "Iframe");
    }

    // Add mutation observer for style/class changes that might affect positioning
    let mutationObserver: MutationObserver | null = null;
    if ("MutationObserver" in targetWindow) {
      mutationObserver = new MutationObserver(measureElements);
      // Watch for transform/style changes on containers in the appropriate document context
      const transformContainer =
        wrapper.closest('[data-testid="transform-component"]') ||
        wrapper.closest("[data-zoom-pan-pinch]") ||
        targetDocument.body;
      mutationObserver.observe(transformContainer, {
        attributeFilter: ["style", "class", "transform"],
        attributes: true,
        subtree: true,
      });
    }

    // Initial measurement
    measureElements();

    return () => {
      observers.forEach((ro) => ro.disconnect());
      if (parentObserverRef.current) {
        parentObserverRef.current.disconnect();
        parentObserverRef.current = null;
      }

      // Clean up all scroll listeners
      scrollListeners.forEach((cleanup) => cleanup());

      // Clean up mutation observer
      if (mutationObserver) {
        mutationObserver.disconnect();
      }

      if (rafIdRef.current) {
        cancelAnimationFrame(rafIdRef.current);
        rafIdRef.current = null;
      }
    };
  }, [children, measureElements, frameContext.document, frameContext.window]); // Include iframe context

  // Cleanup RAF on unmount
  useEffect(() => {
    return () => {
      if (rafIdRef.current) {
        cancelAnimationFrame(rafIdRef.current);
      }
    };
  }, []);

  const memoizedStyle: React.CSSProperties = useMemo(() => {
    if (!rect) return {};
    return {
      position: "fixed",
      top: rect.top,
      left: rect.left,
      width: Math.max(rect.width, MIN_SIZE),
      height: Math.max(rect.height, MIN_SIZE),
      border: "1px solid #5be312",
      zIndex: 9999,
      boxSizing: "border-box",
      pointerEvents: "none",
      cursor: "default",
    };
  }, [rect]);

  return (
    <>
      <span ref={wrapperRef} style={style}>
        {children}
      </span>

      {/* Debug overlay that scales naturally with the transform */}
      {rect && debug && <div style={memoizedStyle} />}
    </>
  );
};

