GroupedComponentItem.displayName = "GroupedComponentItem";

const LazyComponentPreview = memo(({
  componentType,
  componentRegistry,
}: {
  componentType: string;
  componentRegistry: ComponentRegistry;
}) => {
  const [shouldLoad, setShouldLoad] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const element = ref.current;
    if (!element) return;

    let timeoutId: ReturnType<typeof setTimeout> | null = null;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry?.isIntersecting) {
          // Delay loading slightly to improve tab switching performance
          timeoutId = setTimeout(() => setShouldLoad(true), 50);
        }
      },
      { threshold: 0.1 }
    );

    observer.observe(element);
    return () => {
      observer.disconnect();
      if (timeoutId !== null) {
        clearTimeout(timeoutId);
      }
    };
  }, []);

  return (
    <div ref={ref} className="w-full h-full">
      {shouldLoad ? (
        <Suspense fallback={fallback}>
          <ComponentPreview 
            componentType={componentType}
            componentRegistry={componentRegistry}
          />
        </Suspense>
      ) : (
        <div className="w-full h-full bg-muted rounded border" />
      )}
    </div>
  );
});

LazyComponentPreview.displayName = "LazyComponentPreview";

/* istanbul ignore next */

/**
 * Placeholder for child-only components that can't render without parent context
 */
const ChildOnlyPlaceholder = memo(({ componentType }: { componentType: string }) => {
  // Get first letter or first two letters for better recognition
  const initials = componentType
    .replace(/([A-Z])/g, ' $1')
    .trim()
    .split(' ')
    .map(word => word[0])
    .slice(0, 2)
    .join('');

  return (
    <div className="w-full h-full bg-muted/50 rounded border border-dashed flex items-center justify-center">
      <span className="text-[8px] font-medium text-muted-foreground">{initials}</span>
    </div>
  );
});

ChildOnlyPlaceholder.displayName = "ChildOnlyPlaceholder";

const ComponentPreview = memo(({
  componentType,
  componentRegistry,
}: {
  componentType: string;
  componentRegistry: ComponentRegistry;
}) => {
  const isChildOnly = isChildOnlyComponent(componentRegistry, componentType);

  // useMemo must be called unconditionally to follow React's Rules of Hooks
  const previewLayer = useMemo(() => {
    // Skip creating layer for child-only components
    if (isChildOnly) {
      return null;
    }

    // Check cache first
    const cacheKey = `${componentType}-${JSON.stringify(componentRegistry[componentType as keyof typeof componentRegistry]?.schema)}`;
    if (previewLayerCache.has(cacheKey)) {
      return previewLayerCache.get(cacheKey)!;
    }

    try {
      // Use the utility function to create the preview layer
      const layer = createComponentLayer(componentType, componentRegistry, {
        id: `preview-${componentType}`, // Use stable ID for previews
      });

      // Cache the layer
      previewLayerCache.set(cacheKey, layer);
      return layer;
    } catch (error) {
      console.warn(`Failed to create preview for component ${componentType}:`, error);
      return null;
    }
  }, [componentType, componentRegistry, isChildOnly]);

  // Render child-only placeholder after hooks are called
  if (isChildOnly) {
    return <ChildOnlyPlaceholder componentType={componentType} />;
  }

  if (!previewLayer) {
    return <div className="w-full h-full bg-muted rounded border" />;
  }

  const style = { width: '200%', height: '200%' } as const;

  return (
    <div 
      className="w-full h-full bg-background rounded border overflow-hidden transform scale-50 origin-top-left pointer-events-none" 
      style={style}
      // inert prevents all interactions and stops internal components (like poppers)
      // from attaching scroll listeners that interfere with parent scroll
      inert={true}
    >
      <LayerRenderer
        page={previewLayer}
        componentRegistry={componentRegistry}
        className="pointer-events-none"
      />
    </div>
  );
});

ComponentPreview.displayName = "ComponentPreview";

// @components/ui/ui-builder becomes Components UI Builder
function formatCategoryName(name: string) {
  const words = name.split("/");
  const lastWord = words[words.length - 1];
  return lastWord?.replace(/-/g, " ").replace(/\b\w/g, char => char.toUpperCase()) ?? "";
}

import React, {
    useCallback,
    useMemo,
    useState,
    createContext,
    useLayoutEffect,
    useRef,
  } from "react";
import { cn } from "@/lib/utils";
import { GripVertical } from "lucide-react";
import { useDrag } from "@use-gesture/react";
import type { DragConfig } from "@use-gesture/react";

const RESPONSIVE_DEFAULT_SIZE = 800;

  // Resizable Wrapper Component for responsive mode
  interface ResizableWrapperProps {
    children: React.ReactNode;
    isResizable: boolean;
    onDraggingChange?: (dragging: boolean) => void;
    onSizeChange?: (width: number, height: number) => void;
  }

export  const ResizableWrapper: React.FC<ResizableWrapperProps> = ({
    children,
    isResizable,
    onDraggingChange,
    onSizeChange,
  }) => {
    const [dragging, setDragging] = useState(false);
    const [responsiveSize, setResponsiveSize] = useState<{
      width: number;
      height: number;
    } | null>(null);
    const initialSizeRef = useRef<{ width: number, height: number }>({ width: 0, height: 0 });

    // Set initial responsive size
    useLayoutEffect(() => {
      if (isResizable) {
        const initialWidth = RESPONSIVE_DEFAULT_SIZE; // Default responsive width
        const initialHeight = RESPONSIVE_DEFAULT_SIZE; // Default responsive height
        setResponsiveSize({ width: initialWidth, height: initialHeight });
        onSizeChange?.(initialWidth, initialHeight);
      }
    }, [isResizable, onSizeChange]);

      const handleSetDragging = useCallback(
    (value: boolean) => {
      setDragging(value);
      onDraggingChange?.(value);
    },
    [onDraggingChange]
  );

  const horizontalDragConfig = useMemo(() => {
    return {
      axis: "x",
      from: () => [0, 0],
      filterTaps: true,
    } as DragConfig;
  }, []);

  const verticalDragConfig = useMemo(() => {
    return {
      axis: "y",
      from: () => [0, 0],
      filterTaps: true,
    } as DragConfig;
  }, []);

  // Handle horizontal resizing
  const bindHorizontalResizer = useDrag(({ down, movement: [mx], first, last }) => {
    if (first) {
      // Capture the initial size when drag starts
      initialSizeRef.current = {
        width: responsiveSize?.width || RESPONSIVE_DEFAULT_SIZE,
        height: responsiveSize?.height || RESPONSIVE_DEFAULT_SIZE,
      };
      handleSetDragging(true);
    }

    if (down) {
      // Calculate new width based on initial size and movement
      const newWidth = Math.max(320, initialSizeRef.current.width + mx); // Min width of 320px
      const newHeight = initialSizeRef.current.height; // Keep height unchanged
      setResponsiveSize({ width: newWidth, height: newHeight });
      onSizeChange?.(newWidth, newHeight);
    }

    // Notify when drag ends for final measurement update
    if (last) {
      // Small delay to ensure DOM updates are complete
      setTimeout(() => {
        handleSetDragging(false);
      }, 0);
    }
  }, horizontalDragConfig as any);

  // Handle vertical resizing
  const bindVerticalResizer = useDrag(({ down, movement: [, my], first, last }) => {
    if (first) {
      // Capture the initial size when drag starts
      initialSizeRef.current = {
        width: responsiveSize?.width || RESPONSIVE_DEFAULT_SIZE,
        height: responsiveSize?.height || RESPONSIVE_DEFAULT_SIZE,
      };
      handleSetDragging(true);
    }

    if (down) {
      // Calculate new height based on initial size and movement
      const newWidth = initialSizeRef.current.width; // Keep width unchanged
      const newHeight = Math.max(200, initialSizeRef.current.height + my); // Min height of 200px
      setResponsiveSize({ width: newWidth, height: newHeight });
      onSizeChange?.(newWidth, newHeight);
    }

    // Notify when drag ends for final measurement update
    if (last) {
      // Small delay to ensure DOM updates are complete
      setTimeout(() => {
        handleSetDragging(false);
      }, 0);
    }
  }, verticalDragConfig as any);

  const bindHorizontalResizerValues = useMemo(() => {
    return typeof bindHorizontalResizer === "function" ? bindHorizontalResizer() : {};
  }, [bindHorizontalResizer]);

  const bindVerticalResizerValues = useMemo(() => {
    return typeof bindVerticalResizer === "function" ? bindVerticalResizer() : {};
  }, [bindVerticalResizer]);

  const responsiveStyle = useMemo(() => {
    if (isResizable && responsiveSize) {
      return { 
        width: `${responsiveSize.width}px`,
        height: `${responsiveSize.height}px`
      };
    }
    // When not resizable, ensure we inherit the parent's width constraint
    return { width: '100%' };
  }, [isResizable, responsiveSize]);

    const contextValue = useMemo(() => ({
      dragging,
      setDragging: handleSetDragging
    }), [dragging, handleSetDragging]);

    return (
      <DragHandleContext.Provider
        value={contextValue}
      >
        <div className="relative" style={responsiveStyle}>
          {isResizable && (
            <>
              {/* Horizontal resizer on the right middle */}
              <Resizer
                {...bindHorizontalResizerValues}
                className="absolute top-1/2 right-[-20px] -translate-y-1/2"
              >
                <GripVertical className="w-4 h-4" />
              </Resizer>
              {/* Vertical resizer at the bottom center */}
              <Resizer
                {...bindVerticalResizerValues}
                className="absolute bottom-[-20px] left-1/2 -translate-x-1/2 cursor-ns-resize"
              >
                <GripVertical className="w-4 h-4 rotate-90" />
              </Resizer>
            </>
          )}
          {children}
        </div>
      </DragHandleContext.Provider>
    );
  };

  // Context to track if a drag handle is active
export const DragHandleContext = createContext<{
    dragging: boolean;
    setDragging: (v: boolean) => void;
  }>({ dragging: false, setDragging: () => {} });

  // Resizer Handle Component
  const Resizer = ({
    className,
    children,
    ...props
  }: React.HTMLAttributes<HTMLDivElement>) => {
    const handleMouseDown = useCallback((e: React.MouseEvent<HTMLDivElement>) => {
      e.stopPropagation();
      e.preventDefault();
    }, []);

    const handleTouchStart = useCallback(
      (e: React.TouchEvent<HTMLDivElement>) => {
        e.stopPropagation();
      },
      []
    );

    return (
      <div
        data-testid="resizer"
        onMouseDown={handleMouseDown}
        onTouchStart={handleTouchStart}
        className={cn(
          "flex items-center justify-center w-4 h-4 rounded-sm border bg-border hover:bg-muted touch-none z-[1001]",
          // Default cursor is ew-resize, but can be overridden by className
          !className?.includes('cursor-') && "cursor-ew-resize",
          className
        )}
        {...props}
      >
        {children}
      </div>
    );
  };

import {
  createContext,
  type ReactNode,
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useState,
  forwardRef,
} from "react";
import hash from "object-hash";
import { createPortal } from "react-dom";

const styleSelector = 'style, link[rel="stylesheet"]';

const collectStyles = (doc: Document) => {
  const collected: HTMLElement[] = [];

  doc.querySelectorAll(styleSelector).forEach((style) => {
    collected.push(style as HTMLElement);
  });

  return collected;
};

const getStyleSheet = (el: HTMLElement) => {
  return Array.from(document.styleSheets).find((ss) => {
    const ownerNode = ss.ownerNode as HTMLLinkElement;

    return ownerNode.href === (el as HTMLLinkElement).href;
  });
};

const getStyles = (styleSheet?: CSSStyleSheet) => {
  if (styleSheet) {
    try {
      return Array.from(styleSheet.cssRules)
        .map((rule) => rule.cssText)
        .join("");
    } catch (e: any) {
      console.warn(
        "Access to stylesheet %s is denied. Ignoring",
        styleSheet.href,
        e.message
      );
    }
  }

  return "";
};

// Sync attributes from parent window to iFrame
const syncAttributes = (sourceElement: Element, targetElement: Element) => {
  const attributes = sourceElement.attributes;
  if (attributes?.length > 0) {
    Array.from(attributes).forEach((attribute: Attr) => {
      targetElement.setAttribute(attribute.name, attribute.value);
    });
  }
};

const defer = (fn: () => void) => setTimeout(fn, 0);

const CopyHostStyles = ({
  children,
  debug = false,
  onStylesLoaded = () => null,
}: {
  children: ReactNode;
  debug?: boolean;
  onStylesLoaded?: () => void;
}) => {
  const { document: doc, window: win } = useFrame();

  useEffect(() => {
    if (!win || !doc) {
      return () => {};
    }

    const elements: { original: HTMLElement; mirror: HTMLElement }[] = [];
    const hashes: Record<string, boolean> = {};

    const lookupEl = (el: HTMLElement) =>
      elements.findIndex((elementMap) => elementMap.original === el);

    const mirrorEl = async (el: HTMLElement, inlineStyles = false) => {
      let mirror: HTMLStyleElement;

      if (el.nodeName === "LINK" && inlineStyles) {
        mirror = document.createElement("style") as HTMLStyleElement;
        mirror.type = "text/css";

        let styleSheet = getStyleSheet(el);

        if (!styleSheet) {
          await new Promise<void>((resolve) => {
            const fn = () => {
              resolve();
              el.removeEventListener("load", fn);
            };

            el.addEventListener("load", fn);
          });
          styleSheet = getStyleSheet(el);
        }

        const styles = getStyles(styleSheet);

        if (!styles) {
          if (debug) {
            console.warn(
              `Tried to load styles for link element, but couldn't find them. Skipping...`
            );
          }

          return;
        }

        mirror.innerHTML = styles;

        mirror.setAttribute("data-href", el.getAttribute("href")!);
      } else {
        mirror = el.cloneNode(true) as HTMLStyleElement;
      }

      return mirror;
    };

    const addEl = async (el: HTMLElement) => {
      const index = lookupEl(el);
      if (index > -1) {
        if (debug)
          console.log(
            `Tried to add an element that was already mirrored. Updating instead...`
          );

        const element = elements[index];
        if (element?.mirror) {
          element.mirror.innerText = el.innerText;
        }

        return;
      }

      const mirror = await mirrorEl(el);

      if (!mirror) {
        return;
      }

      const elHash = hash(mirror.outerHTML);

      if (hashes[elHash]) {
        if (debug)
          console.log(
            `iframe already contains element that is being mirrored. Skipping...`
          );

        return;
      }

      hashes[elHash] = true;

      doc.head.append(mirror as HTMLElement);
      elements.push({ original: el, mirror: mirror });

      if (debug) console.log(`Added style node ${el.outerHTML}`);
    };

    const removeEl = (el: HTMLElement) => {
      const index = lookupEl(el);
      if (index === -1) {
        if (debug)
          console.log(
            `Tried to remove an element that did not exist. Skipping...`
          );

        return;
      }

      const elHash = hash(el.outerHTML);

      elements[index]?.mirror?.remove();
      delete hashes[elHash];

      if (debug) console.log(`Removed style node ${el.outerHTML}`);
    };

    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.type === "childList") {
          mutation.addedNodes.forEach((node) => {
            if (
              node.nodeType === Node.TEXT_NODE ||
              node.nodeType === Node.ELEMENT_NODE
            ) {
              const el =
                node.nodeType === Node.TEXT_NODE
                  ? node.parentElement
                  : (node as HTMLElement);

              if (el && el.matches(styleSelector)) {
                defer(() => addEl(el));
              }
            }
          });

          mutation.removedNodes.forEach((node) => {
            if (
              node.nodeType === Node.TEXT_NODE ||
              node.nodeType === Node.ELEMENT_NODE
            ) {
              const el =
                node.nodeType === Node.TEXT_NODE
                  ? node.parentElement
                  : (node as HTMLElement);

              if (el && el.matches(styleSelector)) {
                defer(() => removeEl(el));
              }
            }
          });
        }
      });
    });

    const parentDocument = win!.parent.document;

    const collectedStyles = collectStyles(parentDocument);
    const hrefs: string[] = [];
    let stylesLoaded = 0;

    // Sync attributes for the HTML tag
    const parentHtml = parentDocument.getElementsByTagName("html")[0]!;
    syncAttributes(parentHtml, doc.documentElement);

    // Sync attributes for the Body tag
    const parentBody = parentDocument.getElementsByTagName("body")[0]!;
    syncAttributes(parentBody, doc.body);

    // Set iframe body background to transparent
    doc.body.style.backgroundColor = "transparent";

    // Copy CSS custom properties (variables) from parent html element
    const parentComputedStyle = win.parent.getComputedStyle(parentHtml);
    for (let i = 0; i < parentComputedStyle.length; i++) {
      const property = parentComputedStyle[i];
      if (property?.startsWith("--")) {
        const value = parentComputedStyle.getPropertyValue(property);
        doc.documentElement.style.setProperty(property, value);
        doc.body.style.setProperty(property, value);
      }
    }

    // Copy font-related classes from parent body
    parentBody?.classList.forEach((className) => {
      if (
        className.includes("font-") ||
        className === "antialiased" ||
        className.includes("__variable")
      ) {
        doc.body.classList.add(className);
      }
    });

    Promise.all(
      collectedStyles.map(async (styleNode) => {
        if (styleNode.nodeName === "LINK") {
          const linkHref = (styleNode as HTMLLinkElement).href;

          // Don't process link elements with identical hrefs more than once
          if (hrefs.indexOf(linkHref) > -1) {
            return;
          }

          hrefs.push(linkHref);
        }

        const mirror = await mirrorEl(styleNode);

        if (!mirror) return;

        elements.push({ original: styleNode, mirror });

        return mirror;
      })
    ).then((mirrorStyles) => {
      const filtered = mirrorStyles.filter(
        (el) => typeof el !== "undefined"
      ) as HTMLStyleElement[];

      filtered.forEach((mirror) => {
        mirror.onload = () => {
          stylesLoaded = stylesLoaded + 1;

          if (stylesLoaded >= elements.length) {
            onStylesLoaded();
          }
        };
        mirror.onerror = () => {
          console.warn(`AutoFrame couldn't load a stylesheet`);
          stylesLoaded = stylesLoaded + 1;

          if (stylesLoaded >= elements.length) {
            onStylesLoaded();
          }
        };
      });

      // Reset HTML (inside the promise) so in case running twice (i.e. for React Strict mode)
      doc.head.innerHTML = "";

      // Inject initial values in bulk
      doc.head.append(...filtered);

      observer.observe(parentDocument.head, { childList: true, subtree: true });

      filtered.forEach((el) => {
        const elHash = hash(el.outerHTML);

        hashes[elHash] = true;
      });
    });

    return () => {
      observer.disconnect();
    };
  }, [debug, doc, onStylesLoaded, win]);

  return <>{children}</>;
};

export type AutoFrameProps = {
  children: ReactNode;
  className: string;
  debug?: boolean;
  id?: string;
  onReady?: () => void;
  onNotReady?: () => void;
  style?: React.CSSProperties;
  pointerEventsEnabled?: boolean;
} & React.IframeHTMLAttributes<HTMLIFrameElement>;

type AutoFrameContext = {
  document?: Document;
  window?: Window;
};

export const autoFrameContext = createContext<AutoFrameContext>({});

export const useFrame = () => useContext(autoFrameContext);

const AutoFrame = forwardRef<HTMLIFrameElement, AutoFrameProps>(({
  children,
  className,
  debug,
  id,
  onReady = () => {},
  onNotReady = () => {},
  style,
  pointerEventsEnabled = true,
  ...props
}, ref) => {
  const [loaded, setLoaded] = useState(false);
  const [ctx, setCtx] = useState<AutoFrameContext>({});
  const [mountTarget, setMountTarget] = useState<HTMLElement | null>();
  const [stylesLoaded, setStylesLoaded] = useState(false);

  const handleLoad = useCallback(() => {
    setLoaded(true);
  }, []);

  const handleStylesLoaded = useCallback(() => {
    setStylesLoaded(true);
  }, []);

  const iframeStyle = useMemo(
    () => ({
      pointerEvents: pointerEventsEnabled
        ? ("all" as const)
        : ("none" as const),
      ...style,
    }),
    [pointerEventsEnabled, style]
  );

  useEffect(() => {
    if (ref && 'current' in ref && ref.current) {
      const doc = ref.current.contentDocument;
      const win = ref.current.contentWindow;

      setCtx({
        document: doc || undefined,
        window: win || undefined,
      });

      setMountTarget(
        ref.current.contentDocument?.getElementById("frame-root")
      );

      if (doc && win && stylesLoaded) {
        onReady();
      } else {
        onNotReady();
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [ref, loaded, stylesLoaded]);

  return (
    <iframe
      {...props}
      className={className}
      id={id}
      data-testid="auto-frame"
      srcDoc='<!DOCTYPE html><html><head></head><body><div id="frame-root" data-autoform-entry style="display: contents;"></div></body></html>'
      ref={ref}
      style={iframeStyle}
      onLoad={handleLoad}
    >
      <autoFrameContext.Provider value={ctx}>
        {loaded && mountTarget && (
          <CopyHostStyles debug={debug} onStylesLoaded={handleStylesLoaded}>
            {createPortal(children, mountTarget)}
          </CopyHostStyles>
        )}
      </autoFrameContext.Provider>
    </iframe>
  );
});

AutoFrame.displayName = "AutoFrame";

export default AutoFrame;

import { type ReactNode, useCallback, useMemo } from "react";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";

import { Button } from "@/components/ui/button";
import { ChevronDown, XIcon } from "lucide-react";

type ToggleGroupProps = {
  label: string;
  options: ToggleOption[];
  value?: string | string[] | null;
  onChange?: (value: string | string[] | null) => void;
  className?: string;
  allowDeselect?: boolean;
  hideLabel?: boolean;
  multiple?: boolean;
};

export type ToggleOption = {
  className?: string;
  value: string;
  tooltip: string;
  label?: string;
  icon?: ReactNode;
  dropdown?: {
    items: { value: string; label: ReactNode }[];
    defaultValue?: string;
    dropdownDisplay?: "grid";
  };
};

export function ToggleGroup({
  label,
  options,
  value = null,
  onChange,
  className,
  allowDeselect = true,
  hideLabel = false,
  multiple = false,
}: ToggleGroupProps) {
  // Helper to normalize value to array for multi-select
  const valueArray = useMemo(() => multiple
    ? Array.isArray(value)
      ? value
      : value
      ? [value]
      : []
    : value, [multiple, value]);

  const getIsSelected = useCallback((option: ToggleOption) => {
    if (option.dropdown) {
      if (multiple) {
        return option.dropdown.items.some((item) =>
          (valueArray as string[]).includes(
            typeof item.value === "string" ? item.value : ""
          )
        );
      } else {
        return option.dropdown.items.some(
          (item) =>
            value === (typeof item.value === "string" ? item.value : undefined)
        );
      }
    }
    if (multiple) {
      return (valueArray as string[]).includes(option.value);
    }
    return value === option.value;
  }, [multiple, valueArray, value]);

  const handleToggleClick = useCallback((option: ToggleOption) => {
    if (multiple) {
      let newValue: string[] = Array.isArray(valueArray) ? [...valueArray] : [];
      if (option.dropdown) {
        // For dropdown, add/remove all dropdown items
        const dropdownValues = option.dropdown.items.map((item) =>
          typeof item.value === "string" ? item.value : ""
        );
        const hasAny = dropdownValues.some((v) => newValue.includes(v));
        if (hasAny && allowDeselect) {
          newValue = newValue.filter((v) => !dropdownValues.includes(v));
        } else {
          // Add first dropdown value if none selected
          if (!hasAny && dropdownValues[0]) {
            newValue.push(dropdownValues[0]);
          }
        }
      } else {
        const idx = newValue.indexOf(option.value);
        if (idx > -1 && allowDeselect) {
          newValue.splice(idx, 1);
        } else if (idx === -1) {
          newValue.push(option.value);
        }
      }
      onChange?.(newValue.length ? newValue : null);
    } else {
      if (getIsSelected(option) && allowDeselect) {
        onChange?.(null);
        return;
      }
      if (option.dropdown) {
        const dropdownValue =
          option.dropdown.defaultValue ||
          (option.dropdown.items[0] &&
          typeof option.dropdown.items[0].value === "string"
            ? option.dropdown.items[0].value
            : null) ||
          null;
        onChange?.(dropdownValue);
      } else {
        onChange?.(option.value);
      }
    }
  }, [multiple, valueArray, allowDeselect, onChange, getIsSelected]);

  const handleDropdownSelect = useCallback((optionValue: string, dropdownValue: string) => {
    if (multiple) {
      let newValue: string[] = Array.isArray(valueArray) ? [...valueArray] : [];
      // Remove all values from this dropdown's set
      const dropdownValues =
        options
          .find((opt) => opt.value === optionValue)
          ?.dropdown?.items.map((item) =>
            typeof item.value === "string" ? item.value : ""
          ) || [];
      newValue = newValue.filter((v) => !dropdownValues.includes(v));
      // Add the selected value
      if (!newValue.includes(dropdownValue)) {
        newValue.push(dropdownValue);
      }
      onChange?.(newValue.length ? newValue : null);
    } else {
      onChange?.(dropdownValue);
    }
  }, [multiple, valueArray, options, onChange]);

  const selectedClass = "bg-background font-semibold shadow-sm";

  // Single event handlers that use data attributes to avoid creating functions on each render
  const handleToggleClickEvent = useCallback((e: React.MouseEvent<HTMLButtonElement>) => {
    const optionValue = e.currentTarget.dataset.optionValue;
    const option = options.find(opt => opt.value === optionValue);
    if (option) {
      handleToggleClick(option);
    }
  }, [options, handleToggleClick]);

  const handleDropdownSelectEvent = useCallback((e: React.MouseEvent<HTMLDivElement>) => {
    const optionValue = e.currentTarget.dataset.optionValue;
    const dropdownValue = e.currentTarget.dataset.dropdownValue;
    if (optionValue && dropdownValue) {
      handleDropdownSelect(optionValue, dropdownValue);
    }
  }, [handleDropdownSelect]);

  const handleCloseAutoFocus = useCallback((e: Event) => e.preventDefault(), []);

  // Memoized style object
  const minWidthStyle = useMemo(() => ({ minWidth: 0 }), []);

  return (
    <div>
      {!hideLabel && (
        <span className="text-xs font-medium text-muted-foreground">
          {label}
        </span>
      )}
      <div
        className={cn(
          "flex items-center gap-1 flex-wrap bg-muted rounded-md p-1 w-fit",
          className
        )}
      >
        {options.map((option) => {
          const isSelected = getIsSelected(option);

          if (option.dropdown) {
            // For multi, show selected dropdown item if any
            let selectedDropdownItem = null;
            if (multiple) {
              selectedDropdownItem = option.dropdown.items.find((item) =>
                (valueArray as string[]).includes(
                  typeof item.value === "string" ? item.value : ""
                )
              );
            } else {
              selectedDropdownItem = option.dropdown.items.find(
                (item) =>
                  value ===
                  (typeof item.value === "string" ? item.value : undefined)
              );
            }
            return (
              <DropdownMenu key={option.value}>
                <Tooltip>
                  <TooltipTrigger asChild>
                    <div className="flex items-center">
                      <DropdownMenuTrigger asChild>
                        <Button
                          variant="ghost"
                          size="sm"
                          className={cn(
                            "gap-px p-1 min-w-8 h-8 flex items-center justify-between rounded-r-none",
                            isSelected && selectedClass
                          )}
                          onClick={handleToggleClickEvent}
                          data-option-value={option.value}
                          aria-label={
                            option.tooltip || option.label || option.value
                          }
                          aria-pressed={isSelected}
                        >
                          <span
                            className="flex flex-row items-center gap-1"
                            style={minWidthStyle}
                          >
                            {option.icon ? (
                              <div className="contents text-muted-foreground">
                                {option.icon}
                              </div>
                            ) : (
                              <div className="text-muted-foreground text-xs leading-3 font-normal">
                                {option.label}{isSelected && ":"}
                              </div>
                            )}
                            {isSelected && option.dropdown && (
                              <DropdownOption>
                                {selectedDropdownItem?.label ||
                                  option.dropdown.defaultValue ||
                                  ""}
                              </DropdownOption>
                            )}
                          </span>
                          <ChevronDown className="!size-3 ml-1 text-muted-foreground/60 shrink-0" />
                        </Button>
                      </DropdownMenuTrigger>
                      {/* X icon to clear selection, outside the trigger */}
                      {isSelected && option.dropdown && (
                        <Button
                          variant="ghost"
                          size="sm"
                          className={cn(
                            "h-8 px-1 focus:outline-none cursor-pointer rounded-l-none rounded-r-md border-l-border border-l",
                            selectedClass
                          )}
                          aria-label="Clear selection"
                          onClick={handleToggleClickEvent}
                          data-option-value={option.value}
                        >
                          <XIcon className="!size-3 text-muted-foreground" />
                        </Button>
                      )}
                    </div>
                  </TooltipTrigger>
                  <TooltipContent>{option.tooltip}</TooltipContent>
                </Tooltip>
                <DropdownMenuContent
                  align="end"
                  onCloseAutoFocus={handleCloseAutoFocus}
                  className={cn(
                    "max-h-96 overflow-y-auto",
                    option.dropdown.dropdownDisplay === "grid"
                      ? "grid grid-cols-5 gap-px p-1 "
                      : ""
                  )}
                >
                  {option.dropdown.items.map((item) => (
                    <DropdownMenuItem
                      key={
                        typeof item.value === "string" ? item.value : undefined
                      }
                      className={
                        (
                          multiple
                            ? (valueArray as string[]).includes(
                                typeof item.value === "string" ? item.value : ""
                              )
                            : value ===
                              (typeof item.value === "string"
                                ? item.value
                                : undefined)
                        )
                          ? selectedClass
                          : ""
                      }
                      onClick={handleDropdownSelectEvent}
                      data-option-value={option.value}
                      data-dropdown-value={typeof item.value === "string" ? item.value : ""}
                      aria-label={
                        typeof item.label === "string" ? item.label : undefined
                      }
                      aria-selected={
                        multiple
                          ? (valueArray as string[]).includes(
                              typeof item.value === "string" ? item.value : ""
                            )
                          : value ===
                            (typeof item.value === "string"
                              ? item.value
                              : undefined)
                      }
                    >
                      {item.label}
                    </DropdownMenuItem>
                  ))}
                </DropdownMenuContent>
              </DropdownMenu>
            );
          }

          return (
            <Tooltip key={option.value}>
              <TooltipTrigger asChild>
                <Button
                  variant="ghost"
                  size="sm"
                  className={cn(
                    "gap-px p-1 min-w-8 h-8 text-xs flex items-center justify-between",
                    isSelected && selectedClass
                  )}
                  onClick={handleToggleClickEvent}
                  data-option-value={option.value}
                  aria-label={option.tooltip || option.label || option.value}
                  aria-pressed={isSelected}
                >
                  <span
                    className={cn(
                      "text-sx text-muted-foreground",
                      option.icon
                        ? "flex flex-row items-center gap-1"
                        : "flex flex-col items-center justify-center flex-1"
                    )}
                    style={minWidthStyle}
                  >
                    {option.icon}
                    {option.label && (
                      <span
                        className={cn(
                          option.icon && "ml-2",
                          !isSelected && "font-normal"
                        )}
                      >
                        {option.label}
                      </span>
                    )}
                  </span>
                </Button>
              </TooltipTrigger>
              <TooltipContent>{option.tooltip}</TooltipContent>
            </Tooltip>
          );
        })}
      </div>
    </div>
  );
}

export function DropdownOption({
  color,
  children,
}: {
  color?: string;
  children: ReactNode;
}) {
  return (
    <div className="flex flex-row items-center justify-start text-center w-full">
      {color && (
        <span
          className={cn(
            "inline-block size-[14px] rounded-full border border-border mr-1",
            color
          )}
        />
      )}
      <span className="text-xs text-muted-foreground">{children}</span>
    </div>
  );
}

"use client";

import { type ReactNode, useMemo } from "react";
import { cn } from "@/lib/utils";
import { useLayerStore } from "@/lib/ui-builder/store/layer-store";
import { baseColors } from "@/components/ui/ui-builder/internal/utils/base-colors";
import type { ComponentLayer } from "@/components/ui/ui-builder/types";

// Known prefixes for shadcn theme colors
const THEME_COLOR_PREFIXES = ["bg-", "text-", "border-", "shadow-"] as const;

export function ThemeAwareDropdownOption({
  color,
  children,
}: {
  color?: string;
  children: ReactNode;
}) {
  const  selectedPageId = useLayerStore( state => state.selectedPageId);
  const findLayerById = useLayerStore( state => state.findLayerById);

  // Get current theme information
  const selectedPageData = findLayerById(selectedPageId) as ComponentLayer;
  const currentTheme = selectedPageData?.props?.["data-color-theme"];
  const currentMode = selectedPageData?.props?.["data-mode"] || "light";


  // Resolve the actual color value
  const getResolvedColor = (colorClass?: string): string | undefined => {
    if (!colorClass) {
      return undefined;
    }

    // Check if this is a theme color by trying to strip known prefixes
    let themeColorKey: string | undefined;
    for (const prefix of THEME_COLOR_PREFIXES) {
      if (colorClass.startsWith(prefix)) {
        themeColorKey = colorClass.substring(prefix.length);
        break;
      }
    }

    if (themeColorKey && currentTheme) {
      try {
        // Find the current theme data
        const themeData = baseColors.find((theme) => theme.name === currentTheme);

        if (themeData?.cssVars) {
          const modeColors = themeData.cssVars[currentMode as "light" | "dark"];

          if (modeColors) {
            const colorValue = (modeColors as any)[themeColorKey];

            if (colorValue) {
              const hslColor = `hsl(${colorValue})`;
              return hslColor;
            }
          }
        }
      } catch (error) {
        // Silently fail and fall back to using the class name
        console.warn(`Failed to resolve theme color for ${colorClass}:`, error);
      }
    }

    // Fallback to using the Tailwind class (for non-theme colors like bg-red-500)
    return undefined;
  };

  const resolvedColor = getResolvedColor(color);

  const style = useMemo(() => ({ backgroundColor: resolvedColor }), [resolvedColor]);


  return (
    <div className="flex flex-row items-center justify-start text-center w-full">
      {color && (
        <span
          className={cn(
            "inline-block size-[14px] rounded-full border border-border mr-1",
            // Use resolved color as inline style if available, otherwise fall back to class
            !resolvedColor && color
          )}
          style={resolvedColor ? style : undefined}
        />
      )}
      <span className="text-xs text-muted-foreground">{children}</span>
    </div>
  );
}

import {
  TAILWIND_BORDER_COLOR_CLASSES,
  TAILWIND_BACKGROUND_COLOR_CLASSES,
  TAILWIND_TEXT_COLOR_CLASSES,
  TAILWIND_BOX_SHADOW_COLOR_CLASSES,
  SHADCN_TAILWIND_BORDER_COLOR_CLASSES,
  SHADCN_TAILWIND_BACKGROUND_COLOR_CLASSES,
  SHADCN_TAILWIND_TEXT_COLOR_CLASSES,
} from "@/components/ui/ui-builder/internal/utils/tailwind-classes";
import { ThemeAwareDropdownOption } from "@/components/ui/ui-builder/internal/form-fields/classname-control/theme-aware-dropdown-option";
import { DropdownOption } from "@/components/ui/ui-builder/internal/form-fields/classname-control/toggle-group";

// Create theme-aware color dropdown items (for shadcn theme colors)
export function createThemeAwareColorDropdown(
  colorClasses: readonly string[],
  classPrefix: string,
  bgPrefix: string
) {
  return colorClasses.map((cls) => {
    const colorName = cls.replace(classPrefix, "");
    const colorClass = cls.replace(classPrefix, bgPrefix);

    return {
      value: cls,
      label: (
        <ThemeAwareDropdownOption color={colorClass}>
          {colorName}
        </ThemeAwareDropdownOption>
      ),
    };
  });
}

// Create regular color dropdown items (for standard Tailwind colors)
export function createRegularColorDropdown(
  colorClasses: readonly string[],
  classPrefix: string,
  bgPrefix: string
) {
  return colorClasses.map((cls) => {
    const colorName = cls.replace(classPrefix, "");
    const colorClass = cls.replace(classPrefix, bgPrefix);

    return {
      value: cls,
      label: (
        <DropdownOption color={colorClass}>
          {colorName}
        </DropdownOption>
      ),
    };
  });
}

// Combine theme-aware and regular colors for a complete dropdown
export function createCombinedColorDropdown(
  shadcnClasses: readonly string[],
  tailwindClasses: readonly string[],
  classPrefix: string,
  bgPrefix: string
) {
  const themeColors = createThemeAwareColorDropdown(shadcnClasses, classPrefix, bgPrefix);
  const regularColors = createRegularColorDropdown(tailwindClasses, classPrefix, bgPrefix);

  return [
    ...themeColors,
    ...regularColors,
  ];
}

// Theme-aware border colors (prioritizing shadcn theme colors)
export const THEME_AWARE_BORDER_COLOR_ITEMS = createCombinedColorDropdown(
  SHADCN_TAILWIND_BORDER_COLOR_CLASSES,
  TAILWIND_BORDER_COLOR_CLASSES,
  "border-",
  "bg-"
);

// Theme-aware background colors (prioritizing shadcn theme colors) 
export const THEME_AWARE_BACKGROUND_COLOR_ITEMS = createCombinedColorDropdown(
  SHADCN_TAILWIND_BACKGROUND_COLOR_CLASSES,
  TAILWIND_BACKGROUND_COLOR_CLASSES,
  "bg-",
  "bg-"
);

// Theme-aware text colors (prioritizing shadcn theme colors)
export const THEME_AWARE_TEXT_COLOR_ITEMS = createCombinedColorDropdown(
  SHADCN_TAILWIND_TEXT_COLOR_CLASSES,
  TAILWIND_TEXT_COLOR_CLASSES,
