import React, { useCallback, useMemo } from "react";

import MultipleSelector, {
  type Option,
} from "@/components/ui/ui-builder/internal/components/multi-select";
import { TAILWIND_CLASSES_WITH_BREAKPOINTS } from "@/components/ui/ui-builder/internal/utils/tailwind-classes";

const EMPTY_OPTIONS: Option[] = [];

interface ClassNameMultiselectProps {
  value: string;
  onChange: (newClassName: string) => void;
}

const ClassNameMultiselect: React.FC<ClassNameMultiselectProps> = ({
  value,
  onChange,
}) => {
  const searchClasses = useCallback(
    async (value: string): Promise<Option[]> => {
      return new Promise((resolve) => {
        const res = TAILWIND_CLASSES_WITH_BREAKPOINTS.filter((option) =>
          option.includes(value)
        );
        resolve(
          res.map((cls) => ({
            value: cls,
            label: cls,
          }))
        );
      });
    },
    []
  );

  const handleChange = useCallback(
    (values: Option[]) => {
      const newClassName = values.map((v) => v.value).join(" ");
      onChange(newClassName);
    },
    [onChange]
  );

  const emptyIndicator = useMemo(
    () => (
      <p className="text-center text-lg leading-10 text-gray-600 dark:text-gray-400">
        No results found.
      </p>
    ),
    []
  );

  const loadingIndicator = useMemo(
    () => (
      <p className="py-2 text-center text-lg leading-10 text-muted-foreground">
        Loading...
      </p>
    ),
    []
  );


  const multipleSelectorValues = useMemo(() => {
    return value
    ?.split(" ")
    .filter((cls) => cls.trim() !== "")
    .map((cls: string) => ({
      value: cls,
      label: cls,
    })) || EMPTY_OPTIONS
  }, [value]);


  return (
    <MultipleSelector
      defaultOptions={EMPTY_OPTIONS}
      value={multipleSelectorValues}
      onChange={handleChange}
      placeholder="Type class name..."
      creatable
      emptyIndicator={emptyIndicator}
      loadingIndicator={loadingIndicator}
      onSearch={searchClasses}
    />
  );
};

export default ClassNameMultiselect;

import React, { useMemo, useState, useEffect, useCallback } from "react";
import { CONFIG, LAYOUT_GROUPS, LAYOUT_ORDER, type StateType } from "@/components/ui/ui-builder/internal/form-fields/classname-control/config";
import { ClassNameGroupControl } from "@/components/ui/ui-builder/internal/form-fields/classname-control/classname-group-control";
import { cn } from "@/lib/utils";
import { isTailwindClass } from "@/components/ui/ui-builder/internal/form-fields/classname-control/helpers";

interface ClassNameItemControlProps {
    value: string;
    onChange: (value: string) => void;
}

export function ClassNameItemControl({ value, onChange }: ClassNameItemControlProps) {
    // Memoize parsing for performance
    const parsed = useMemo(() => {
      if (value) {
        const tokens = value.trim().split(/\s+/);
        // Parse all keys
        const parsedState = Object.entries(CONFIG).reduce(
          (acc, [key, config]) => {
            const parsed = tokens.filter((token) => isTailwindClass(config.possibleTypes.filter((type): type is string => type !== null), token));
            if (parsed.length > 0) {
              (acc as any)[key] = config.multiple ? parsed : parsed[0];
            }
            return acc;
          },
          Object.fromEntries(Object.keys(CONFIG).map((k) => [k, null])) as Record<
            string,
            any
          >
        ) as StateType;
        // For each group, only allow one key to be active at a time
        const initialSelected: { [groupLabel: string]: string } = {};
        LAYOUT_GROUPS.forEach((group) => {
          // If multiple keys have values, clear all but the first
          let found = false;
          group.keys.forEach((key) => {
            if (parsedState[key]) {
              if (!found) {
                found = true;
                initialSelected[group.label] = key;
              } else {
                parsedState[key] = null;
              }
            }
          });
          if (!found && group.keys[0]) {
            initialSelected[group.label] = group.keys[0];
          }
        });
        // Find handled tokens
        const handledTokens = new Set(
          Object.values(parsedState).flatMap((v) =>
            Array.isArray(v) ? v : v ? [v] : []
          )
        );
        const unhandledTokens = tokens.filter(
          (token) => !handledTokens.has(token)
        );
        return {
          parsedState,
          unhandledTokens,
          initialSelected,
        };
      } else {
        // Reset state if value is empty
        const initialSelected: { [groupLabel: string]: string } = {};
        LAYOUT_GROUPS.forEach((group) => {
          if (group.keys[0]) {
            initialSelected[group.label] = group.keys[0];
          }
        });
        return {
          parsedState: {} as StateType,
          unhandledTokens: [],
          initialSelected,
        };
      }
    }, [value]);

    const [state, setState] = useState<StateType>(parsed.parsedState);
    const [unhandled, setUnhandled] = useState<string[]>(parsed.unhandledTokens);
    const [selectedKeys, setSelectedKeys] = useState<{
      [groupLabel: string]: string;
    }>(parsed.initialSelected);

    // Only update state if parsed result changes (deep compare)
    useEffect(() => {
      const stateChanged =
        JSON.stringify(state) !== JSON.stringify(parsed.parsedState);
      const unhandledChanged =
        JSON.stringify(unhandled) !== JSON.stringify(parsed.unhandledTokens);
      const selectedChanged =
        JSON.stringify(selectedKeys) !== JSON.stringify(parsed.initialSelected);
      if (stateChanged) setState(parsed.parsedState);
      if (unhandledChanged) setUnhandled(parsed.unhandledTokens);
      if (selectedChanged) setSelectedKeys(parsed.initialSelected);
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [parsed]);

    // Helper to build class string from state and unhandled
    const buildClassString = (
      customState: StateType = state,
      customUnhandled: string[] = unhandled
    ) => {
      const classes: string[] = [];
      for (const key in customState) {
        const value = customState[key as keyof StateType];
        if (value) {
          if (Array.isArray(value)) {
            classes.push(...value);
          } else {
            classes.push(value);
          }
        }
      }
      return [...classes, ...customUnhandled].join(" ").trim();
    };

    // Handler for UI changes (toggles, dropdowns, etc.)
    const handleStateChange = useCallback((
      key: keyof StateType,
      value: string | string[] | null
    ) => {
      setState((prev) => {
        const newState = { ...prev, [key]: value };
        // If this key is part of a group, update selectedKeys and clear other keys in the group
        const group = LAYOUT_GROUPS.find((g) => g.keys.includes(key as string));
        if (group) {
          setSelectedKeys((prevSel) => ({
            ...prevSel,
            [group.label]: key as string,
          }));
          // Clear other keys in the group
          group.keys.forEach((k) => {
            if (k !== key) newState[k] = null;
          });
          // Also clear from unhandled
          if (typeof group.clearState === "function") {
            const classesToClear = group.clearState(key as string, newState);
            setUnhandled((prevUnhandled) =>
              prevUnhandled.filter((token) => !classesToClear.includes(token))
            );
          }
        }

        // if visibility changes from visible to invisible null the value for that key in the newState
        const layoutOrderItemsInvisible = LAYOUT_ORDER.filter(
          (item) => item.isVisible && !item.isVisible(newState)
        );
        layoutOrderItemsInvisible.forEach((item) => {
          if (item.type === "item") {
            newState[item.key] = null;
          } else if (item.type === "group") {
            const group = LAYOUT_GROUPS.find((g) => g.label === item.label);
            if (group) {
              group.keys.forEach((key) => {
                newState[key] = null;
              });
            }
          }
        });

        return newState;
      });
    }, []);

    // Helper to create bound change handlers
    const createChangeHandler = useCallback((key: keyof StateType) => 
      (value: string | string[] | null) => handleStateChange(key, value),
      [handleStateChange]
    );

    // Handler for group key selection
    const handleGroupKeySelect = (groupLabel: string, key: string) => {
      setSelectedKeys((prev) => ({
        ...prev,
        [groupLabel]: key,
      }));
      const group = LAYOUT_GROUPS.find((g) => g.label === groupLabel);
      if (group) {
        let classesToClear: string[] = [];
        setState((prevState) => {
          const newState = { ...prevState };
          if (typeof group.clearState === "function") {
            classesToClear = group.clearState(key, newState);
          }
          // Remove all classes in classesToClear from state for all keys in the group except the selected one
          for (const k of group.keys) {
            if (k !== key) {
              const config = CONFIG[k];
              if (!config) continue;
              if (config.multiple && Array.isArray(newState[k])) {
                newState[k] = (newState[k] as string[]).filter(
                  (v) => !classesToClear.includes(v as any)
                );
                if (Array.isArray(newState[k]) && newState[k]?.length === 0)
                  newState[k] = null;
              } else if (classesToClear.includes(newState[k] as any)) {
                newState[k] = null;
              }
            }
          }
          // Remove from unhandled synchronously
          setUnhandled((prevUnhandled) =>
            prevUnhandled.filter((token) => !classesToClear.includes(token))
          );
          // Set the selected key's value to null if it was not previously set
          if (!newState[key]) newState[key] = null;
          return newState;
        });
      }
    };

    // Call onChange with the new class string after state/unhandled change
    useEffect(() => {
      const classString = buildClassString(state, unhandled);
      if (onChange) onChange(classString);
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [state, unhandled]);

    return (
      <div className="w-full" data-testid="classname-item-control">
        <div className="flex flex-wrap gap-y-1 px-4 pt-2 pb-4">
          {LAYOUT_ORDER.map((entry) => {
            if (entry.type === "group") {
              const group = LAYOUT_GROUPS.find((g) => g.label === entry.label);
              if (!group) return null;
              const keys = group.keys.map(String);
              const selectedKey = selectedKeys[group.label] || keys[0];
              if (entry.isVisible && !entry.isVisible(state)) return null;
              if (!selectedKey) return null;
              const groupConfig = CONFIG[selectedKey as keyof typeof CONFIG];
              if (!groupConfig) return null;

              return (
                <div key={group.label} className={cn("w-full", entry.className)} data-testid={`group-${group.label.toLowerCase().replace(/\s+/g, '-')}`}>
                  <ClassNameGroupControl
                    groupConfig={groupConfig}
                    group={group}
                    state={state}
                    handleStateChange={handleStateChange}
                    handleGroupKeySelect={handleGroupKeySelect}
                    selectedKey={selectedKey}
                  />
                </div>
              );
            } else if (entry.type === "item") {
              if (entry.isVisible && !entry.isVisible(state)) return null;
              const configKey = entry.key;
              const ungroupedConfig = CONFIG[configKey];
              if (!ungroupedConfig) return null;
              return (
                <div key={configKey} className={cn("w-full", entry.className)} data-testid={`item-${configKey}`}>
                  <ungroupedConfig.component
                    {...ungroupedConfig}
                    {...("multiple" in ungroupedConfig
                      ? { multiple: ungroupedConfig.multiple }
                      : {})}
                    value={state[configKey]}
                    onChange={createChangeHandler(configKey)}
                  />
                </div>
              );
            }
            return null;
          })}
        </div>
      </div>
    );
  }

import { CONFIG, type ConfigItem, type StateType } from "@/components/ui/ui-builder/internal/form-fields/classname-control/config";
import { Button } from "@/components/ui/button";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";
import { MoreVertical } from "lucide-react";
import { useMemo, useCallback } from "react";

const EMPTY_OBJECT = {};

type ClassNameGroupControlProps = {
    groupConfig: ConfigItem;
    group: {
      label: string;
      keys: string[];
    };
    state: StateType;
    handleStateChange: (
      key: keyof StateType,
      value: string | string[] | null
    ) => void;
    handleGroupKeySelect: (groupLabel: string, key: string) => void;
    selectedKey: string | string[] | null;
  };

  export function ClassNameGroupControl({
    groupConfig,
    group,
    state,
    handleStateChange,
    handleGroupKeySelect,
    selectedKey,
  }: ClassNameGroupControlProps) {

    // Memoize the conditional spread props
    const spreadProps = useMemo(() => 
      "multiple" in groupConfig 
        ? { multiple: groupConfig.multiple }
        : EMPTY_OBJECT,
      [groupConfig]
    );

    // Memoize the change handler
    const handleChange = useCallback((value: string | string[] | null) => 
      handleStateChange(selectedKey as keyof StateType, value),
      [handleStateChange, selectedKey]
    );

    // Memoize the dropdown click handler
    const handleDropdownClick = useCallback((key: string) => 
      () => handleGroupKeySelect(group.label, key),
      [handleGroupKeySelect, group.label]
    );

    return (
      <>
        <span className="text-xs font-medium text-muted-foreground">
          {group.label}
        </span>
        <div className="flex flex-row">
          <div>
            <groupConfig.component
              {...groupConfig}
              {...spreadProps}
              hideLabel={true}
              value={state[selectedKey as keyof StateType]}
              onChange={handleChange}
            />
          </div>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="outline" size="icon" className="text-xs w-6 px-1 py-1">
                <MoreVertical className="!size-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              {group.keys.map((key) => (
                <DropdownMenuItem
                  key={String(key)}
                  onClick={handleDropdownClick(String(key))}
                  className={
                    selectedKey === String(key)
                      ? "bg-secondary-foreground/10"
                      : ""
                  }
                >
                  {CONFIG[String(key)]?.label || String(key)}
                </DropdownMenuItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </>
    );
  }

import { useState, useEffect, useCallback } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import ClassNameMultiselect from "@/components/ui/ui-builder/internal/form-fields/classname-control/classname-multiselect";
import { ClassNameItemControl } from "@/components/ui/ui-builder/internal/form-fields/classname-control/classname-item-control";
import {
  Accordion,
  AccordionItem,
  AccordionTrigger,
  AccordionContent,
} from "@/components/ui/accordion";
import { Badge } from "@/components/ui/badge";

interface BreakpointClassNameControlProps {
  onChange?: (classes: string) => void;
  value?: string;
}
export const BreakpointClassNameControl = ({
  onChange,
  value,
}: BreakpointClassNameControlProps) => {
  // Helper to parse classString into base, md, rest
  const parseClassString = (str: string) => {
    const tokens = str.trim().split(/\s+/);
    const base: string[] = [];
    const md: string[] = [];
    const rest: string[] = [];
    for (const token of tokens) {
      if (token.startsWith("md:")) {
        md.push(token.slice(3));
      } else if (token.includes(":")) {
        rest.push(token);
      } else if (token) {
        base.push(token);
      }
    }
    return {
      base: base.join(" "),
      md: md.join(" "),
      rest: rest.join(" "),
    };
  };

  // State for the full class string
  const [classString, setClassString] = useState(value || "");
  // State for the tab
  const [tab, setTab] = useState<"base" | "md">("base");

  // Sync classString with value prop (uncontrolled to controlled fix)
  useEffect(() => {
    if (typeof value === "string" && value !== classString) {
      setClassString(value);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value]);

  // Parse the class string for the tabs
  const { base, md, rest } = parseClassString(classString);

  // Handlers for each tab
  const handleBaseChange = useCallback(
    (newBase: string) => {
      const newClassString = [
        newBase,
        md &&
          md
            .split(" ")
            .map((cls) => `md:${cls}`)
            .join(" "),
        rest,
      ]
        .filter(Boolean)
        .join(" ")
        .replace(/\s+/g, " ")
        .trim();
      setClassString(newClassString);
    },
    [md, rest]
  );

  const handleMdChange = useCallback(
    (newMd: string) => {
      const mdClasses = newMd
        .split(" ")
        .filter(Boolean)
        .map((cls) => `md:${cls}`)
        .join(" ");
      const newClassString = [base, mdClasses, rest]
        .filter(Boolean)
        .join(" ")
        .replace(/\s+/g, " ")
        .trim();
      setClassString(newClassString);
    },
    [base, rest]
  );

  // When classString changes, call parent onChange
  useEffect(() => {
    if (onChange) onChange(classString);
  }, [classString, onChange]);

  // When multiselect changes, update classString (and tabs will re-parse)
  const handleMultiselectChange = useCallback((newClassString: string) => {
    setClassString(newClassString);
  }, []);

  const handleTabChange = useCallback(
    (val: string) => setTab(val as "base" | "md"),
    []
  );

  return (
    <div
      className="w-full border rounded-lg"
      data-testid="breakpoint-classname-control"
    >
      <Tabs
        value={tab}
        onValueChange={handleTabChange}
        className="w-full"
        data-testid="breakpoint-tabs"
      >
        <TabsList
          className="w-full grid grid-cols-2"
          data-testid="breakpoint-tabs-list"
        >
          <TabsTrigger value="base" data-testid="base-tab-trigger">
            <Tooltip>
              <TooltipTrigger asChild>
                <div className="flex items-center">
                  <span>Base</span>
                  {base && (
                    <Badge className="ml-1 justify-center text-center px-[3px] h-[18px] min-w-[18px] !text-[10px]">
                      {base.split(" ").filter(Boolean).length}
                    </Badge>
                  )}
                </div>
              </TooltipTrigger>
              <TooltipContent>Base styles for all screen sizes</TooltipContent>
            </Tooltip>
          </TabsTrigger>
          <TabsTrigger value="md" data-testid="md-tab-trigger">
            <Tooltip>
              <TooltipTrigger asChild>
