import React from 'react';
import type { Variable, PropValue, ComponentLayer, FunctionRegistry } from '@/components/ui/ui-builder/types';
import { isVariableReference } from '@/components/ui/ui-builder/types';

/**
 * Resolves a variable reference in layer children to its actual value
 * @param children - The children value which may be a VariableReference, string, or ComponentLayer[]
 * @param variables - Array of available variables
 * @param variableValues - Object mapping variable IDs to their resolved values
 * @returns The resolved children value (string for variable references, or original value)
 */
export function resolveChildrenVariableReference(
  children: ComponentLayer['children'],
  variables: Variable[],
  variableValues?: Record<string, PropValue>
): ComponentLayer['children'] {
  if (isVariableReference(children)) {
    const variable = variables.find(v => v.id === children.__variableRef);
    if (variable) {
      // Use provided value or fall back to default value
      const resolvedValue = variableValues?.[variable.id] ?? variable.defaultValue;
      // Ensure we return a string for text content
      return String(resolvedValue);
    }
    // Variable not found, return empty string
    return '';
  }
  // Return original value for string or ComponentLayer[]
  return children;
}

/**
 * Resolves variable references in props using provided variable values.
 * Also resolves __function_* metadata props by looking up functions from the registry.
 * 
 * @param props - The props object that may contain variable references or function metadata
 * @param variables - Array of available variables
 * @param variableValues - Object mapping variable IDs to their resolved values
 * @param functionRegistry - Optional function registry for resolving function-type variables
 * @returns Props with variable references resolved and functions looked up
 */
export function resolveVariableReferences(
  props: Record<string, PropValue>,
  variables: Variable[],
  variableValues?: Record<string, PropValue>,
  functionRegistry?: FunctionRegistry
): Record<string, PropValue> {
  const resolved: Record<string, PropValue> = {};

  // First pass: collect all __function_* metadata to resolve function props
  const functionMetadata: Record<string, string> = {};
  for (const [key, value] of Object.entries(props)) {
    if (key.startsWith('__function_') && typeof value === 'string') {
      // Extract the prop name from __function_propName
      const propName = key.replace('__function_', '');
      functionMetadata[propName] = value;
    }
  }

  // Resolve function metadata props first (these may not have a corresponding base prop)
  for (const [propName, functionId] of Object.entries(functionMetadata)) {
    if (functionRegistry) {
      const funcDef = functionRegistry[functionId];
      if (funcDef) {
        resolved[propName] = funcDef.fn;
      } else {
        console.warn(`Function "${functionId}" not found in function registry for prop "${propName}"`);
        resolved[propName] = undefined;
      }
    }
  }

  for (const [key, value] of Object.entries(props)) {
    // Skip __function_* metadata props (they're handled separately)
    if (key.startsWith('__function_')) {
      continue;
    }

    // Skip props that were already resolved via __function_* metadata
    // Only skip if functionRegistry was provided, otherwise preserve the original value
    if (functionMetadata[key]) {
      if (!functionRegistry) {
        // Warn when function metadata exists but no registry is provided
        console.warn(
          `Function metadata "__function_${key}" found but no functionRegistry provided. ` +
          `Falling back to original prop value for "${key}".`
        );
      } else {
        continue;
      }
    }

    if (isVariableReference(value)) {
      const variable = variables.find(v => v.id === value.__variableRef);
      if (variable) {
        // Handle function-type variables specially
        if (variable.type === 'function') {
          // For function variables, look up the actual function from the registry
          const varFunctionId = String(variable.defaultValue);
          if (functionRegistry) {
            const funcDef = functionRegistry[varFunctionId];
            if (funcDef) {
              resolved[key] = funcDef.fn;
            } else {
              // Function not found in registry
              console.warn(`Function "${varFunctionId}" not found in function registry`);
              resolved[key] = undefined;
            }
          } else {
            // No function registry provided
            console.warn('Function-type variable found but no functionRegistry provided');
            resolved[key] = undefined;
          }
        } else {
          // Use provided value or fall back to default value
          resolved[key] = variableValues?.[variable.id] ?? variable.defaultValue;
        }
      } else {
        // Variable not found, use default value or undefined
        resolved[key] = undefined;
      }
    } else if (typeof value === 'object' && value !== null && !Array.isArray(value) && !React.isValidElement(value)) {
      // Recursively resolve nested objects (but not React elements or arrays)
      resolved[key] = resolveVariableReferences(value as Record<string, PropValue>, variables, variableValues, functionRegistry);
    } else {
      // Regular value, keep as is
      resolved[key] = value;
    }
  }

  return resolved;
}

// Export the isVariableReference function for backward compatibility
export { isVariableReference };

/**
 * Validation logic for paste operations in the UI Builder.
 * Ensures that layers can only be pasted into compatible parent layers.
 */

import type { ComponentLayer, ComponentRegistry } from '@/components/ui/ui-builder/types';
import { canLayerAcceptChildren } from '@/lib/ui-builder/store/layer-utils';
import { hasChildrenFieldOfTypeString } from '@/lib/ui-builder/store/schema-utils';

/**
 * Check if a layer can be pasted into a target layer.
 * 
 * Validates:
 * 1. Target layer exists
 * 2. Target layer can accept children (has children field and currently has array children)
 * 3. Target layer doesn't only accept string children
 * 4. Source layer's childOf constraint (if any) allows the target layer type
 * 
 * @param sourceLayer - The layer being pasted (from clipboard)
 * @param targetLayerId - The ID of the layer to paste into
 * @param componentRegistry - The component registry for looking up component definitions
 * @param findLayerById - Function to find a layer by ID
 * @returns true if the paste operation is valid
 */
export function canPasteLayer(
  sourceLayer: ComponentLayer,
  targetLayerId: string,
  componentRegistry: ComponentRegistry,
  findLayerById: (id: string) => ComponentLayer | undefined
): boolean {
  // Check if target layer exists
  const targetLayer = findLayerById(targetLayerId);
  if (!targetLayer) {
    return false;
  }

  // Check if target can accept children
  if (!canLayerAcceptChildren(targetLayer, componentRegistry)) {
    return false;
  }

  // Check if target only accepts string children
  const targetDef = componentRegistry[targetLayer.type];
  if (targetDef && 'shape' in targetDef.schema && hasChildrenFieldOfTypeString(targetDef.schema)) {
    return false;
  }

  // Check childOf constraint on the source layer
  const sourceDef = componentRegistry[sourceLayer.type];
  if (sourceDef?.childOf && !sourceDef.childOf.includes(targetLayer.type)) {
    return false;
  }

  return true;
}

/**
 * Check if a component type can be pasted into a target layer.
 * Similar to canPasteLayer but works with a component type string instead of a full layer.
 * 
 * @param componentType - The type of component being pasted
 * @param targetLayerId - The ID of the layer to paste into
 * @param componentRegistry - The component registry for looking up component definitions
 * @param findLayerById - Function to find a layer by ID
 * @returns true if the paste operation is valid
 */
export function canPasteComponentType(
  componentType: string,
  targetLayerId: string,
  componentRegistry: ComponentRegistry,
  findLayerById: (id: string) => ComponentLayer | undefined
): boolean {
  // Check if target layer exists
  const targetLayer = findLayerById(targetLayerId);
  if (!targetLayer) {
    return false;
  }

  // Check if target can accept children
  if (!canLayerAcceptChildren(targetLayer, componentRegistry)) {
    return false;
  }

  // Check if target only accepts string children
  const targetDef = componentRegistry[targetLayer.type];
  if (targetDef && 'shape' in targetDef.schema && hasChildrenFieldOfTypeString(targetDef.schema)) {
    return false;
  }

  // Check childOf constraint on the component type
  const componentDef = componentRegistry[componentType];
  if (componentDef?.childOf && !componentDef.childOf.includes(targetLayer.type)) {
    return false;
  }

  return true;
}

export function getScrollParent(element: HTMLElement | null): HTMLElement | null {
    if (!element) return null;

    const overflowRegex = /(auto|scroll)/;

    let parent: HTMLElement | null = element.parentElement;

    while (parent) {
      const style = getComputedStyle(parent);
      const overflowY = style.overflowY;
      const overflowX = style.overflowX;

      if (overflowRegex.test(overflowY) || overflowRegex.test(overflowX)) {
        return parent;
      }

      parent = parent.parentElement;
    }

    return null;
  }

import type { z } from 'zod';
import type { FunctionRegistry, Variable } from '@/components/ui/ui-builder/types';

/**
 * Checks if the given Zod schema represents a function type.
 * This is used to detect when a component prop expects a function (e.g., onClick).
 */
export function isZodFunctionSchema(schema: z.ZodTypeAny): boolean {
  const def = (schema as any)?._zod?.def;
  if (!def) return false;

  // Check for function type
  if (def.type === 'function') {
    return true;
  }

  // Check wrapped types (optional, nullable, default)
  if (def.type === 'optional' || def.type === 'nullable' || def.type === 'default') {
    const innerSchema = def.innerType ?? def.schema;
    if (innerSchema) {
      return isZodFunctionSchema(innerSchema);
    }
  }

  return false;
}

/**
 * Extracts the function arguments schema from a Zod function schema.
 * Returns undefined if the schema is not a function schema or has no args defined.
 */
export function extractFunctionArgsSchema(schema: z.ZodTypeAny): z.ZodTuple<any, any> | z.ZodObject<any> | undefined {
  const def = (schema as any)?._zod?.def;
  if (!def) return undefined;

  if (def.type === 'function' && def.args) {
    return def.args;
  }

  // Handle wrapped types
  if (def.type === 'optional' || def.type === 'nullable' || def.type === 'default') {
    const innerSchema = def.innerType ?? def.schema;
    if (innerSchema) {
      return extractFunctionArgsSchema(innerSchema);
    }
  }

  return undefined;
}

/**
 * Checks if two function signatures are compatible.
 * 
 * Compatibility rules:
 * 1. Positional args: (a, b, c, d) => void can accept (a) => void (extra args are ignored by the function)
 * 2. Object params: ({a, b, c}) => void can accept ({a}) => void (subset of object properties)
 * 
 * @param targetSchema - The schema expected by the component prop (what the component will call with)
 * @param candidateSchema - The schema of the function being tested (what it actually accepts)
 * @returns true if the candidate function can be safely called with the target arguments
 */
export function areFunctionSignaturesCompatible(
  targetSchema: z.ZodTuple<any, any> | z.ZodObject<any> | z.ZodTypeAny | undefined,
  candidateSchema: z.ZodTuple<any, any> | z.ZodObject<any> | z.ZodTypeAny | undefined
): boolean {
  // If no schemas provided, assume compatible (permissive)
  if (!targetSchema || !candidateSchema) {
    return true;
  }

  const targetDef = (targetSchema as any)?._zod?.def;
  const candidateDef = (candidateSchema as any)?._zod?.def;

  if (!targetDef || !candidateDef) {
    return true; // If we can't introspect, assume compatible
  }

  // Handle tuple (positional args) comparison
  if (targetDef.type === 'tuple' && candidateDef.type === 'tuple') {
    const targetItems = targetDef.items || [];
    const candidateItems = candidateDef.items || [];

    // Candidate can have fewer arguments than target (extra args ignored)
    // But candidate cannot require more arguments than target provides
    return candidateItems.length <= targetItems.length;
  }

  // Handle object (named params) comparison
  if (targetDef.type === 'object' && candidateDef.type === 'object') {
    const targetShape = targetDef.shape || {};
    const candidateShape = candidateDef.shape || {};

    // All required properties in candidate must exist in target
    for (const key of Object.keys(candidateShape)) {
      if (!(key in targetShape)) {
        return false;
      }
    }
    return true;
  }

  // Mixed comparison - be permissive
  return true;
}

/**
 * Returns a list of function IDs from the registry that are compatible with the target schema.
 * 
 * @param functionRegistry - The available functions
 * @param targetSchema - The function schema expected by the component prop
 * @returns Array of function IDs that are compatible
 */
export function getCompatibleFunctions(
  functionRegistry: FunctionRegistry | undefined,
  targetSchema: z.ZodTypeAny | undefined
): string[] {
  if (!functionRegistry) {
    return [];
  }

  const targetArgsSchema = targetSchema ? extractFunctionArgsSchema(targetSchema) : undefined;

  return Object.entries(functionRegistry)
    .filter(([, definition]) => {
      return areFunctionSignaturesCompatible(targetArgsSchema, definition.schema);
    })
    .map(([id]) => id);
}

/**
 * Returns function-type variables from the provided variables array.
 * 
 * @param variables - Array of all variables
 * @returns Array of variables with type 'function'
 */
export function getFunctionTypeVariables(variables: Variable[]): Variable[] {
  return variables.filter(v => v.type === 'function');
}

/**
 * Gets compatible function-type variables for a given target schema.
 * This combines variable filtering with function compatibility checking.
 * 
 * @param variables - All variables
 * @param functionRegistry - The function registry
 * @param targetSchema - The function schema expected by the component prop
 * @returns Array of function-type variables whose referenced functions are compatible
 */
export function getCompatibleFunctionVariables(
  variables: Variable[],
  functionRegistry: FunctionRegistry | undefined,
  targetSchema: z.ZodTypeAny | undefined
): Variable[] {
  if (!functionRegistry) {
    return [];
  }

  const functionVariables = getFunctionTypeVariables(variables);
  const compatibleFunctionIds = getCompatibleFunctions(functionRegistry, targetSchema);

  return functionVariables.filter(v => {
    // v.defaultValue is the functionRegistry key for function-type variables
    const functionId = v.defaultValue as string;
    return compatibleFunctionIds.includes(functionId);
  });
}


import { z } from 'zod';

/**
 * Helper to get the Zod v4 def type from a schema.
 */
function getDefType(schema: z.ZodType): string {
    return (schema as any)._zod?.def?.type || "";
}

/**
 * Helper to get the def object from a Zod v4 schema.
 */
function getDef(schema: z.ZodType): any {
    return (schema as any)._zod?.def;
}

/**
 * Generates default props based on the provided Zod schema.
 * Supports boolean, date, number, string, enum, objects composed of these primitives, and arrays of these primitives.
 * Logs a warning for unsupported types.
 *
 * @param schema - The Zod schema object.
 * @returns An object containing default values for the schema.
 */
export function getDefaultProps(schema: z.ZodObject<any>): Record<string, any> {
    const shape = schema.shape;
    const defaultProps: Record<string, any> = {};

    for (const key in shape) {
        if (Object.prototype.hasOwnProperty.call(shape, key)) {
            const fieldSchema = shape[key];
            const value = getDefaultValue(fieldSchema, key);
            if(value !== undefined){
                defaultProps[key] = value;
            }
        }
    }

    return defaultProps;
}

/**
 * Determines the default value for a given Zod schema.
 * Handles nullable and coerced fields appropriately.
 *
 * @param schema - The Zod schema for the field.
 * @param fieldName - The name of the field (used for logging).
 * @returns The default value for the field.
 */
function getDefaultValue(schema: z.ZodType, fieldName: string): any {
    const defType = getDefType(schema);
    const def = getDef(schema);

    // Handle ZodDefault to return the specified default value
    if (defType === "default" && def) {
        // In Zod v4, defaultValue can be the value directly or a function
        const defaultValue = def.defaultValue;
        if (typeof defaultValue === "function") {
            return defaultValue();
        }
        return defaultValue;
    }

    if (!schema.isOptional()){
        console.warn(`No default value set for required field "${fieldName}".`);
    }
    return undefined;
}

/**
 * Patches the given Zod object schema by transforming unions of literals to enums,
 * coercing number and date types, and adding an optional `className` property.
 *
 * @param schema - The original Zod object schema to be patched.
 * @returns A new Zod object schema with the specified transformations applied.
 */

export function patchSchema(schema: z.ZodObject<any>): z.ZodObject<any> {
    const schemaWithFixedEnums = transformUnionToEnum(schema);
    const schemaWithCoercedTypes = addCoerceToNumberAndDate(schemaWithFixedEnums);
    const schemaWithCommon = addCommon(schemaWithCoercedTypes);

    return schemaWithCommon;
}

/**
 * Extends the given Zod object schema by adding an optional `className` property.
 *
 * @param schema - The original Zod object schema.
 * @returns A new Zod object schema with the `className` property added.
 */
function addCommon<T extends z.ZodRawShape>(
    schema: z.ZodObject<T>
) {
    return schema.extend({
        className: z.string().optional(),
    });
}

/**
 * Transforms a ZodUnion of ZodLiterals into a ZodEnum with a default value.
 * If the schema is nullable or optional, it recursively applies the transformation to the inner schema.
 *
 * @param schema - The original Zod schema, which can be a ZodUnion, ZodNullable, ZodOptional, or ZodObject.
 * @returns A transformed Zod schema with unions of literals converted to enums, or the original schema if no transformation is needed.
 */
function transformUnionToEnum<T extends z.ZodType>(schema: T): T {
    const defType = getDefType(schema);
    const def = getDef(schema);

    // Handle ZodUnion of string literals
    if (defType === "union" && def?.options) {
        const options = def.options;

        // Check if all options are ZodLiteral instances with string values
        const allStringLiterals = options.every((option: any) => {
            const optDef = getDef(option);
            return optDef?.type === "literal" && typeof optDef?.value === 'string';
        });

        if (allStringLiterals) {
            const enumValues = options.map((option: any) => {
                const optDef = getDef(option);
                return optDef.value;
            }).reverse();

            // Ensure there is at least one value to create an enum
            if (enumValues.length === 0) {
                throw new Error("Cannot create enum with no values.");
            }

            // Create a ZodEnum from the string literals
            const enumSchema = z.enum(enumValues as [string, ...string[]]);

            // Determine if the original schema was nullable or optional
            let transformedSchema: z.ZodType = enumSchema;

            // Apply default before adding modifiers to ensure it doesn't get overridden
            transformedSchema = enumSchema.default(enumValues[0]);


            if (schema.isNullable()) {
                transformedSchema = transformedSchema.nullable();
            }

            if (schema.isOptional()) {
                transformedSchema = transformedSchema.optional();
            }

            return transformedSchema as unknown as T;
        }
    }

    // Recursively handle nullable schemas
    if (defType === "nullable" && def?.innerType) {
        const inner = def.innerType;
        const transformedInner = transformUnionToEnum(inner);
        return transformedInner.nullable() as any;
    }

    // Recursively handle optional schemas
    if (defType === "optional" && def?.innerType) {
        const inner = def.innerType;
        const transformedInner = transformUnionToEnum(inner);
        return transformedInner.optional() as any;
    }

    // Recursively handle ZodObjects by transforming their shape
    if (defType === "object") {
        const shape = (schema as unknown as z.ZodObject<any>).shape;
        const transformedShape: Record<string, z.ZodType> = {};

        for (const [key, value] of Object.entries(shape)) {
            transformedShape[key] = transformUnionToEnum(value as z.ZodType);
        }

        return z.object(transformedShape) as unknown as T;
    }

    // Handle ZodArrays by transforming their element type
    if (defType === "array" && def?.element) {
        const transformedElement = transformUnionToEnum(def.element);
        return z.array(transformedElement) as unknown as T;
    }

    // Handle ZodTuples by transforming each element type
    if (defType === "tuple" && def?.items) {
        const transformedItems = def.items.map((item: any) => transformUnionToEnum(item));
        return z.tuple(transformedItems as [z.ZodType, ...z.ZodType[]]) as unknown as T;
    }

    // If none of the above, return the schema unchanged
    return schema;
}

/**
 * Recursively applies coercion to number and date fields within the given Zod schema.
 * Handles nullable, optional, objects, arrays, unions, and enums appropriately to ensure type safety.
 *
 * @param schema - The original Zod schema to transform.
 * @returns A new Zod schema with coercions applied where necessary.
 */
function addCoerceToNumberAndDate<T extends z.ZodType>(schema: T): T {
    const defType = getDefType(schema);
    const def = getDef(schema);

    // Handle nullable schemas
    if (defType === "nullable" && def?.innerType) {
        const inner = def.innerType;
        return addCoerceToNumberAndDate(inner).nullable() as any;
    }

    // Handle optional schemas
    if (defType === "optional" && def?.innerType) {
        const inner = def.innerType;
        return addCoerceToNumberAndDate(inner).optional() as any;
    }

    // Handle objects by recursively applying the transformation to each property
    if (defType === "object") {
        const shape = (schema as unknown as z.ZodObject<any>).shape;
        const transformedShape: Record<string, z.ZodType> = {};

        for (const [key, value] of Object.entries(shape)) {
            transformedShape[key] = addCoerceToNumberAndDate(value as z.ZodType);
        }

        return z.object(transformedShape) as any;
    }

    // Handle arrays by applying the transformation to the array's element type
    if (defType === "array" && def?.element) {
        const innerType = def.element;
        return z.array(addCoerceToNumberAndDate(innerType)) as any;
    }

    // Apply coercion to number fields (handles number, int, float in Zod v4)
    if (["number", "int", "float"].includes(defType)) {
        return z.coerce.number().optional() as any;
    }

    // Apply coercion to date fields
    if (defType === "date") {
        return z.coerce.date().optional() as any;
    }

    // Handle unions by applying the transformation to each option
    if (defType === "union" && def?.options) {
        const transformedOptions = def.options.map((option: any) => addCoerceToNumberAndDate(option));
        return z.union(transformedOptions as [z.ZodType, z.ZodType, ...z.ZodType[]]) as any;
    }

    // Handle enums by returning them as-is
    if (defType === "enum") {
        return schema;
    }

    // If none of the above, return the schema unchanged
    return schema;
}

// patch for autoform to respect existing values, specifically for enums
export function addDefaultValues<T extends z.ZodObject<any>>(
  schema: T,
  defaultValues: Partial<z.infer<T>>
): T {
  const shape = schema.shape;

  const updatedShape = { ...shape };

  for (const key in defaultValues) {
    if (updatedShape[key]) {
      // Apply the default value to the existing schema field
      updatedShape[key] = updatedShape[key].default(defaultValues[key]);
    } else if (process.env.NODE_ENV !== "production") {
      console.warn(
        `Key "${key}" does not exist in the schema and will be ignored.`
      );
    }
  }

  return z.object(updatedShape) as T;
}

/**
 * Checks if a Zod schema has a children field of type ANY
 */
export function hasAnyChildrenField(schema: z.ZodObject<any>): boolean {
    const shape = schema.shape;
    if (!shape.children) {
        return false;
    }

    // Unwrap optional and nullable wrappers to get the inner type
    let childrenSchema = shape.children;
    let childDefType = getDefType(childrenSchema);

    while (childDefType === "optional" || childDefType === "nullable") {
        const childDef = getDef(childrenSchema);
        if (childDef?.innerType) {
            childrenSchema = childDef.innerType;
            childDefType = getDefType(childrenSchema);
        } else {
            break;
        }
    }

    return childDefType === "any";
}

/**
* Checks if a Zod schema has a children field of type String
*/
export function hasChildrenFieldOfTypeString(schema: z.ZodObject<any>): boolean {
    const shape = schema.shape;
    if (!shape.children) {
        return false;
    }

    // Unwrap optional and nullable wrappers to get the inner type
    let childrenSchema = shape.children;
    let childDefType = getDefType(childrenSchema);

    while (childDefType === "optional" || childDefType === "nullable") {
        const childDef = getDef(childrenSchema);
        if (childDef?.innerType) {
            childrenSchema = childDef.innerType;
            childDefType = getDefType(childrenSchema);
        } else {
            break;
        }
    }

    return childDefType === "string";
}

import type { LayerStore } from "@/lib/ui-builder/store/layer-store";
import type { ComponentLayer, ComponentRegistry } from '@/components/ui/ui-builder/types';
import { getDefaultProps } from '@/lib/ui-builder/store/schema-utils';
import { TAILWIND_V4_COLOR_KEYS } from '@/components/ui/ui-builder/internal/utils/base-colors';

/**
 * Recursively visits each layer in the layer tree and applies the provided visitor function to each layer.
 * The visitor function can modify the layer and its children as needed.
 *
 * @param layer - The current layer to visit.
 * @param visitor - A function that takes a layer and returns a modified layer.
 * @returns The modified layer after applying the visitor function.
 */
export const visitLayer = (layer: ComponentLayer, parentLayer: ComponentLayer | null, visitor: (layer: ComponentLayer, parentLayer: ComponentLayer | null) => ComponentLayer): ComponentLayer => {
    // Apply the visitor to the current layer
    const updatedLayer = visitor(layer, parentLayer);

    // Recursively traverse and update children if they exist
    if (hasLayerChildren(updatedLayer)) {
        const updatedChildren = updatedLayer.children.map((child) =>
            visitLayer(child, updatedLayer, visitor)
        );
        return { ...updatedLayer, children: updatedChildren };
    }

    return updatedLayer;
};

export const countLayers = (layers: ComponentLayer['children']): number => {
    if (typeof layers === 'string' || !Array.isArray(layers)) {
        // String or VariableReference
        return 0;
    }
    return layers.reduce((count, layer) => {
        if (hasLayerChildren(layer)) {
            return count + 1 + countLayers(layer.children);
        }
        return count + 1;
    }, 0);
};

export const addLayer = (layers: ComponentLayer[], newLayer: ComponentLayer, parentId?: string, parentPosition?: number): ComponentLayer[] => {
    const updatedPages = layers.map((page) =>
        visitLayer(page, null, (layer) => {
            if (layer.id === parentId) {
                // Handle both layers with existing children and those with undefined/null children
                let updatedChildren: ComponentLayer[] = [];

                if (hasLayerChildren(layer)) {
                    updatedChildren = [...layer.children];
                } else if (layer.children === undefined || layer.children === null || (Array.isArray(layer.children) && layer.children.length === 0)) {
                    // Initialize children array for layers with undefined/null children or empty arrays
                    updatedChildren = [];
                } else {
                    // For layers with string children or other non-array types, we can't add children
                    return layer;
                }

                if (parentPosition !== undefined) {
                    if (parentPosition < 0) {
                        // If parentPosition is negative, insert at the beginning
                        updatedChildren = [newLayer, ...updatedChildren];
                    } else if (parentPosition >= updatedChildren.length) {
                        // If parentPosition is greater than or equal to the length, append to the end
                        updatedChildren = [...updatedChildren, newLayer];
                    } else {
                        // Insert at the specified position
                        updatedChildren = [
                            ...updatedChildren.slice(0, parentPosition),
                            newLayer,
                            ...updatedChildren.slice(parentPosition)
                        ];
                    }
                } else {
                    // If parentPosition is undefined, append to the end
                    updatedChildren = [...updatedChildren, newLayer];
                }

                return { ...layer, children: updatedChildren };
            }

            return layer;
        })
    );
    return updatedPages;
}

export const findAllParentLayersRecursive = (layers: ComponentLayer[], layerId: string): ComponentLayer[] => {
    const parents: ComponentLayer[] = [];

    const findParents = (layers: ComponentLayer[], targetId: string): boolean => {
        for (const layer of layers) {
            if (hasLayerChildren(layer)) {
                if (layer.children.some(child => child.id === targetId)) {
                    parents.push(layer);
                    // Continue searching upwards
                    findParents(layers, layer.id);
                    return true;
                }

                if (findParents(layer.children, targetId)) {
                    parents.push(layer);
                    return true;
                }
            }
        }
        return false;
    };

    findParents(layers, layerId);
    return parents;
};

export const findLayerRecursive = (layers: ComponentLayer[], layerId: string): ComponentLayer | undefined => {
    for (const layer of layers) {
        if (layer.id === layerId) {
            return layer;
        }
        if (hasLayerChildren(layer)) {
            const foundInChildren = findLayerRecursive(layer.children, layerId);
            if (foundInChildren) {
                return foundInChildren;
            }
        }
    }
    return undefined;
};

export const duplicateWithNewIdsAndName = (layer: ComponentLayer, addCopySuffix: boolean = true): ComponentLayer => {
    const newLayer: ComponentLayer = { ...layer, id: createId() };
    if (layer.name) {
      newLayer.name = `${ layer.name }${ addCopySuffix ? ' (Copy)' : ''}`;
    }
    if (hasLayerChildren(newLayer) && hasLayerChildren(layer)) {
      newLayer.children = layer.children.map(child => duplicateWithNewIdsAndName(child, false));
    }
    return newLayer;
  };


export function createId(): string {
    const ALPHABET = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
    const ID_LENGTH = 7;
    let result = '';
    const alphabetLength = ALPHABET.length;

    for (let i = 0; i < ID_LENGTH; i++) {
        const randomIndex = Math.floor(Math.random() * alphabetLength);
        result += ALPHABET.charAt(randomIndex);
    }

    return result;
}

/**
 * Checks if a layer has ComponentLayer[] children (not string or VariableReference)
 * Note: VariableReference is an object (not an array), so Array.isArray returns false for it
 */
export const hasLayerChildren = (layer: ComponentLayer): layer is ComponentLayer & { children: ComponentLayer[] } => {
    return Array.isArray(layer.children) && typeof layer.children !== 'string';
};

export function migrateV1ToV2(persistedState: unknown): LayerStore {
