import type { ComponentType as ReactComponentType } from "react";
import type { ComponentRegistry, ComponentLayer, RegistryEntry } from '@/components/ui/ui-builder/types';
import type { FieldConfigItem } from "@/components/ui/auto-form/types";

// Cache for field overrides to avoid regenerating them
const fieldOverrideCache = new Map<string, Record<string, FieldConfigItem>>();

// Helper to create a cache key for a layer
const createCacheKey = (layer: ComponentLayer): string => {
  // Include layer type, id, and a hash of props to detect changes
  const propsHash = JSON.stringify(layer.props);
  return `${layer.type}-${layer.id}-${propsHash}`;
};

export const generateFieldOverrides = (registry: ComponentRegistry, layer: ComponentLayer): Record<string, FieldConfigItem> => {
    const componentDefinition = registry[layer.type];
    if (!componentDefinition) {
        return {};
    }

    if (!componentDefinition.fieldOverrides) {
        return {};
    }

    // Create cache key for this layer
    const cacheKey = createCacheKey(layer);

    // Check if we have cached overrides for this exact layer state
    if (fieldOverrideCache.has(cacheKey)) {
        return fieldOverrideCache.get(cacheKey)!;
    }

    // Generate field overrides
    const fieldOverrides: Record<string, FieldConfigItem> = {};
    Object.keys(componentDefinition.fieldOverrides).forEach(key => {
        const override = componentDefinition.fieldOverrides?.[key];
        if (override) {
            fieldOverrides[key] = override(layer);
        }
    });

    // Cache the result
    fieldOverrideCache.set(cacheKey, fieldOverrides);

    // Clean up old cache entries to prevent memory leaks (keep last 100 entries)
    if (fieldOverrideCache.size > 100) {
        const firstKey = fieldOverrideCache.keys().next().value;
        if (firstKey) {
            fieldOverrideCache.delete(firstKey);
        }
    }

    return fieldOverrides;
}

//Checking of component type, checked via from property, if undefined or null then its a primitive like <div/>, <img/>, etc
export function isPrimitiveComponent(component: RegistryEntry<ReactComponentType<any>>): boolean {
    return component.from === undefined || component.from === null;
}

//Checking of component type, checked via from property, if defined and not null then its a complex component like <Button/>, <Badge/>, etc
export function isCustomComponent(component: RegistryEntry<ReactComponentType<any>>): boolean {
    return component.from !== undefined && component.from !== null;
}

import { create, type StateCreator } from 'zustand';
import type { ComponentType as ReactComponentType } from "react";
import type { RegistryEntry, ComponentRegistry, BlockRegistry, FunctionRegistry, FunctionDefinition, ComponentLayer } from '@/components/ui/ui-builder/types';

/**
 * Clipboard state for copy/cut/paste operations
 */
export interface ClipboardState {
  layer: ComponentLayer | null;
  isCut: boolean;
  sourceLayerId: string | null;
}



export interface EditorStore {
    previewMode: 'mobile' | 'tablet' | 'desktop' | 'responsive';
    setPreviewMode: (mode: 'mobile' | 'tablet' | 'desktop' | 'responsive') => void;

    registry: ComponentRegistry;
    blocks: BlockRegistry | undefined;
    functionRegistry: FunctionRegistry | undefined;

    initialize: (registry: ComponentRegistry, persistLayerStoreConfig: boolean, allowPagesCreation: boolean, allowPagesDeletion: boolean, allowVariableEditing: boolean, blocks?: BlockRegistry, functionRegistry?: FunctionRegistry) => void;
    getComponentDefinition: (type: string) => RegistryEntry<ReactComponentType<any>> | undefined;
    getFunctionDefinition: (id: string) => FunctionDefinition | undefined;

    persistLayerStoreConfig: boolean;
    setPersistLayerStoreConfig: (shouldPersist: boolean) => void;

    // Revision counter to track state changes for form revalidation
    revisionCounter: number;
    incrementRevision: () => void;

    allowPagesCreation: boolean;
    setAllowPagesCreation: (allow: boolean) => void;
    allowPagesDeletion: boolean;
    setAllowPagesDeletion: (allow: boolean) => void;
    allowVariableEditing: boolean;
    setAllowVariableEditing: (allow: boolean) => void;

    // Panel visibility state
    showLeftPanel: boolean;
    setShowLeftPanel: (show: boolean) => void;
    showRightPanel: boolean;
    setShowRightPanel: (show: boolean) => void;

    // Clipboard state for copy/cut/paste
    clipboard: ClipboardState;
    setClipboard: (clipboard: ClipboardState) => void;
    clearClipboard: () => void;
}

const store: StateCreator<EditorStore, [], []> = (set, get) => ({
    previewMode: 'responsive',
    setPreviewMode: (mode) => set({ previewMode: mode }),

    registry: {},
    blocks: undefined,
    functionRegistry: undefined,

    initialize: (registry, persistLayerStoreConfig, allowPagesCreation, allowPagesDeletion, allowVariableEditing, blocks, functionRegistry) => {
        set(state => ({ ...state, registry, persistLayerStoreConfig, allowPagesCreation, allowPagesDeletion, allowVariableEditing, blocks, functionRegistry }));
    },
    getComponentDefinition: (type: string) => {
        const { registry } = get();
        if (!registry) {
            console.warn("Registry accessed via editor store before initialization.");
            return undefined;
        }
        return registry[type];
    },
    getFunctionDefinition: (id: string) => {
        const { functionRegistry } = get();
        if (!functionRegistry) {
            return undefined;
        }
        return functionRegistry[id];
    },

    persistLayerStoreConfig: true,
    setPersistLayerStoreConfig: (shouldPersist) => set({ persistLayerStoreConfig: shouldPersist }),

    revisionCounter: 0,
    incrementRevision: () => set(state => ({ revisionCounter: state.revisionCounter + 1 })),

    allowPagesCreation: true,
    setAllowPagesCreation: (allow) => set({ allowPagesCreation: allow }),
    allowPagesDeletion: true,
    setAllowPagesDeletion: (allow) => set({ allowPagesDeletion: allow }),
    allowVariableEditing: true,
    setAllowVariableEditing: (allow) => set({ allowVariableEditing: allow }),

    // Panel visibility state
    showLeftPanel: true,
    setShowLeftPanel: (show) => set({ showLeftPanel: show }),
    showRightPanel: true,
    setShowRightPanel: (show) => set({ showRightPanel: show }),

    // Clipboard state for copy/cut/paste
    clipboard: {
        layer: null,
        isCut: false,
        sourceLayerId: null,
    },
    setClipboard: (clipboard) => set({ clipboard }),
    clearClipboard: () => set({ 
        clipboard: { 
            layer: null, 
            isCut: false, 
            sourceLayerId: null 
        } 
    }),
});

export const useEditorStore = create<EditorStore>()(store);

/**
 * Unified keyboard shortcut registry for the UI Builder.
 * Centralizes all shortcut definitions for consistency across menus and keyboard handlers.
 */

export interface ShortcutKeys {
  metaKey?: boolean;
  shiftKey?: boolean;
  ctrlKey?: boolean;
  altKey?: boolean;
}

export interface ShortcutDefinition {
  id: string;
  keys: ShortcutKeys;
  key: string;
  label: string;
  shortcutDisplay: string;
  category: 'edit' | 'navigation' | 'view';
}

/**
 * All keyboard shortcuts used in the UI Builder.
 * These definitions are used for:
 * - Registering keyboard event handlers
 * - Displaying shortcut hints in menus
 * - Consistent shortcut behavior across the application
 */
export const SHORTCUTS = {
  delete: {
    id: 'delete',
    keys: {},
    key: 'Backspace',
    label: 'Delete',
    shortcutDisplay: '',
    category: 'edit',
  },
  duplicate: {
    id: 'duplicate',
    keys: { metaKey: true },
    key: 'd',
    label: 'Duplicate',
    shortcutDisplay: 'D',
    category: 'edit',
  },
  copy: {
    id: 'copy',
    keys: { metaKey: true },
    key: 'c',
    label: 'Copy',
    shortcutDisplay: 'C',
    category: 'edit',
  },
  cut: {
    id: 'cut',
    keys: { metaKey: true },
    key: 'x',
    label: 'Cut',
    shortcutDisplay: 'X',
    category: 'edit',
  },
  paste: {
    id: 'paste',
    keys: { metaKey: true },
    key: 'v',
    label: 'Paste',
    shortcutDisplay: 'V',
    category: 'edit',
  },
  undo: {
    id: 'undo',
    keys: { metaKey: true },
    key: 'z',
    label: 'Undo',
    shortcutDisplay: 'Z',
    category: 'edit',
  },
  redo: {
    id: 'redo',
    keys: { metaKey: true, shiftKey: true },
    key: 'z',
    label: 'Redo',
    shortcutDisplay: 'Z',
    category: 'edit',
  },
} as const satisfies Record<string, ShortcutDefinition>;

export type ShortcutId = keyof typeof SHORTCUTS;

/**
 * Get the display string for a shortcut.
 * Returns the human-readable shortcut display (e.g., "C" for copy).
 */
export function getShortcutDisplay(shortcutId: ShortcutId): string {
  return SHORTCUTS[shortcutId].shortcutDisplay;
}

/**
 * Get a shortcut definition by ID.
 */
export function getShortcut(shortcutId: ShortcutId): ShortcutDefinition {
  return SHORTCUTS[shortcutId];
}

/**
 * Check if a keyboard event matches a shortcut definition.
 */
export function matchesShortcut(
  event: KeyboardEvent,
  shortcutId: ShortcutId
): boolean {
  const shortcut = SHORTCUTS[shortcutId];
  const keys = shortcut.keys as ShortcutKeys;
  const key = shortcut.key;

  const metaKeyMatch = keys.metaKey === undefined || event.metaKey === keys.metaKey;
  const shiftKeyMatch = keys.shiftKey === undefined || event.shiftKey === keys.shiftKey;
  const ctrlKeyMatch = keys.ctrlKey === undefined || event.ctrlKey === keys.ctrlKey;
  const altKeyMatch = keys.altKey === undefined || event.altKey === keys.altKey;
  const keyMatch = event.key.toLowerCase() === key.toLowerCase();

  return metaKeyMatch && shiftKeyMatch && ctrlKeyMatch && altKeyMatch && keyMatch;
}

/**
 * Convert a shortcut definition to the format expected by useKeyboardShortcuts hook.
 */
export function toKeyboardShortcut(
  shortcutId: ShortcutId,
  handler: (event: KeyboardEvent) => void
) {
  const shortcut = SHORTCUTS[shortcutId];
  return {
    keys: shortcut.keys,
    key: shortcut.key,
    handler,
  };
}

