/**
 * Hook for layer actions (copy, cut, paste, delete, duplicate).
 * Centralizes layer operations with validation and clipboard support.
 * Uses the editor store for global clipboard state.
 */

import { useCallback } from 'react';
import { useLayerStore } from '@/lib/ui-builder/store/layer-store';
import { useEditorStore, type ClipboardState } from '@/lib/ui-builder/store/editor-store';
import { duplicateWithNewIdsAndName } from '@/lib/ui-builder/store/layer-utils';
import { canPasteLayer } from '@/lib/ui-builder/utils/paste-validation';

export type { ClipboardState } from '@/lib/ui-builder/store/editor-store';

export interface LayerActionsState {
  clipboard: ClipboardState;
  canPaste: boolean;
}

export interface LayerActionsHandlers {
  handleCopy: () => void;
  handleCut: () => void;
  handlePaste: () => void;
  handleDelete: () => void;
  handleDuplicate: () => void;
  canPerformPaste: (targetLayerId: string) => boolean;
}

export interface UseLayerActionsResult extends LayerActionsState, LayerActionsHandlers {}

/**
 * Hook that provides layer actions with clipboard support.
 * Uses the global clipboard from editor store for cross-component copy/paste.
 * 
 * @param layerId - The ID of the layer to operate on (optional, defaults to selected layer)
 * @returns Object with clipboard state and action handlers
 */
export function useGlobalLayerActions(layerId?: string): UseLayerActionsResult {
  // Layer store
  const selectedLayerId = useLayerStore((state) => state.selectedLayerId);
  const findLayerById = useLayerStore((state) => state.findLayerById);
  const removeLayer = useLayerStore((state) => state.removeLayer);
  const duplicateLayer = useLayerStore((state) => state.duplicateLayer);
  const addLayerDirect = useLayerStore((state) => state.addLayerDirect);
  const isLayerAPage = useLayerStore((state) => state.isLayerAPage);

  // Editor store - including clipboard
  const componentRegistry = useEditorStore((state) => state.registry);
  const allowPagesCreation = useEditorStore((state) => state.allowPagesCreation);
  const allowPagesDeletion = useEditorStore((state) => state.allowPagesDeletion);
  const clipboard = useEditorStore((state) => state.clipboard);
  const setClipboard = useEditorStore((state) => state.setClipboard);
  const clearClipboard = useEditorStore((state) => state.clearClipboard);

  // Get the effective layer ID (passed in or selected)
  const effectiveLayerId = layerId ?? selectedLayerId;

  /**
   * Copy the layer to clipboard
   */
  const handleCopy = useCallback(() => {
    if (!effectiveLayerId) return;

    const layer = findLayerById(effectiveLayerId);
    if (!layer) return;

    // Deep clone the layer with new IDs prepared
    const clonedLayer = duplicateWithNewIdsAndName(layer, false);

    setClipboard({
      layer: clonedLayer,
      isCut: false,
      sourceLayerId: effectiveLayerId,
    });
  }, [effectiveLayerId, findLayerById, setClipboard]);

  /**
   * Cut the layer (copy to clipboard and delete)
   */
  const handleCut = useCallback(() => {
    if (!effectiveLayerId) return;

    const layer = findLayerById(effectiveLayerId);
    if (!layer) return;

    // Check if we can delete this layer (for pages, check permissions)
    const isPage = isLayerAPage(effectiveLayerId);
    if (isPage && !allowPagesDeletion) return;

    // Deep clone the layer with new IDs prepared
    const clonedLayer = duplicateWithNewIdsAndName(layer, false);

    setClipboard({
      layer: clonedLayer,
      isCut: true,
      sourceLayerId: effectiveLayerId,
    });

    // Delete the original layer
    removeLayer(effectiveLayerId);
  }, [effectiveLayerId, findLayerById, isLayerAPage, allowPagesDeletion, removeLayer, setClipboard]);

  /**
   * Paste the clipboard layer into the selected layer
   */
  const handlePaste = useCallback(() => {
    if (!clipboard.layer || !effectiveLayerId) return;

    // Validate paste operation
    if (!canPasteLayer(clipboard.layer, effectiveLayerId, componentRegistry, findLayerById)) {
      return;
    }

    // Create a new copy with fresh IDs
    const layerToAdd = duplicateWithNewIdsAndName(clipboard.layer, false);

    // Add the layer to the target
    addLayerDirect(layerToAdd, effectiveLayerId);

    // If this was a cut operation, clear the clipboard
    if (clipboard.isCut) {
      clearClipboard();
    }
  }, [clipboard, effectiveLayerId, componentRegistry, findLayerById, addLayerDirect, clearClipboard]);

  /**
   * Delete the layer
   */
  const handleDelete = useCallback(() => {
    if (!effectiveLayerId) return;

    // Check if we can delete this layer (for pages, check permissions)
    const isPage = isLayerAPage(effectiveLayerId);
    if (isPage && !allowPagesDeletion) return;

    removeLayer(effectiveLayerId);
  }, [effectiveLayerId, isLayerAPage, allowPagesDeletion, removeLayer]);

  /**
   * Duplicate the layer
   */
  const handleDuplicate = useCallback(() => {
    if (!effectiveLayerId) return;

    // Check if we can duplicate this layer (for pages, check permissions)
    const isPage = isLayerAPage(effectiveLayerId);
    if (isPage && !allowPagesCreation) return;

    duplicateLayer(effectiveLayerId);
  }, [effectiveLayerId, isLayerAPage, allowPagesCreation, duplicateLayer]);

  /**
   * Check if paste operation is valid for a target layer
   */
  const canPerformPaste = useCallback(
    (targetLayerId: string): boolean => {
      if (!clipboard.layer) return false;
      return canPasteLayer(clipboard.layer, targetLayerId, componentRegistry, findLayerById);
    },
    [clipboard.layer, componentRegistry, findLayerById]
  );

  // Compute whether paste is currently possible
  const canPaste = effectiveLayerId
    ? canPerformPaste(effectiveLayerId)
    : false;

  return {
    clipboard,
    canPaste,
    handleCopy,
    handleCut,
    handlePaste,
    handleDelete,
    handleDuplicate,
    canPerformPaste,
  };
}

import { useEffect } from 'react';

export const useKeyboardShortcutsDnd = (activeLayerId: string | null, handleDragCancel: () => void) => {
  // Handle escape key to cancel drag operations
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.key === 'Escape' && activeLayerId) {
        event.preventDefault();
        event.stopPropagation();
        handleDragCancel();
      }
    };

    // Only add the listener when actively dragging
    if (activeLayerId) {
      document.addEventListener('keydown', handleKeyDown);
      return () => {
        document.removeEventListener('keydown', handleKeyDown);
      };
    }
  }, [activeLayerId, handleDragCancel]);
};

import { useCallback } from 'react';
import { useLayerStore } from '@/lib/ui-builder/store/layer-store';
import { useEditorStore } from '@/lib/ui-builder/store/editor-store';
import { canLayerAcceptChildren } from '@/lib/ui-builder/store/layer-utils';

export const useDropValidation = (
  activeLayerId: string | null, 
  isLayerDescendantOf: (childId: string, parentId: string) => boolean,
  newComponentType?: string | null
) => {
  const findLayerById = useLayerStore((state) => state.findLayerById);
  const componentRegistry = useEditorStore((state) => state.registry);

  const canDropOnLayer = useCallback((layerId: string): boolean => {
    // Safety check for layerId
    if (!layerId) return false;

    const targetLayer = findLayerById(layerId);
    if (!targetLayer) return false;

    // If no active drag (neither existing layer nor new component), check if layer can accept children
    if (!activeLayerId && !newComponentType) {
      return canLayerAcceptChildren(targetLayer, componentRegistry);
    }

    // Handle new component drag from popover
    if (newComponentType) {
      const componentDef = componentRegistry[newComponentType];

      // Check childOf constraint for new component
      if (componentDef?.childOf && !componentDef.childOf.includes(targetLayer.type)) {
        return false;
      }

      return canLayerAcceptChildren(targetLayer, componentRegistry);
    }

    // Handle existing layer drag
    if (activeLayerId) {
      // Don't allow dropping onto self or descendants
      if (isLayerDescendantOf(layerId, activeLayerId)) {
        return false;
      }

      const draggedLayer = findLayerById(activeLayerId);
      // If we can't find the dragged layer, still allow the drop to proceed
      // This handles edge cases during state transitions
      if (!draggedLayer) {
        return canLayerAcceptChildren(targetLayer, componentRegistry);
      }

      // Check childOf constraint: if the dragged layer has a childOf constraint,
      // only allow dropping onto valid parent types
      const draggedDef = componentRegistry[draggedLayer.type];
      if (draggedDef?.childOf && !draggedDef.childOf.includes(targetLayer.type)) {
        return false;
      }
    }

    return canLayerAcceptChildren(targetLayer, componentRegistry);
  }, [activeLayerId, newComponentType, isLayerDescendantOf, findLayerById, componentRegistry]);

  return { canDropOnLayer };
};

import { useSensors, useSensor, MouseSensor, TouchSensor } from '@dnd-kit/core';

export const useDndSensors = () => {
  return useSensors(
    useSensor(MouseSensor, {
      activationConstraint: {
        distance: 4,
      },
    }),
    useSensor(TouchSensor, {
      activationConstraint: {
        delay: 100,
        tolerance: 8,
      },
    })
  );
};

import { useCallback } from 'react';
import { type DragStartEvent, type DragEndEvent } from '@dnd-kit/core';
import { useLayerStore } from '@/lib/ui-builder/store/layer-store';
import { findAllParentLayersRecursive } from '@/lib/ui-builder/store/layer-utils';

interface UseDndEventHandlersProps {
  stopAutoScroll: () => void;
  setActiveLayerId: (layerId: string | null) => void;
  setNewComponentType?: (componentType: string | null) => void;
  clearDragState?: () => void;
  canDropOnLayer?: (layerId: string) => boolean;
}

export const useDndEventHandlers = ({ 
  stopAutoScroll, 
  setActiveLayerId,
  setNewComponentType,
  clearDragState,
  canDropOnLayer,
}: UseDndEventHandlersProps) => {
  const moveLayer = useLayerStore((state) => state.moveLayer);
  const addComponentLayer = useLayerStore((state) => state.addComponentLayer);
  const pages = useLayerStore((state) => state.pages);

  // Helper function to check if a layer is a descendant of another layer
  const isLayerDescendantOf = useCallback((childId: string, parentId: string): boolean => {
    if (childId === parentId) return true; // A layer is considered its own descendant for drop prevention
    const parentLayers = findAllParentLayersRecursive(pages, childId);
    return parentLayers.some(parent => parent.id === parentId);
  }, [pages]);

  const handleDragStart = useCallback((event: DragStartEvent) => {
    const { active } = event;
    if (active.data.current?.type === 'layer') {
      setActiveLayerId(active.data.current.layerId);
    } else if (active.data.current?.type === 'new-component') {
      // Handle new component drag from popover
      const componentType = active.data.current.componentType;
      if (componentType && setNewComponentType) {
        setNewComponentType(componentType);
      }
    }
  }, [setActiveLayerId, setNewComponentType]);

  const handleDragEnd = useCallback((event: DragEndEvent) => {
    const { active, over } = event;

    // Stop auto-scroll immediately
    stopAutoScroll();

    const activeData = active.data.current;
    const overData = over?.data.current;

    // Handle case where there's no valid drop target
    if (!over || !overData || overData.type !== 'drop-zone') {
      if (clearDragState) {
        clearDragState();
      } else {
        setActiveLayerId(null);
      }
      return;
    }

    const targetParentId = overData.parentId;
    const targetPosition = overData.position;

    // Validate drop is allowed (childOf constraints, etc.)
    if (canDropOnLayer && !canDropOnLayer(targetParentId)) {
      if (clearDragState) {
        clearDragState();
      } else {
        setActiveLayerId(null);
      }
      return;
    }

    // Handle existing layer drag
    if (activeData?.type === 'layer' && activeData.layerId) {
      const activeLayerId = activeData.layerId;

      // Don't allow dropping a layer onto itself or its descendants
      if (isLayerDescendantOf(targetParentId, activeLayerId)) {
        if (clearDragState) {
          clearDragState();
        } else {
          setActiveLayerId(null);
        }
        return;
      }

      moveLayer(activeLayerId, targetParentId, targetPosition);
    } 
    // Handle new component drag from popover
    else if (activeData?.type === 'new-component' && activeData.componentType) {
      addComponentLayer(activeData.componentType, targetParentId, targetPosition);
    }

    if (clearDragState) {
      clearDragState();
    } else {
      setActiveLayerId(null);
    }
  }, [moveLayer, addComponentLayer, isLayerDescendantOf, stopAutoScroll, setActiveLayerId, clearDragState, canDropOnLayer]);

  const handleDragCancel = useCallback(() => {
    stopAutoScroll();
    if (clearDragState) {
      clearDragState();
    } else {
      setActiveLayerId(null);
    }
  }, [stopAutoScroll, setActiveLayerId, clearDragState]);

  return {
    handleDragStart,
    handleDragEnd,
    handleDragCancel,
    isLayerDescendantOf,
  };
};

import { useCallback, useRef } from 'react';
import { getIframeElements } from '@/lib/ui-builder/context/dnd-utils';
import { 
  type AutoScrollState, 
  AUTO_SCROLL_THRESHOLD, 
  calculateScrollSpeed 
} from '../context/auto-scroll-constants';

export const useAutoScroll = () => {
  // Auto-scroll state management
  const autoScrollStateRef = useRef<AutoScrollState>({
    isScrolling: false,
    directions: { left: false, right: false, top: false, bottom: false },
    speeds: { horizontal: 0, vertical: 0 },
  });

  const mousePositionRef = useRef<{ x: number; y: number } | null>(null);
  const animationFrameRef = useRef<number | null>(null);

  // Auto-scroll logic
  const performAutoScroll = useCallback(() => {
    const iframeElements = getIframeElements();
    if (!iframeElements || !mousePositionRef.current) {
      return;
    }

    const { iframe, window: iframeWindow } = iframeElements;

    // Get iframe bounds in viewport coordinates
    const iframeRect = iframe.getBoundingClientRect();

    // Convert mouse position to iframe-relative coordinates (without transform)
    const iframeMouseX = mousePositionRef.current.x - iframeRect.left;
    const iframeMouseY = mousePositionRef.current.y - iframeRect.top;

    // The scrollable area dimensions are the iframe dimensions
    const scrollableWidth = iframeRect.width;
    const scrollableHeight = iframeRect.height;

    const distanceFromLeft = iframeMouseX;
    const distanceFromRight = scrollableWidth - iframeMouseX;
    const distanceFromTop = iframeMouseY;
    const distanceFromBottom = scrollableHeight - iframeMouseY;

    let shouldScrollLeft = distanceFromLeft < AUTO_SCROLL_THRESHOLD;
    let shouldScrollRight = distanceFromRight < AUTO_SCROLL_THRESHOLD;
    let shouldScrollUp = distanceFromTop < AUTO_SCROLL_THRESHOLD;
    let shouldScrollDown = distanceFromBottom < AUTO_SCROLL_THRESHOLD;

    if (shouldScrollLeft && shouldScrollRight) {
      if (distanceFromLeft < distanceFromRight) {
        shouldScrollRight = false;
      } else {
        shouldScrollLeft = false;
      }
    }

    if (shouldScrollUp && shouldScrollDown) {
      if (distanceFromTop < distanceFromBottom) {
        shouldScrollDown = false;
      } else {
        shouldScrollUp = false;
      }
    }

    // Calculate scroll speeds
    const leftSpeed = shouldScrollLeft ? calculateScrollSpeed(Math.max(0, distanceFromLeft)) : 0;
    const rightSpeed = shouldScrollRight ? calculateScrollSpeed(Math.max(0, distanceFromRight)) : 0;
    const upSpeed = shouldScrollUp ? calculateScrollSpeed(Math.max(0, distanceFromTop)) : 0;
    const downSpeed = shouldScrollDown ? calculateScrollSpeed(Math.max(0, distanceFromBottom)) : 0;

    // Update auto-scroll state
    const state = autoScrollStateRef.current;
    state.directions = {
      left: shouldScrollLeft,
      right: shouldScrollRight,
      top: shouldScrollUp,
      bottom: shouldScrollDown,
    };
    state.speeds = {
      horizontal: leftSpeed || rightSpeed,
      vertical: upSpeed || downSpeed,
    };
    state.isScrolling = shouldScrollLeft || shouldScrollRight || shouldScrollUp || shouldScrollDown;

    // Perform the actual scrolling
    if (state.isScrolling) {
      let scrollX = 0;
      let scrollY = 0;

      if (shouldScrollLeft) scrollX = -leftSpeed;
      else if (shouldScrollRight) scrollX = rightSpeed;

      if (shouldScrollUp) scrollY = -upSpeed;
      else if (shouldScrollDown) scrollY = downSpeed;

      // Apply scroll to iframe content
      if (scrollX !== 0 || scrollY !== 0) {
        try {
          iframeWindow.scrollBy(scrollX, scrollY);
        } catch (error) {
          console.warn('Auto-scroll failed:', error);
        }
      }

      // Continue scrolling
      animationFrameRef.current = requestAnimationFrame(performAutoScroll);
    } else {
      // Stop scrolling
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = null;
      }
    }
  }, []);

  // Mouse move handler for auto-scroll (parent document)
  // Accepts isDragging boolean to support both existing layer drags and new component drags
  const handleParentMouseMove = useCallback((event: MouseEvent, isDragging: boolean) => {
    if (!isDragging) return;

    mousePositionRef.current = { x: event.clientX, y: event.clientY };

    // Start auto-scroll if not already running
    if (!animationFrameRef.current) {
      animationFrameRef.current = requestAnimationFrame(performAutoScroll);
    }
  }, [performAutoScroll]);

  // Mouse move handler for auto-scroll (iframe content)
  // Accepts isDragging boolean to support both existing layer drags and new component drags
  const handleIframeMouseMove = useCallback((event: MouseEvent, isDragging: boolean) => {
    if (!isDragging) return;

    const iframeElements = getIframeElements();
    if (!iframeElements) return;

    const { iframe } = iframeElements;
    const iframeRect = iframe.getBoundingClientRect();

    // Convert iframe-relative mouse position to parent document coordinates
    const parentX = event.clientX + iframeRect.left;
    const parentY = event.clientY + iframeRect.top;

    mousePositionRef.current = { x: parentX, y: parentY };

    // Start auto-scroll if not already running
    if (!animationFrameRef.current) {
      animationFrameRef.current = requestAnimationFrame(performAutoScroll);
    }
  }, [performAutoScroll]);

  // Stop auto-scroll function
  const stopAutoScroll = useCallback(() => {
    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
      animationFrameRef.current = null;
    }

    autoScrollStateRef.current = {
      isScrolling: false,
      directions: { left: false, right: false, top: false, bottom: false },
      speeds: { horizontal: 0, vertical: 0 },
    };

    mousePositionRef.current = null;
  }, []);

  return {
    handleParentMouseMove,
    handleIframeMouseMove,
    stopAutoScroll,
    autoScrollState: autoScrollStateRef.current,
  };
};

import React, { useState, useEffect, useMemo } from 'react';
import { createPortal } from 'react-dom';
import { DragOverlay } from '@dnd-kit/core';
import { useLayerStore } from '@/lib/ui-builder/store/layer-store';

// Custom DragOverlay that renders inside the transform container
export const TransformAwareDragOverlay: React.FC<{ 
  children: React.ReactNode;
}> = ({ children }) => {
  const [mountNode, setMountNode] = useState<HTMLElement | null>(null);

  // Create a positioned overlay that follows the mouse within the transform context
  const overlayStyle = useMemo(() => ({
    position: 'fixed' as const,
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    pointerEvents: 'none' as const,
    zIndex: 9999,
  }), []);

  useEffect(() => {
    // Try to find the transform component container where we want to render the overlay
    const transformComponent = document.querySelector('[data-testid="transform-component"]') as HTMLElement;
    if (transformComponent) {
      setMountNode(transformComponent);
    } else {
      // Fallback to editor panel container
      const editorContainer = document.getElementById('editor-panel-content');
      setMountNode(editorContainer);
    }
  }, []);

  // Render the overlay inside the transformed container using a portal
  if (!mountNode) {
    return (
      <DragOverlay dropAnimation={null}>
        {children}
      </DragOverlay>
    );
  }

  return createPortal(
    <div style={overlayStyle}>
      <DragOverlay dropAnimation={null}>
        {children}
      </DragOverlay>
    </div>,
    mountNode
  );
};

interface DragOverlayContentProps {
  layerId?: string;
  componentType?: string;
}

/* istanbul ignore next */
export const DragOverlayContent: React.FC<DragOverlayContentProps> = ({ layerId, componentType }) => {
  const layer = useLayerStore((state) => layerId ? state.findLayerById(layerId) : null);

  // Determine display name from layer or component type
  const displayName = layer?.name || layer?.type || componentType || 'Component';

  if (!layer && !componentType) {
    return null;
  }

  return (
    <div className="mt-10 bg-white dark:bg-gray-800 shadow-lg border-2 border-primary/50 rounded-lg px-3 py-2 text-sm font-medium opacity-90 text-nowrap min-w-fit pointer-events-none">
      {displayName}
    </div>
  );
};

// Helper function to get iframe and its content window
export const getIframeElements = () => {
    const iframe = document.querySelector('[data-testid="auto-frame"]') as HTMLIFrameElement | null;
    if (!iframe) return null;

    const iframeWindow = iframe.contentWindow;
    const iframeDocument = iframe.contentDocument;

    if (!iframeWindow || !iframeDocument) return null;

    return {
      iframe,
      window: iframeWindow,
      document: iframeDocument,
      body: iframeDocument.body,
    };
  };

import { createContext, useContext } from 'react';

// Main DND context state interface
export interface DndContextState {
  isDragging: boolean;
  activeLayerId: string | null;
  /** Component type being dragged from the popover (for new component drags) */
  newComponentType: string | null;
  canDropOnLayer: (layerId: string) => boolean;
}

// Component drag context for tracking individual component drag states
export interface ComponentDragContextState {
  isDragging: boolean;
  setDragging: (isDragging: boolean) => void;
}

// Context definitions
export const DndContextStateContext = createContext<DndContextState>({
  isDragging: false,
  activeLayerId: null,
  newComponentType: null,
  canDropOnLayer: () => false,
});

export const ComponentDragContext = createContext<ComponentDragContextState>({
  isDragging: false,
  setDragging: () => {},
});

// Custom hooks for using contexts
export const useDndContext = () => useContext(DndContextStateContext);
export const useComponentDragContext = () => useContext(ComponentDragContext);

import React, { useState, type ReactNode, useMemo, useEffect, useCallback } from 'react';
import { DndContext } from '@dnd-kit/core';
import { createTransformAwareCollisionDetection } from '@/lib/ui-builder/context/dnd-context-colission-utils';
import { getIframeElements } from '@/lib/ui-builder/context/dnd-utils';
import { 
  DndContextStateContext, 
  ComponentDragContext,
  type DndContextState,
  type ComponentDragContextState,
  useDndContext,
  useComponentDragContext
} from '@/lib/ui-builder/context/dnd-contexts';
import { TransformAwareDragOverlay, DragOverlayContent } from '@/lib/ui-builder/context/drag-overlay';
import { useAutoScroll } from '@/lib/ui-builder/hooks/use-auto-scroll';
import { useDndSensors } from '@/lib/ui-builder/hooks/use-dnd-sensors';
import { useDndEventHandlers } from '@/lib/ui-builder/hooks/use-dnd-event-handlers';
import { useDropValidation } from '@/lib/ui-builder/hooks/use-drop-validation';
import { useKeyboardShortcutsDnd } from '@/lib/ui-builder/hooks/use-keyboard-shortcuts-dnd';

// Re-export the contexts and hooks for backward compatibility
export { useDndContext, useComponentDragContext };

interface DndContextProviderProps {
  children: ReactNode;
}

export const DndContextProvider: React.FC<DndContextProviderProps> = ({ children }) => {
  const [activeLayerId, setActiveLayerId] = useState<string | null>(null);
  const [newComponentType, setNewComponentType] = useState<string | null>(null);
  const [componentDragging, setComponentDragging] = useState(false);

  // Use extracted hooks
  const { handleParentMouseMove, handleIframeMouseMove, stopAutoScroll } = useAutoScroll();
  const sensors = useDndSensors();

  // Wrapper to clear both active layer and new component type
  const clearDragState = useCallback(() => {
    setActiveLayerId(null);
    setNewComponentType(null);
  }, []);

  // Create a ref to hold the latest canDropOnLayer function for use in event handlers
  // This avoids circular dependency: we need canDropOnLayer in handleDragEnd,
  // but canDropOnLayer depends on isLayerDescendantOf from handleDragEnd
  const canDropOnLayerRef = React.useRef<(layerId: string) => boolean>(() => false);

  const { handleDragStart, handleDragEnd, handleDragCancel, isLayerDescendantOf } = useDndEventHandlers({
    stopAutoScroll,
    setActiveLayerId,
    setNewComponentType,
    clearDragState,
    canDropOnLayer: (layerId: string) => canDropOnLayerRef.current(layerId),
  });
  const { canDropOnLayer } = useDropValidation(activeLayerId, isLayerDescendantOf, newComponentType);

  // Keep the ref updated with the latest canDropOnLayer
  React.useEffect(() => {
    canDropOnLayerRef.current = canDropOnLayer;
  }, [canDropOnLayer]);

  // Use keyboard shortcuts hook - also cancel new component drags
  const handleKeyboardCancel = useCallback(() => {
    handleDragCancel();
    clearDragState();
  }, [handleDragCancel, clearDragState]);

  useKeyboardShortcutsDnd(activeLayerId || newComponentType, handleKeyboardCancel);

  // Create the custom collision detection instance fresh each time
  // Don't memoize this to ensure we always get fresh scroll positions during auto-scroll
  const collisionDetection = createTransformAwareCollisionDetection();

  const contextValue: DndContextState = useMemo(() => ({
    isDragging: !!activeLayerId || !!newComponentType,
    activeLayerId,
    newComponentType,
    canDropOnLayer,
  }), [activeLayerId, newComponentType, canDropOnLayer]);

  const componentDragContextValue: ComponentDragContextState = useMemo(() => ({
    isDragging: componentDragging,
    setDragging: setComponentDragging,
  }), [componentDragging]);

  // Auto-scroll event listeners setup/cleanup
  // Support both existing layer drags (activeLayerId) and new component drags (newComponentType)
  const isDragging = !!activeLayerId || !!newComponentType;

  useEffect(() => {
    if (isDragging) {
      // Add global mouse move listener for auto-scroll on parent document
      const handleParentMove = (event: MouseEvent) => handleParentMouseMove(event, true);
      document.addEventListener('mousemove', handleParentMove);

      // Also add mouse move listener to iframe content window
      const iframeElements = getIframeElements();
      let iframeCleanup: (() => void) | null = null;

      if (iframeElements) {
        const { window: iframeWindow } = iframeElements;
        if (iframeWindow) {
          const handleIframeMove = (event: MouseEvent) => handleIframeMouseMove(event, true);
          iframeWindow.addEventListener('mousemove', handleIframeMove);
          iframeCleanup = () => {
            try {
              iframeWindow.removeEventListener('mousemove', handleIframeMove);
            } catch (error) {
              // Iframe might be unmounted, ignore errors
              console.warn('Failed to remove iframe mouse listener:', error);
            }
          };
        }
      }

      return () => {
        document.removeEventListener('mousemove', handleParentMove);
        if (iframeCleanup) {
          iframeCleanup();
        }
        stopAutoScroll();
      };
    } else {
      // Clean up when no active drag
      stopAutoScroll();
    }
  }, [isDragging, handleParentMouseMove, handleIframeMouseMove, stopAutoScroll]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopAutoScroll();
    };
  }, [stopAutoScroll]);

  return (
    <DndContextStateContext.Provider value={contextValue}>
      <ComponentDragContext.Provider value={componentDragContextValue}>
        <DndContext
          sensors={sensors}
          collisionDetection={collisionDetection}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
          onDragCancel={handleDragCancel}
        >
          {children}
          <TransformAwareDragOverlay>
            {(activeLayerId || newComponentType) ? (
              <DragOverlayContent 
                layerId={activeLayerId || undefined} 
                componentType={newComponentType || undefined} 
              />
            ) : null}
          </TransformAwareDragOverlay>
        </DndContext>
      </ComponentDragContext.Provider>
    </DndContextStateContext.Provider>
  );
};

import {
    type CollisionDetection,
    pointerWithin,
    rectIntersection,
} from '@dnd-kit/core';
import { getIframeElements } from '@/lib/ui-builder/context/dnd-utils';
import { debugCollisionDetection } from '@/lib/ui-builder/context/collision-debug';

// Debug flag - only enable in development
const DEBUG_COLLISION = process.env.NODE_ENV === 'development' && false;

// Transform state interface
interface TransformState {
    scale: number;
    positionX: number;
    positionY: number;
}


// Custom collision detection that accounts for transform state
export const createTransformAwareCollisionDetection = (): CollisionDetection => {
    return ({ active, droppableRects, droppableContainers, pointerCoordinates, collisionRect }) => {
        if (!pointerCoordinates) {
            return [];
        }

        const transformState = getTransformState();

        // Get iframe and its current scroll position (force fresh read)
        const iframeElements = getIframeElements();
        let iframeScrollOffset = { x: 0, y: 0 };
        let iframeRect = { left: 0, top: 0 };

        if (iframeElements) {
            const { iframe, window: iframeWindow } = iframeElements;
            if (iframeWindow) {
                // Force a fresh read of scroll position to handle auto-scroll updates
                iframeScrollOffset = {
                    x: iframeWindow.pageXOffset || iframeWindow.scrollX || 0,
                    y: iframeWindow.pageYOffset || iframeWindow.scrollY || 0,
                };
            }

            // Get iframe position relative to viewport
            const rect = iframe.getBoundingClientRect();
            iframeRect = { left: rect.left, top: rect.top };
        }

        // During auto-scroll, force fresh droppable rectangle calculations
        // This addresses the core issue where cached rectangles become stale
        const freshDroppableRects = new Map(droppableRects);

        // Special handling for when we're at the very top (scrollY = 0) vs mid-scroll
        const isAtTop = Math.abs(iframeScrollOffset.y) < 5;
        const needsFreshRects = Math.abs(iframeScrollOffset.y) > 10 || isAtTop;

        // If we're scrolling significantly or at the very top, recalculate rectangles for all droppable containers
        if (needsFreshRects) {
            droppableContainers.forEach((container) => {
                const element = container.node.current;
                if (element) {
                    try {
                        // Get fresh rectangle from the DOM (this gives us viewport coordinates)
                        const rect = element.getBoundingClientRect();

                        if (isAtTop) {
                            // When at the top, convert viewport coordinates to content coordinates
                            // Since we're at top, scroll offset is 0, so we just convert viewport to content
                            freshDroppableRects.set(container.id, {
                                top: rect.top - iframeRect.top + iframeScrollOffset.y,
                                left: rect.left - iframeRect.left + iframeScrollOffset.x,
                                bottom: rect.bottom - iframeRect.top + iframeScrollOffset.y, 
                                right: rect.right - iframeRect.left + iframeScrollOffset.x,
                                width: rect.width,
                                height: rect.height,
                            });
                        } else {
                            // When mid-scroll, convert viewport to iframe-relative, then add scroll offset for content coordinates
                            // rect.left is viewport coordinates, need to convert to content coordinates
                            freshDroppableRects.set(container.id, {
                                top: rect.top - iframeRect.top + iframeScrollOffset.y,
                                left: rect.left - iframeRect.left + iframeScrollOffset.x,
                                bottom: rect.bottom - iframeRect.top + iframeScrollOffset.y,
                                right: rect.right - iframeRect.left + iframeScrollOffset.x,
                                width: rect.width,
                                height: rect.height,
                            });
                        }
                    } catch (error) {
                        // Keep the old rectangle if we can't get a fresh one
                        console.warn('Failed to get fresh rectangle for:', container.id, error);
                    }
                }
            });
        }

        // Transform pointer coordinates step by step:
        // 1. First, convert from viewport coordinates to iframe-relative coordinates
        const iframeRelativeX = pointerCoordinates.x - iframeRect.left;
        const iframeRelativeY = pointerCoordinates.y - iframeRect.top;

        // 2. Then account for the zoom/pan transform
        const transformAdjustedX = (iframeRelativeX - transformState.positionX) / transformState.scale;
        const transformAdjustedY = (iframeRelativeY - transformState.positionY) / transformState.scale;

        // 3. Handle coordinate calculation differently for "at top" vs "mid-scroll"
        let adjustedPointerCoordinates;

        if (isAtTop) {
            // When at the very top, use simpler coordinate calculation
            // This matches the coordinate system used for fresh rectangles at top
            adjustedPointerCoordinates = {
                x: transformAdjustedX,
                y: transformAdjustedY,
            };
        } else {
            // When mid-scroll, add the scroll offset to get content-relative coordinates
            // The scroll offset represents how much content has moved from its original position
            // We add it because if content scrolled down by 100px, a viewport point needs 100px added to hit the same content
            adjustedPointerCoordinates = {
                x: transformAdjustedX + iframeScrollOffset.x,
                y: transformAdjustedY + iframeScrollOffset.y,
            };
        }

        // Alternative coordinate calculation to test if the issue is in the math
        // Try a simpler approach that might work better during scroll
        const simpleAdjustedCoordinates = {
            x: pointerCoordinates.x - iframeRect.left + iframeScrollOffset.x,
            y: pointerCoordinates.y - iframeRect.top + iframeScrollOffset.y,
        };

        // Debug logging for scroll direction issues
        const isScrollingUp = iframeScrollOffset.y > 0;
        const debugInfo = {
            scrollY: iframeScrollOffset.y,
            direction: isScrollingUp ? 'up' : 'down',
            isAtTop: isAtTop,
            original: pointerCoordinates,
            iframeRelative: { x: iframeRelativeX, y: iframeRelativeY },
            transformAdjusted: { x: transformAdjustedX, y: transformAdjustedY },
            final: adjustedPointerCoordinates,
            simple: simpleAdjustedCoordinates,
            transform: transformState,
            rectsRefreshed: needsFreshRects,
            containerCount: droppableContainers.length,
        };

        // Only log when there's significant scrolling or at top to debug the up/down issue
        if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
            console.debug('Collision detection scroll debug:', debugInfo);
        }



        // Validate adjusted coordinates are reasonable
        if (isNaN(adjustedPointerCoordinates.x) || isNaN(adjustedPointerCoordinates.y)) {
            console.warn('Invalid adjusted coordinates, falling back to original');
            // Use original coordinates as fallback
            const fallbackArgs = {
                active,
                droppableRects,
                droppableContainers,
                pointerCoordinates,
                collisionRect,
            };
            return pointerWithin(fallbackArgs);
        }

        // Use the adjusted coordinates and fresh rectangles for collision detection
        const adjustedArgs = {
            active,
            droppableRects: freshDroppableRects,
            droppableContainers,
            pointerCoordinates: adjustedPointerCoordinates,
            collisionRect,
        };

        // First try pointer-based collision detection with adjusted coordinates
        const pointerCollisions = pointerWithin(adjustedArgs);

        if (pointerCollisions.length > 0) {
            if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
                console.debug(`Found pointer collisions with fresh rects ${ isAtTop ? '(at top)' : '(mid-scroll)' }:`, pointerCollisions.map(c => c.id));
            }

            // Debug visualization for successful collisions
            if (DEBUG_COLLISION) {
                debugCollisionDetection(
                    pointerCoordinates, 
                    adjustedPointerCoordinates, 
                    freshDroppableRects, 
                    pointerCollisions,
                    { method: 'pointerWithin', scrollState: isAtTop ? 'at top' : 'mid-scroll', ...debugInfo }
                );
            }

            return pointerCollisions;
        }

        // Fallback to rect-based collision detection with adjusted coordinates
        const rectCollisions = rectIntersection(adjustedArgs);

        if (rectCollisions.length > 0) {
            if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
                console.debug(`Found rect collisions with fresh rects ${ isAtTop ? '(at top)' : '(mid-scroll)' }:`, rectCollisions.map(c => c.id));
            }

            // Debug visualization for successful rect collisions
            if (DEBUG_COLLISION) {
                debugCollisionDetection(
                    pointerCoordinates, 
                    adjustedPointerCoordinates, 
                    freshDroppableRects, 
                    rectCollisions,
                    { method: 'rectIntersection', scrollState: isAtTop ? 'at top' : 'mid-scroll', ...debugInfo }
                );
            }

            return rectCollisions;
        }

        // If fresh rectangles didn't work, try with simpler coordinate calculation
        // This skips the complex transform math and uses a direct approach
        const simpleArgs = {
            active,
            droppableRects: freshDroppableRects,
            droppableContainers,
            pointerCoordinates: simpleAdjustedCoordinates,
            collisionRect,
        };

        const simpleCollisions = pointerWithin(simpleArgs);
        if (simpleCollisions.length > 0) {
            if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
                console.debug(`Found collisions with simple coords ${ isAtTop ? '(at top)' : '(mid-scroll)' }:`, simpleCollisions.map(c => c.id));
            }
            return simpleCollisions;
        }

        // If fresh rectangles didn't work, try with the original cached rectangles
        // This handles cases where the fresh rectangle calculation might have issues
        const cachedArgs = {
            active,
            droppableRects,
            droppableContainers,
            pointerCoordinates: adjustedPointerCoordinates,
            collisionRect,
        };

        const cachedCollisions = pointerWithin(cachedArgs);
        if (cachedCollisions.length > 0) {
            if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
                console.debug(`Found collisions with cached rects ${ isAtTop ? '(at top)' : '(mid-scroll)' }:`, cachedCollisions.map(c => c.id));
            }
            return cachedCollisions;
        }

        // Final fallback: try original coordinates if adjusted ones didn't work
        // This can happen during rapid auto-scroll when coordinate transformation gets out of sync
        const originalArgs = {
            active,
            droppableRects,
            droppableContainers,
            pointerCoordinates,
            collisionRect,
        };

        const originalCollisions = pointerWithin(originalArgs);
        if (DEBUG_COLLISION && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop) && originalCollisions.length === 0) {
            console.debug(`No collisions found with any method ${ isAtTop ? '(at top)' : '(mid-scroll)' }`);
        }

        // Debug visualization for when no collisions are found
        if (DEBUG_COLLISION && originalCollisions.length === 0 && (Math.abs(iframeScrollOffset.y) > 50 || isAtTop)) {
            debugCollisionDetection(
                pointerCoordinates, 
                adjustedPointerCoordinates, 
                freshDroppableRects, 
                originalCollisions,
                { method: 'no collisions found', scrollState: isAtTop ? 'at top' : 'mid-scroll', ...debugInfo }
            );
        }


        return originalCollisions;
    };
};

// Helper function to get transform state
const getTransformState = (): TransformState => {
    const transformComponent = document.querySelector('[data-testid="transform-component"]');
    let transformState: TransformState = { scale: 1, positionX: 0, positionY: 0 };

    if (transformComponent) {
        const computedStyle = window.getComputedStyle(transformComponent);
        const transform = computedStyle.transform;

        if (transform && transform !== 'none') {
            const matrixMatch = transform.match(/matrix\(([^)]*)\)/);
            if (matrixMatch && matrixMatch[1]) {
                const values = matrixMatch[1].split(',').map(v => parseFloat(v.trim()));
                if (values.length >= 6) {
                    const scale = values[0];
                    const positionX = values[4];
                    const positionY = values[5];
                    if (scale !== undefined && positionX !== undefined && positionY !== undefined) {
                        transformState = {
                            scale, // scaleX
                            positionX, // translateX
                            positionY, // translateY
                        };
                    }
                }
            }
        }
    }

    return transformState;
};

/**
 * Collision Debug Utility for DnD Kit
 * 
 * This utility provides visual debugging for drag-and-drop collision detection.
 * It displays:
 * - Original pointer position (red circle)
 * - Adjusted pointer position (blue circle)
 * - Droppable rectangles (orange boxes, lime when colliding)
 * - Collision lines connecting pointer to detected collisions
 * - Debug info panel with coordinates and collision count
 * 
 * Usage:
 * - In browser console: enableCollisionDebug() to turn on
 * - In browser console: disableCollisionDebug() to turn off
 * - In browser console: toggleCollisionDebug() to toggle
 * - In browser console: clearDebugElements() to clear visualizations
 * 
 * Debug visualizations automatically clear after a few seconds.
 */

import type { Collision, UniqueIdentifier } from "@dnd-kit/core";
import type { Coordinates } from "@dnd-kit/utilities";
import { getIframeElements } from '@/lib/ui-builder/context/dnd-utils';

// Enable/disable debug visualization
let DEBUG = false;

const debugElements: Record<
  string,
  { svg: SVGSVGElement; elements: SVGElement[] }
> = {};

let timeout: NodeJS.Timeout;

// Main debug function for collision visualization
export const collisionDebug = (
  pointerCoordinates: Coordinates,
  adjustedPointerCoordinates: Coordinates,
  droppableRects: Map<UniqueIdentifier, { top: number; left: number; bottom: number; right: number; width: number; height: number }>,
  collisions: Collision[],
  options: {
    showPointer?: boolean;
    showRects?: boolean;
    showCollisions?: boolean;
    autoHide?: boolean;
    hideDelay?: number;
  } = {}
) => {
  if (!DEBUG) return;

  const {
    showPointer = true,
    showRects = true,
    showCollisions = true,
    autoHide = true,
    hideDelay = 2000
  } = options;

  const debugId = "collision-debug";

  // Clear previous timeout
  clearTimeout(timeout);

  // Auto-hide after delay
  if (autoHide) {
    timeout = setTimeout(() => {
      clearDebugElements();
    }, hideDelay);
  }

  requestAnimationFrame(() => {
    // Clear existing debug elements
    clearDebugElements();

    // Get iframe elements and positioning
    const iframeElements = getIframeElements();
    if (!iframeElements) {
      console.warn('Could not find iframe for debug overlay positioning');
      return;
    }

    const { iframe, window: iframeWindow } = iframeElements;
    const iframeRect = iframe.getBoundingClientRect();
    const iframeScrollOffset = {
      x: iframeWindow.pageXOffset || iframeWindow.scrollX || 0,
      y: iframeWindow.pageYOffset || iframeWindow.scrollY || 0,
    };

    const svgNs = "http://www.w3.org/2000/svg";
    const svg = document.createElementNS(svgNs, "svg");
    const elements: SVGElement[] = [];

    // Setup SVG container positioned inside iframe to cover the entire content area
    svg.setAttribute("id", debugId);
    svg.setAttribute(
      "style",
      `position: absolute; height: 100%; width: 100%; pointer-events: none; top: 0px; left: 0px; z-index: 9999; overflow: visible;`
    );

    // Convert coordinates to iframe content positioning
    const originalPointerRelative = {
      x: pointerCoordinates.x,
      y: pointerCoordinates.y
    };

    // The adjustedPointerCoordinates should already be in content coordinates
    // but let's make sure we're displaying them correctly
    const adjustedPointerRelative = {
      x: adjustedPointerCoordinates.x + iframeRect.left,
      y: adjustedPointerCoordinates.y + iframeRect.top
    };

    // Debug: log the coordinate spaces to console  
    console.log(' Debug Coordinates:', {
      original: pointerCoordinates,
      originalRelative: originalPointerRelative,
      adjusted: adjustedPointerCoordinates,
      adjustedRelative: adjustedPointerRelative,
      iframeRect,
      iframeScrollOffset,
      // Show first few rectangles for comparison (before and after conversion)
      sampleRects: Array.from(droppableRects.entries()).slice(0, 2).map(([id, rect]) => ({
        id,
        original: rect,
        converted: {
          left: rect.left + iframeRect.left,
          top: rect.top + iframeRect.top,
          width: rect.width,
          height: rect.height
        }
      }))
    });

    // Show pointer positions
    if (showPointer) {
      // Original pointer (relative to iframe)
      const originalPointer = createCircle(originalPointerRelative.x, originalPointerRelative.y, 6, "red", "Original Pointer");
      elements.push(originalPointer);
      svg.appendChild(originalPointer);

      // Adjusted pointer (relative to iframe content)
      const adjustedPointer = createCircle(adjustedPointerRelative.x, adjustedPointerRelative.y, 6, "blue", "Adjusted Pointer");
      elements.push(adjustedPointer);
      svg.appendChild(adjustedPointer);

      // Line connecting original and adjusted pointers
      if (originalPointerRelative.x !== adjustedPointerRelative.x || originalPointerRelative.y !== adjustedPointerRelative.y) {
        const connectionLine = createLine(
          originalPointerRelative.x, originalPointerRelative.y,
          adjustedPointerRelative.x, adjustedPointerRelative.y,
          "purple", 2, "dashed"
        );
        elements.push(connectionLine);
        svg.appendChild(connectionLine);
      }
    }

    // Show droppable rectangles
    if (showRects) {
      droppableRects.forEach((rect, id) => {
        const isColliding = collisions.some(c => c.id === id);
        const color = isColliding ? "lime" : "orange";
        const opacity = isColliding ? "0.3" : "0.1";

        // Convert rectangle coordinates to iframe-relative coordinates
        const rectRelative = {
          left: rect.left + iframeRect.left,
          top: rect.top + iframeRect.top,
          width: rect.width,
          height: rect.height
        };

        const rectElement = createRect(rectRelative.left, rectRelative.top, rectRelative.width, rectRelative.height, color, opacity);
        elements.push(rectElement);
        svg.appendChild(rectElement);

        // Add label for the rectangle
        const label = createText(rectRelative.left + 2, rectRelative.top + 12, String(id), "black", "10px");
        elements.push(label);
        svg.appendChild(label);
      });
    }

    // Show collision connections
    if (showCollisions && collisions.length > 0) {
      collisions.forEach((collision) => {
        const rect = droppableRects.get(collision.id);
        if (rect) {
          // Convert rectangle coordinates to iframe-relative coordinates
          const rectRelative = {
            left: rect.left + iframeRect.left,
            top: rect.top + iframeRect.top,
            width: rect.width,
            height: rect.height
          };

          // Draw line from adjusted pointer to collision center
          const centerX = rectRelative.left + rectRelative.width / 2;
          const centerY = rectRelative.top + rectRelative.height / 2;

          const collisionLine = createLine(
            adjustedPointerRelative.x, adjustedPointerRelative.y,
            centerX, centerY,
            "lime", 3, "solid"
          );
          elements.push(collisionLine);
          svg.appendChild(collisionLine);

          // Add collision info text
          const collisionText = createText(
            centerX, centerY - 5,
            `Collision: ${String(collision.id)}`,
            "lime", "12px", "bold"
          );
          elements.push(collisionText);
          svg.appendChild(collisionText);
        }
      });
    }

    // Add debug info panel with detailed coordinate info
    const infoPanel = createInfoPanel(originalPointerRelative, adjustedPointerRelative, collisions.length, {
      iframeScrollOffset,
      isAtTop: Math.abs(iframeScrollOffset.y) < 5,
      originalViewport: pointerCoordinates
    });
    elements.push(infoPanel);
    svg.appendChild(infoPanel);

    // Position debug overlay inside iframe document so it scrolls naturally with content
    const { document: iframeDoc } = iframeElements;
    iframeDoc.body.appendChild(svg);
    debugElements[debugId] = { svg, elements };
  });
};

// Utility function to clear all debug elements
export const clearDebugElements = () => {
  Object.entries(debugElements).forEach(([id, { svg }]) => {
    svg.remove();
    delete debugElements[id];
  });
};

// Utility function to toggle debug mode
export const toggleCollisionDebug = () => {
  DEBUG = !DEBUG;
  console.log(` Collision Debug ${DEBUG ? 'ENABLED' : 'DISABLED'}`);
  if (!DEBUG) {
    clearDebugElements();
  }
  return DEBUG;
};

// Utility function to enable debug mode
export const enableCollisionDebug = () => {
  DEBUG = true;
  console.log(' Collision Debug ENABLED');
  return DEBUG;
};

// Utility function to disable debug mode
export const disableCollisionDebug = () => {
  DEBUG = false;
  clearDebugElements();
  console.log(' Collision Debug DISABLED');
  return DEBUG;
};

// Make debug functions available globally for console access (development only)
if (typeof window !== 'undefined' && process.env.NODE_ENV !== 'production') {
  (window as any).toggleCollisionDebug = toggleCollisionDebug;
  (window as any).enableCollisionDebug = enableCollisionDebug;
  (window as any).disableCollisionDebug = disableCollisionDebug;
  (window as any).clearDebugElements = clearDebugElements;
}

// Helper functions for creating SVG elements
function createCircle(x: number, y: number, radius: number, color: string, title?: string): SVGCircleElement {
  const circle = document.createElementNS("http://www.w3.org/2000/svg", "circle");
  circle.setAttribute("cx", x.toString());
  circle.setAttribute("cy", y.toString());
  circle.setAttribute("r", radius.toString());
  circle.setAttribute("fill", color);
  circle.setAttribute("stroke", "white");
  circle.setAttribute("stroke-width", "2");

  if (title) {
    const titleElement = document.createElementNS("http://www.w3.org/2000/svg", "title");
    titleElement.textContent = title;
    circle.appendChild(titleElement);
  }

  return circle;
}

function createLine(
  x1: number, y1: number, x2: number, y2: number,
  color: string, width: number = 2, dashArray?: string
): SVGLineElement {
  const line = document.createElementNS("http://www.w3.org/2000/svg", "line");
  line.setAttribute("x1", x1.toString());
  line.setAttribute("y1", y1.toString());
  line.setAttribute("x2", x2.toString());
  line.setAttribute("y2", y2.toString());
  line.setAttribute("stroke", color);
  line.setAttribute("stroke-width", width.toString());

  if (dashArray) {
    line.setAttribute("stroke-dasharray", dashArray === "dashed" ? "5,5" : dashArray);
  }

  return line;
}

function createRect(
  x: number, y: number, width: number, height: number,
  color: string, opacity: string = "0.2"
): SVGRectElement {
  const rect = document.createElementNS("http://www.w3.org/2000/svg", "rect");
  rect.setAttribute("x", x.toString());
  rect.setAttribute("y", y.toString());
  rect.setAttribute("width", width.toString());
  rect.setAttribute("height", height.toString());
  rect.setAttribute("fill", color);
  rect.setAttribute("fill-opacity", opacity);
  rect.setAttribute("stroke", color);
  rect.setAttribute("stroke-width", "1");

  return rect;
}

function createText(
  x: number, y: number, text: string, color: string = "black",
  fontSize: string = "12px", fontWeight: string = "normal"
): SVGTextElement {
  const textElement = document.createElementNS("http://www.w3.org/2000/svg", "text");
  textElement.setAttribute("x", x.toString());
  textElement.setAttribute("y", y.toString());
  textElement.setAttribute("fill", color);
  textElement.setAttribute("font-size", fontSize);
  textElement.setAttribute("font-weight", fontWeight);
  textElement.setAttribute("font-family", "monospace");
  textElement.textContent = text;

  return textElement;
}

function createInfoPanel(
  originalPointer: Coordinates,
  adjustedPointer: Coordinates,
  collisionCount: number,
  debugInfo?: {
    iframeScrollOffset: { x: number; y: number };
    isAtTop: boolean;
    originalViewport: Coordinates;
  }
): SVGGElement {
  const group = document.createElementNS("http://www.w3.org/2000/svg", "g");

  // Background
  const bg = createRect(10, 10, 350, 140, "white", "0.9");
  bg.setAttribute("stroke", "black");
  bg.setAttribute("stroke-width", "1");
  group.appendChild(bg);

  // Info text
  const lines = [
    `Original Pointer: (${Math.round(originalPointer.x)}, ${Math.round(originalPointer.y)})`,
    `Adjusted Pointer: (${Math.round(adjustedPointer.x)}, ${Math.round(adjustedPointer.y)})`,
    `Collisions Found: ${collisionCount}`,
    `Debug Mode: ON`
  ];

  if (debugInfo) {
    lines.push(
      `Scroll: (${Math.round(debugInfo.iframeScrollOffset.x)}, ${Math.round(debugInfo.iframeScrollOffset.y)})`,
      `At Top: ${debugInfo.isAtTop}`,
      `Viewport: (${Math.round(debugInfo.originalViewport.x)}, ${Math.round(debugInfo.originalViewport.y)})`
    );
  }

  lines.forEach((line, index) => {
    const text = createText(15, 30 + (index * 15), line, "black", "11px");
    group.appendChild(text);
  });

  return group;
}

// Debug function specifically for your collision detection system
export const debugCollisionDetection = (
  originalPointer: Coordinates,
  adjustedPointer: Coordinates,
  droppableRects: Map<UniqueIdentifier, any>,
  collisions: Collision[],
  debugInfo?: any
) => {
  if (!DEBUG) return;

  console.group(" Collision Detection Debug");
  console.log("Original Pointer:", originalPointer);
  console.log("Adjusted Pointer:", adjustedPointer);
  console.log("Droppable Rects:", Array.from(droppableRects.entries()));
  console.log("Collisions:", collisions);

  if (debugInfo) {
    console.log("Additional Debug Info:", debugInfo);
  }

  console.groupEnd();

  // Visual debug
  collisionDebug(originalPointer, adjustedPointer, droppableRects, collisions, {
    showPointer: true,
    showRects: true,
    showCollisions: true,
    autoHide: true,
    hideDelay: 3000
  });
};

// Auto-scroll configuration constants
export const AUTO_SCROLL_THRESHOLD = 50; // Distance in pixels from edge to trigger auto-scroll
export const MIN_SCROLL_SPEED = 5; // Minimum scroll speed in pixels per frame
export const MAX_SCROLL_SPEED = 25; // Maximum scroll speed in pixels per frame

// Auto-scroll state interface
export interface AutoScrollState {
  isScrolling: boolean;
  directions: {
    left: boolean;
    right: boolean;
    top: boolean;
    bottom: boolean;
  };
  speeds: {
    horizontal: number;
    vertical: number;
  };
}

// Helper function to calculate scroll speed based on distance from edge
export const calculateScrollSpeed = (distanceFromEdge: number): number => {
  if (distanceFromEdge >= AUTO_SCROLL_THRESHOLD) return 0;

  // Calculate speed as a proportion - closer to edge = faster
  const speedRatio = (AUTO_SCROLL_THRESHOLD - distanceFromEdge) / AUTO_SCROLL_THRESHOLD;
  const easedRatio = Math.pow(speedRatio, 2);
  return MIN_SCROLL_SPEED + (MAX_SCROLL_SPEED - MIN_SCROLL_SPEED) * easedRatio;
};

import { useState, useEffect } from 'react';

// prevents nextjs hydration error if using nextjs
export const useStore = <T, F>(
    store: (callback: (state: T) => unknown) => unknown,
    callback: (state: T) => F,
) => {
    const result = store(callback) as F
    const [data, setData] = useState<F>()

    useEffect(() => {
        setData(result)
    }, [result])

    return data
};

import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

import { useEffect } from "react";

export interface KeyCombination {
  keys: {
    ctrlKey?: boolean;
    metaKey?: boolean;
    shiftKey?: boolean;
    altKey?: boolean;
  };
  key: string;
  handler: (event: KeyboardEvent) => void;
}

export function useKeyboardShortcuts(combinations: KeyCombination[]) {
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      combinations.forEach((combo) => {
        const { keys: { ctrlKey, metaKey, shiftKey, altKey }, key, handler } = combo;
        const isMatch =
          (ctrlKey === undefined || event.ctrlKey === ctrlKey) &&
          (metaKey === undefined || event.metaKey === metaKey) &&
          (shiftKey === undefined || event.shiftKey === shiftKey) &&
          (altKey === undefined || event.altKey === altKey) &&
          event.key.toLowerCase() === key.toLowerCase();

        if (isMatch) {
          event.preventDefault();
          handler(event);
        }
      });
    };

    window.addEventListener("keydown", handleKeyDown);

    // Cleanup event listener on unmount
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [combinations]);
}

import { useEffect, useState } from "react";

export function useDebounce<T>(value: T, delay?: number): T {
    const [debouncedValue, setDebouncedValue] = useState<T>(value);

    useEffect(() => {
        const timer = setTimeout(() => setDebouncedValue(value), delay || 500);

        return () => {
            clearTimeout(timer);
        };
    }, [value, delay]);

    return debouncedValue;
}

"use client";

import * as React from "react";

export interface useCopyToClipboardProps {
  timeout?: number;
}

export function useCopyToClipboard({
  timeout = 2000,
}: useCopyToClipboardProps) {
  const [isCopied, setIsCopied] = React.useState<boolean>(false);

  const copyToClipboard = (value: string) => {
    if (typeof window === "undefined" || !navigator.clipboard?.writeText) {
      return;
    }

    if (!value) {
      return;
    }

    navigator.clipboard.writeText(value).then(() => {
      setIsCopied(true);

      setTimeout(() => {
        setIsCopied(false);
      }, timeout);
    });
  };

  return { isCopied, copyToClipboard };
}

