interface MenuProps {
  layerId: string;
  x: number;
  y: number;
  width: number;
  height: number;
  zIndex: number;
  handleDuplicateComponent?: () => void;
  handleDeleteComponent?: () => void;
}

export const LayerMenu: React.FC<MenuProps> = ({
  layerId,
  x,
  y,
  zIndex,
  handleDuplicateComponent,
  handleDeleteComponent,
}) => {
  const [popoverOpen, setPopoverOpen] = useState(false);
  const selectedLayer = useLayerStore((state) => state.findLayerById(layerId));
  const isLayerAPage = useLayerStore((state) => state.isLayerAPage(layerId));

  const componentRegistry = useEditorStore((state) => state.registry);
  const allowPagesCreation = useEditorStore((state) => state.allowPagesCreation);
  const allowPagesDeletion = useEditorStore((state) => state.allowPagesDeletion);

  // Check permissions for page operations
  const canDuplicate = !isLayerAPage || allowPagesCreation;
  const canDelete = !isLayerAPage || allowPagesDeletion;

  const style = useMemo(() => ({
    top: y,
    left: x,
    zIndex: zIndex,
  }), [y, x, zIndex]);

  const buttonVariantsValues = useMemo(() => {
    return buttonVariants({ variant: "ghost", size: "sm" });
  }, []);

  const canRenderAddChild = useMemo(() => {
    if (!selectedLayer) return false;

    const componentDef = componentRegistry[selectedLayer.type as keyof typeof componentRegistry];
    if (!componentDef) return false;

    // Safely check if schema has shape property (ZodObject) and children field
    const hasChildrenField = 'shape' in componentDef.schema && 
                            componentDef.schema.shape && 
                            componentDef.schema.shape.children !== undefined;

    return (
      hasLayerChildren(selectedLayer) &&
      hasChildrenField
    );
  }, [selectedLayer, componentRegistry]);

  return (
    <>
      <div
        className="fixed"
        style={style}
      >
        <span
          className={cn(
            "h-5 group flex items-center rounded-bl-full rounded-r-full bg-blue-500/85 p-0 text-sm font-semibold text-secondary-foreground shadow-sm ring-1 ring-inset ring-blue-500 hover:bg-secondary/85 hover:h-10 hover:ring-2 transition-all duration-200 ease-in-out overflow-hidden cursor-pointer hover:cursor-auto",
            popoverOpen ? "h-10 ring-2" : ""
          )}
        >
          <ChevronRight
            className={cn(
              "h-5 w-5 text-secondary-foreground group-hover:size-8 transition-all duration-200 ease-in-out group-hover:opacity-30",
              popoverOpen ? "size-8 opacity-30" : ""
            )}
          />

          <div
            className={cn(
              "flex flex-nowrap overflow-hidden max-w-0 group-hover:max-w-xs transition-all duration-200 ease-in-out",
              popoverOpen ? "max-w-xs" : ""
            )}
          >
            {canRenderAddChild && (
              <AddComponentsPopover
                parentLayerId={layerId}
                className="flex-shrink w-min inline-flex"
                onOpenChange={setPopoverOpen}
              >
                <div
                  className={cn(
                    buttonVariantsValues,
                    "cursor-pointer"
                  )}
                >
                  <span className="sr-only">Add Component</span>
                  <Plus className="h-5 w-5 text-secondary-foreground" />
                </div>
              </AddComponentsPopover>
            )}
            {canDuplicate && (
              <div
                className={cn(
                  buttonVariantsValues,
                  "cursor-pointer"
                )}
                onClick={handleDuplicateComponent}
              >
                <span className="sr-only">Duplicate {isLayerAPage ? "Page" : "Component"}</span>
                <Copy className="h-5 w-5 text-secondary-foreground" />
              </div>
            )}
            {canDelete && (
              <div
                className={cn(
                  buttonVariantsValues,
                  "rounded-r-full mr-1 cursor-pointer"
                )}
                onClick={handleDeleteComponent}
              >
                <span className="sr-only">Delete {isLayerAPage ? "Page" : "Component"}</span>
                <Trash className="h-5 w-5 text-secondary-foreground" />
              </div>
            )}
          </div>
        </span>
      </div>
    </>
  );
};

import React, { useState, useRef, useCallback, useEffect, useMemo } from "react";
import {
  createUseGesture,
  pinchAction,
  wheelAction,
  dragAction,
} from "@use-gesture/react";
import { Button } from "@/components/ui/button";
import { ZoomIn, ZoomOut } from "lucide-react";
import { useEditorStore } from "@/lib/ui-builder/store/editor-store";

const MAX_TRANSLATION = 6000;
const MIN_SCALE = 0.1;
const MAX_SCALE = 5;

interface InteractiveCanvasProps {
  children: React.ReactNode;
  frameId?: string;
  disableWheel: boolean;
  disablePinch: boolean;
  disableDrag: boolean;
}

export function InteractiveCanvas({
  children,
  frameId,
  disableWheel,
  disablePinch,
  disableDrag,
}: InteractiveCanvasProps) {

  const windowFrame = frameId
    ? (document.getElementById(frameId) as HTMLIFrameElement)?.contentDocument
    : window;

  const [scale, setScale] = useState(0.98);
  const initialTranslation = useMemo(() => ({ x: 0, y: 0 }), []);
  const [translation, setTranslation] = useState(initialTranslation);
  const containerRef = useRef<HTMLDivElement>(null);
  const previewMode = useEditorStore((state) => state.previewMode);

  const zoom = useCallback((scaleDelta: number) => {
    setScale((prevScale) => {
      const newScale = clamp(prevScale + scaleDelta, MIN_SCALE, MAX_SCALE);

      return newScale;
    });
  }, []);

  useEffect(() => {
    const handleGesture = (e: Event) => {
      e.preventDefault();
    };

    document.addEventListener("gesturechange", handleGesture);
    document.addEventListener("gestureend", handleGesture);
    document.addEventListener("gesturestart", handleGesture);
    return () => {
      document.removeEventListener("gesturechange", handleGesture);
      document.removeEventListener("gestureend", handleGesture);
      document.removeEventListener("gesturestart", handleGesture);
    };
  }, []);

  useEffect(() => {
    // Reset scale and translation on preview mode change to center the contents
    switch (previewMode) {
      case "tablet":
        setScale(0.80);
        setTranslation({ x: -30, y: 5 });
        break;
      case "desktop":
        setScale(0.46);
        setTranslation({ x: -170, y: 5 });
        break;
      default:
        setScale(0.95);
        setTranslation({ x: 0, y: 5 });
    }
  }, [previewMode]);

  const useGestureHook = useMemo(() => createUseGesture([wheelAction, pinchAction, dragAction]), []);

  const gestureHandlers = useMemo(() => {
    return {
      onWheel: ({ event, delta }: { event: WheelEvent; delta: [number, number] }) => {
        // Normalize deltaY for different deltaModes
        let deltaY = delta[1];
        if (event.deltaMode === 1) {
          // Delta is in lines; convert to pixels
          deltaY *= 16; // Approximate line height in pixels
        } else if (event.deltaMode === 2) {
          // Delta is in pages; convert to pixels
          deltaY *= window.innerHeight;
        }

        if (event.metaKey || event.ctrlKey) {
          // Zoom based on normalized deltaY
          zoom(-deltaY / 500);
        } else {
          // Handle panning
          setTranslation((prev) => ({
            x: clamp(prev.x - delta[0], -MAX_TRANSLATION, MAX_TRANSLATION),
            y: clamp(prev.y - deltaY, -MAX_TRANSLATION, MAX_TRANSLATION),
          }));
        }
      },
      onPinch: ({ delta: [d] }: { delta: [number] }) => {
        zoom(d / 200);
      },
      onDrag: ({ movement: [x, y] }: { movement: [number, number] }) => {
        setTranslation((prev) => ({
          x: clamp(prev.x + x, -MAX_TRANSLATION, MAX_TRANSLATION),
          y: clamp(prev.y + y, -MAX_TRANSLATION, MAX_TRANSLATION),
        }));
      },
    };
  }, [zoom, setTranslation]);

  const gestureOptions = useMemo(() => {
    return {
      window: windowFrame || window,

      wheel: {
        target: containerRef,
        eventOptions: { passive: false, capture: true },
        window: windowFrame || window,
        enabled: !disableWheel,
      },
      pinch: {
        rubberband: true,
        target: containerRef,
        eventOptions: { passive: false, capture: true },
        window: windowFrame || window,
        enabled: !disablePinch,
      },
      drag: {
        target: containerRef,
        eventOptions: { passive: false, capture: true },
        enabled: !disableDrag,
      },
    };
  }, [windowFrame, containerRef, disableWheel, disablePinch, disableDrag]);

  const bind = useGestureHook(
    gestureHandlers as any,
    gestureOptions
  );

  const transformStyle = useCallback(() => {
    return {
      transform: `translate(${translation.x}px, ${translation.y}px) scale(${scale})`,
      transformOrigin: "top center",
    };
  }, [translation.x, translation.y, scale]);

  const handleZoomIn = useCallback(() => {
    zoom(0.05);
  }, [zoom]);

  const handleZoomOut = useCallback(() => {
    zoom(-0.05);
  }, [zoom]);

  return (
    <div
      ref={containerRef}
      {...bind()}
      className="w-full h-full overflow-hidden relative touch-none cursor-default transition-transform duration-100 ease-in-out"
    >
      <div data-testid="interactive-canvas-container" className="touch-none" style={transformStyle()}>
        {children}
      </div>
      <ZoomControls onZoomIn={handleZoomIn} onZoomOut={handleZoomOut} />
    </div>
  );
}

interface ZoomControlsProps {
  onZoomIn: () => void;
  onZoomOut: () => void;
}

const ZoomControls = ({ onZoomIn, onZoomOut }: ZoomControlsProps) => {
  return (
    <div className="absolute bottom-2 md:right-2 right-4">
      <Button
        data-testid="button-ZoomIn"
        variant="secondary"
        size="icon"
        className="rounded-l-full rounded-r-none shadow md:p-4 p-6"
        onClick={onZoomIn}
      >
        <ZoomIn />
      </Button>
      <Button
        data-testid="button-ZoomOut"
        variant="secondary"
        size="icon"
        className="rounded-l-none rounded-r-full shadow md:p-4 p-6"
        onClick={onZoomOut}
      >
        <ZoomOut />
      </Button>
    </div>
  );
};

function clamp(value: number, min: number, max: number) {
  return Math.max(min, Math.min(max, value));
}

import React, {
  useCallback,
  useLayoutEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import ReactDOM from "react-dom";
import { GripVertical } from "lucide-react";
import { DragConfig, useDrag } from "@use-gesture/react";
import { cn } from "@/lib/utils";

const initialSize = { width: 0 };

interface IframeWrapperProps extends React.HTMLAttributes<HTMLIFrameElement> {
  children: React.ReactNode;
  frameId?: string;
  resizable: boolean;
}

export const IframeWrapper: React.FC<IframeWrapperProps> = React.memo(
  ({ children, frameId, resizable, style, ...props }) => {
    const iframeRef = useRef<HTMLIFrameElement>(null);
    const mountNodeRef = useRef<HTMLElement | null>(null); // Ref to store the mount node
    const [iframeSize, setIframeSize] = useState<{ width: number } | null>(
      null
    );
    const [isMounted, setIsMounted] = useState<boolean>(false); // State to track mount node readiness



    // Ref to store the initial size at the start of dragging
    const initialSizeRef = useRef<{ width: number }>(initialSize);

    const updateIframeHeight = () => {
      if (!iframeRef.current || !mountNodeRef.current) return;
      const newHeight = mountNodeRef.current.scrollHeight;
      iframeRef.current.style.height = `${newHeight}px`;
    };

    const dragConfig = useMemo(() => {
      return {
        axis: "x",
        from: () => [0, 0],
        filterTaps: true,
      } as DragConfig
    }, []);

    // Handle resizing using useDrag
    const bindResizer = useDrag(
      ({ down, movement: [mx], first }) => {
        if (first) {
          // Capture the initial size when drag starts
          initialSizeRef.current = {
            width: iframeRef.current?.offsetWidth || 0,
          };
        }

        if (down) {
          // Calculate new size based on initial size and movement
          setIframeSize({
            width: initialSizeRef.current.width + mx,
          });
        }
      },
      dragConfig as any
    );

    useLayoutEffect(() => {
      if (resizable && iframeRef.current) {
        setIframeSize({
          width: iframeRef.current.parentElement?.offsetWidth || 600, // Fallback to 600px or any default width
        });
      }
    }, [resizable]);

    useLayoutEffect(() => {
      const iframe = iframeRef.current;
      if (!iframe) return;

      const handleLoad = () => {
        const iframeDoc =
          iframe.contentDocument || iframe.contentWindow?.document;
        if (!iframeDoc) return;

        if (!mountNodeRef.current) {
          // Create a div inside the iframe for mounting React components only once
          const mountNode = iframeDoc.createElement("div");
          mountNode.id = "iframe-mount-node";
          iframeDoc.body.appendChild(mountNode);
          mountNodeRef.current = mountNode;

          iframeDoc.body.style.backgroundColor = "transparent";

          // Copy font variables and classes from parent to iframe
          const parentBody = document.body;
          const parentHtml = document.documentElement;
          const parentComputedStyle = window.getComputedStyle(parentHtml);

          // Copy all CSS custom properties (variables) from parent html element
          for (let i = 0; i < parentComputedStyle.length; i++) {
            const property = parentComputedStyle[i];
            if (property.startsWith("--")) {
              const value = parentComputedStyle.getPropertyValue(property);
              iframeDoc.documentElement.style.setProperty(property, value);
              iframeDoc.body.style.setProperty(property, value);
            }
          }

          // Copy font-related classes from parent body
          parentBody.classList.forEach((className) => {
            if (
              className.includes("font-") ||
              className === "antialiased" ||
              className.includes("__variable")
            ) {
              iframeDoc.body.classList.add(className);
            }
          });

          // Function to inject stylesheets into the iframe
          const injectStylesheets = () => {
            // Select all linked stylesheets in the parent document
            const links = Array.from(
              document.querySelectorAll('link[rel="stylesheet"]')
            );
            // Select all style tags in the parent document
            const styles = Array.from(document.querySelectorAll("style"));

            // Inject linked stylesheets
            links.forEach((link) => {
              const clonedLink = iframeDoc.createElement("link");
              clonedLink.rel = "stylesheet";
              clonedLink.href = (link as HTMLLinkElement).href;
              iframeDoc.head.appendChild(clonedLink);
            });

            // Inject inline styles
            styles.forEach((style) => {
              const clonedStyle = iframeDoc.createElement("style");
              clonedStyle.textContent = style.textContent || "";
              iframeDoc.head.appendChild(clonedStyle);
            });
          };

          // Inject styles on initial load
          injectStylesheets();

          const resizeObserver = new ResizeObserver(() => {
            updateIframeHeight();
          });

          // Observe the mount node for size changes
          resizeObserver.observe(mountNodeRef.current);

          const mutationObserver = new MutationObserver(() => {
            updateIframeHeight();
          });

          // Observe the mount node for changes
          mutationObserver.observe(mountNodeRef.current, {
            childList: true,
            subtree: true,
          });

          // Initial height update
          setTimeout(updateIframeHeight, 0);

          // Set the mount node as ready
          setIsMounted(true);

          // Cleanup observer on unmount
          return () => {
            resizeObserver.disconnect();
            mutationObserver.disconnect();
          };
        }
      };

      // Attach the load event listener
      iframe.addEventListener("load", handleLoad);

      // If the iframe is already loaded, trigger the handler
      if (iframe.contentDocument?.readyState === "complete") {
        handleLoad();
      }

      // Cleanup event listener on unmount
      return () => {
        iframe.removeEventListener("load", handleLoad);
      };
    }, [children]);


    const resizerStyle = useMemo(() => {
      return {
        left: iframeSize?.width ? `${iframeSize.width}px` : undefined
      }
    }, [iframeSize]);

    const iframeStyle = useMemo(() => {
      return {
        width: resizable
          ? iframeSize
            ? `${iframeSize.width}px`
            : "100%"
          : undefined,
        opacity: isMounted ? 1 : 0,
        transition: "opacity 0.5s ease-in",
        ...style,
      }
    }, [iframeSize, resizable, isMounted, style]);

    const bindResizerValues = useMemo(() => {
      return typeof bindResizer === 'function' ? bindResizer() : {};
    }, [bindResizer]);

    return (
      <div className="relative block">
        {resizable && (
