            <DropdownMenuContent align="end">
              {group.keys.map((key) => (
                <DropdownMenuItem
                  key={String(key)}
                  onClick={handleDropdownClick(String(key))}
                  className={
                    selectedKey === String(key)
                      ? "bg-secondary-foreground/10"
                      : ""
                  }
                >
                  {CONFIG[String(key)].label || String(key)}
                </DropdownMenuItem>
              ))}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </>
    );
  }

import { useState, useEffect, useCallback } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import ClassNameMultiselect from "@/components/ui/ui-builder/internal/classname-multiselect";
import { ClassNameItemControl } from "@/components/ui/ui-builder/internal/classname-control/classname-item-control";
import {
  Accordion,
  AccordionItem,
  AccordionTrigger,
  AccordionContent,
} from "@/components/ui/accordion";
import { Badge } from "@/components/ui/badge";

interface BreakpointClassNameControlProps {
  onChange?: (classes: string) => void;
  value?: string;
}
export const BreakpointClassNameControl = ({
  onChange,
  value,
}: BreakpointClassNameControlProps) => {
  // Helper to parse classString into base, md, rest
  const parseClassString = (str: string) => {
    const tokens = str.trim().split(/\s+/);
    const base: string[] = [];
    const md: string[] = [];
    const rest: string[] = [];
    for (const token of tokens) {
      if (token.startsWith("md:")) {
        md.push(token.slice(3));
      } else if (token.includes(":")) {
        rest.push(token);
      } else if (token) {
        base.push(token);
      }
    }
    return {
      base: base.join(" "),
      md: md.join(" "),
      rest: rest.join(" "),
    };
  };

  // State for the full class string
  const [classString, setClassString] = useState(value || "");
  // State for the tab
  const [tab, setTab] = useState<"base" | "md">("base");

  // Sync classString with value prop (uncontrolled to controlled fix)
  useEffect(() => {
    if (typeof value === "string" && value !== classString) {
      setClassString(value);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value]);

  // Parse the class string for the tabs
  const { base, md, rest } = parseClassString(classString);

  // Handlers for each tab
  const handleBaseChange = useCallback(
    (newBase: string) => {
      const newClassString = [
        newBase,
        md &&
          md
            .split(" ")
            .map((cls) => `md:${cls}`)
            .join(" "),
        rest,
      ]
        .filter(Boolean)
        .join(" ")
        .replace(/\s+/g, " ")
        .trim();
      setClassString(newClassString);
    },
    [md, rest]
  );

  const handleMdChange = useCallback(
    (newMd: string) => {
      const mdClasses = newMd
        .split(" ")
        .filter(Boolean)
        .map((cls) => `md:${cls}`)
        .join(" ");
      const newClassString = [base, mdClasses, rest]
        .filter(Boolean)
        .join(" ")
        .replace(/\s+/g, " ")
        .trim();
      setClassString(newClassString);
    },
    [base, rest]
  );

  // When classString changes, call parent onChange
  useEffect(() => {
    if (onChange) onChange(classString);
  }, [classString, onChange]);

  // When multiselect changes, update classString (and tabs will re-parse)
  const handleMultiselectChange = useCallback((newClassString: string) => {
    setClassString(newClassString);
  }, []);

  const handleTabChange = useCallback(
    (val: string) => setTab(val as "base" | "md"),
    []
  );

  return (
    <div
      className="w-full border rounded-lg"
      data-testid="breakpoint-classname-control"
    >
      <Tabs
        value={tab}
        onValueChange={handleTabChange}
        className="w-full"
        data-testid="breakpoint-tabs"
      >
        <TabsList
          className="w-full grid grid-cols-2"
          data-testid="breakpoint-tabs-list"
        >
          <TabsTrigger value="base" data-testid="base-tab-trigger">
            <Tooltip>
              <TooltipTrigger asChild>
                <div className="flex items-center">
                  <span>Base</span>
                  {base && (
                    <Badge className="ml-1 justify-center text-center px-[3px] h-[18px] min-w-[18px] !text-[10px]">
                      {base.split(" ").filter(Boolean).length}
                    </Badge>
                  )}
                </div>
              </TooltipTrigger>
              <TooltipContent>Base styles for all screen sizes</TooltipContent>
            </Tooltip>
          </TabsTrigger>
          <TabsTrigger value="md" data-testid="md-tab-trigger">
            <Tooltip>
              <TooltipTrigger asChild>
                <div className="flex items-center">
                  <span className="overflow-hidden text-ellipsis whitespace-nowrap text-nowrap">
                    Tablet & Desktop
                  </span>
                  {md && (
                    <Badge className="ml-1 justify-center text-center px-[3px] h-[18px] min-w-[18px] !text-[10px]">
                      {md.split(" ").filter(Boolean).length}
                    </Badge>
                  )}
                </div>
              </TooltipTrigger>
              <TooltipContent>
                Overrides for screens larger than 768px (md:*)
              </TooltipContent>
            </Tooltip>
          </TabsTrigger>
        </TabsList>
        <TabsContent
          className="mt-0"
          value="base"
          data-testid="base-tab-content"
        >
          <ClassNameItemControl value={base} onChange={handleBaseChange} />
        </TabsContent>
        <TabsContent className="mt-0" value="md" data-testid="md-tab-content">
          <ClassNameItemControl value={md} onChange={handleMdChange} />
        </TabsContent>
      </Tabs>
      <Accordion
        type="single"
        collapsible
        defaultValue=""
        data-testid="classes-accordion"
      >
        <AccordionItem
          value="classes"
          className="border-t border-b-0 px-4 [&_#accordion-content[data-state=open]]:overflow-visible [&_#accordion-content[data-state=closed]]:overflow-hidden"
          data-testid="classes-accordion-item"
        >
          <AccordionTrigger
            className="text-sm"
            data-testid="classes-accordion-trigger"
          >
            Edit Classes
          </AccordionTrigger>
            <AccordionContent
              data-testid="classes-accordion-content"
              id="accordion-content"
            >
            <ClassNameMultiselect
              value={classString}
              onChange={handleMultiselectChange}
            />
          </AccordionContent>
        </AccordionItem>
      </Accordion>
    </div>
  );
};

import React from 'react';
import { Variable, VariableReference, PropValue, isVariableReference } from '@/components/ui/ui-builder/types';

/**
 * Resolves variable references in props using provided variable values
 * @param props - The props object that may contain variable references
 * @param variables - Array of available variables
 * @param variableValues - Object mapping variable IDs to their resolved values
 * @returns Props with variable references resolved
 */
export function resolveVariableReferences(
  props: Record<string, PropValue>,
  variables: Variable[],
  variableValues?: Record<string, PropValue>
): Record<string, PropValue> {
  const resolved: Record<string, PropValue> = {};

  for (const [key, value] of Object.entries(props)) {
    if (isVariableReference(value)) {
      const variable = variables.find(v => v.id === value.__variableRef);
      if (variable) {
        // Use provided value or fall back to default value
        resolved[key] = variableValues?.[variable.id] ?? variable.defaultValue;
      } else {
        // Variable not found, use default value or undefined
        resolved[key] = undefined;
      }
    } else if (typeof value === 'object' && value !== null && !Array.isArray(value) && !React.isValidElement(value)) {
      // Recursively resolve nested objects (but not React elements or arrays)
      resolved[key] = resolveVariableReferences(value as Record<string, PropValue>, variables, variableValues);
    } else {
      // Regular value, keep as is
      resolved[key] = value;
    }
  }

  return resolved;
}

// Export the isVariableReference function for backward compatibility
export { isVariableReference };

export function getScrollParent(element: HTMLElement | null): HTMLElement | null {
    if (!element) return null;

    const overflowRegex = /(auto|scroll)/;

    let parent: HTMLElement | null = element.parentElement;

    while (parent) {
      const style = getComputedStyle(parent);
      const overflowY = style.overflowY;
      const overflowX = style.overflowX;

      if (overflowRegex.test(overflowY) || overflowRegex.test(overflowX)) {
        return parent;
      }

      parent = parent.parentElement;
    }

    return null;
  }

/* eslint-disable @typescript-eslint/no-explicit-any */
import { z, ZodObject, ZodTypeAny, ZodDate, ZodNumber, ZodEnum, ZodOptional, ZodNullable, ZodDefault, ZodArray, ZodRawShape, ZodLiteral, ZodUnion, ZodTuple } from 'zod';

/**
 * Generates default props based on the provided Zod schema.
 * Supports boolean, date, number, string, enum, objects composed of these primitives, and arrays of these primitives.
 * Logs a warning for unsupported types.
 *
 * @param schema - The Zod schema object.
 * @returns An object containing default values for the schema.
 */
export function getDefaultProps(schema: ZodObject<any>): Record<string, any> {
    const shape = schema.shape;
    const defaultProps: Record<string, any> = {};

    for (const key in shape) {
        if (Object.prototype.hasOwnProperty.call(shape, key)) {
            const fieldSchema = shape[key];
            const value = getDefaultValue(fieldSchema, key);
            if(value !== undefined){
                defaultProps[key] = value;
            }
        }
    }

    return defaultProps;
}

/**
 * Determines the default value for a given Zod schema.
 * Handles nullable and coerced fields appropriately.
 *
 * @param schema - The Zod schema for the field.
 * @param fieldName - The name of the field (used for logging).
 * @returns The default value for the field.
 */
function getDefaultValue(schema: ZodTypeAny, fieldName: string): any {
    // Handle ZodDefault to return the specified default value
    if (schema instanceof ZodDefault) {
        return schema._def.defaultValue();
    }

    if (!schema.isOptional()){
        console.warn(`No default value set for required field "${fieldName}".`);
    }
    return undefined;
}

/**
 * Patches the given Zod object schema by transforming unions of literals to enums,
 * coercing number and date types, and adding an optional `className` property.
 *
 * @param schema - The original Zod object schema to be patched.
 * @returns A new Zod object schema with the specified transformations applied.
 */

export function patchSchema(schema: ZodObject<any>): ZodObject<any> {
    const schemaWithFixedEnums = transformUnionToEnum(schema);
    const schemaWithCoercedTypes = addCoerceToNumberAndDate(schemaWithFixedEnums);
    const schemaWithCommon = addCommon(schemaWithCoercedTypes);

    return schemaWithCommon;
}

/**
 * Extends the given Zod object schema by adding an optional `className` property.
 *
 * @param schema - The original Zod object schema.
 * @returns A new Zod object schema with the `className` property added.
 */
function addCommon<T extends ZodRawShape>(
    schema: ZodObject<T>
) {
    return schema.extend({
        className: z.string().optional(),
    });
}

/**
 * Transforms a ZodUnion of ZodLiterals into a ZodEnum with a default value.
 * If the schema is nullable or optional, it recursively applies the transformation to the inner schema.
 *
 * @param schema - The original Zod schema, which can be a ZodUnion, ZodNullable, ZodOptional, or ZodObject.
 * @returns A transformed Zod schema with unions of literals converted to enums, or the original schema if no transformation is needed.
 */
function transformUnionToEnum<T extends ZodTypeAny>(schema: T): T {
    // Handle ZodUnion of string literals
    if (schema instanceof ZodUnion) {
        const options = schema.options;

        // Check if all options are ZodLiteral instances with string values
        if (
            options.every(
                (option: any) => option instanceof ZodLiteral && typeof option._def.value === 'string'
            )
        ) {
            const enumValues = options.map(
                (option: ZodLiteral<string>) => option.value
            ).reverse();

            // Ensure there is at least one value to create an enum
            if (enumValues.length === 0) {
                throw new Error("Cannot create enum with no values.");
            }

            // Create a ZodEnum from the string literals
            const enumSchema = z.enum(enumValues as [string, ...string[]]);

            // Determine if the original schema was nullable or optional
            let transformedSchema: ZodTypeAny = enumSchema;

            // Apply default before adding modifiers to ensure it doesn't get overridden
            transformedSchema = enumSchema.default(enumValues[0]);


            if (schema.isNullable()) {
                transformedSchema = transformedSchema.nullable();
            }

            if (schema.isOptional()) {
                transformedSchema = transformedSchema.optional();
            }

            return transformedSchema as unknown as T;
        }
    }

    // Recursively handle nullable and optional schemas
    if (schema instanceof ZodNullable) {
        const inner = schema.unwrap();
        const transformedInner = transformUnionToEnum(inner);
        return transformedInner.nullable() as any;
    }

    if (schema instanceof ZodOptional) {
        const inner = schema.unwrap();
        const transformedInner = transformUnionToEnum(inner);
        return transformedInner.optional() as any;
    }

    // Recursively handle ZodObjects by transforming their shape
    if (schema instanceof ZodObject) {
        const transformedShape: Record<string, ZodTypeAny> = {};

        for (const [key, value] of Object.entries(schema.shape)) {
            transformedShape[key] = transformUnionToEnum(value as ZodTypeAny);
        }

        return z.object(transformedShape) as unknown as T;
    }

    // Handle ZodArrays by transforming their element type
    if (schema instanceof ZodArray) {
        const transformedElement = transformUnionToEnum(schema.element);
        return z.array(transformedElement) as unknown as T;
    }

    // Handle ZodTuples by transforming each element type
    if (schema instanceof ZodTuple) {
        const transformedItems = schema.items.map((item: any) => transformUnionToEnum(item));
        return z.tuple(transformedItems) as unknown as T;
    }

    // If none of the above, return the schema unchanged
    return schema;
}

/**
 * Recursively applies coercion to number and date fields within the given Zod schema.
 * Handles nullable, optional, objects, arrays, unions, and enums appropriately to ensure type safety.
 *
 * @param schema - The original Zod schema to transform.
 * @returns A new Zod schema with coercions applied where necessary.
 */
function addCoerceToNumberAndDate<T extends ZodTypeAny>(schema: T): T {
    // Handle nullable schemas
    if (schema instanceof ZodNullable) {
        const inner = schema.unwrap();
        return addCoerceToNumberAndDate(inner).nullable() as any;
    }

    // Handle optional schemas
    if (schema instanceof ZodOptional) {
        const inner = schema.unwrap();
        return addCoerceToNumberAndDate(inner).optional() as any;
    }

    // Handle objects by recursively applying the transformation to each property
    if (schema instanceof ZodObject) {
        const shape: ZodRawShape = schema.shape;
        const transformedShape: ZodRawShape = {};

        for (const [key, value] of Object.entries(shape)) {
            transformedShape[key] = addCoerceToNumberAndDate(value);
        }

        return z.object(transformedShape) as any;
    }

    // Handle arrays by applying the transformation to the array's element type
    if (schema instanceof ZodArray) {
        const innerType = schema.element;
        return z.array(addCoerceToNumberAndDate(innerType)) as any;
    }

    // Apply coercion to number fields
    if (schema instanceof ZodNumber) {
        return z.coerce.number().optional() as any; // Adjust `.optional()` based on your schema requirements
    }

    // Apply coercion to date fields
    if (schema instanceof ZodDate) {
        return z.coerce.date().optional() as any; // Adjust `.optional()` based on your schema requirements
    }

    // Handle unions by applying the transformation to each option
    if (schema instanceof ZodUnion) {
        const transformedOptions = schema.options.map((option: any) => addCoerceToNumberAndDate(option));
        return z.union(transformedOptions) as any;
    }

    // Handle enums by returning them as-is
    if (schema instanceof ZodEnum) {
        return schema;
    }

    // If none of the above, return the schema unchanged
    return schema;
}

// patch for autoform to respect existing values, specifically for enums
export function addDefaultValues<T extends ZodObject<any>>(
  schema: T,
  defaultValues: Partial<z.infer<T>>
): T {
  const shape = schema.shape;

  const updatedShape = { ...shape };

  for (const key in defaultValues) {
    if (updatedShape[key]) {
      // Apply the default value to the existing schema field
      updatedShape[key] = updatedShape[key].default(defaultValues[key]);
    } else if (process.env.NODE_ENV !== "production") {
      console.warn(
        `Key "${key}" does not exist in the schema and will be ignored.`
      );
    }
  }

  return z.object(updatedShape) as T;
}

import { LayerStore } from "@/lib/ui-builder/store/layer-store";
import { ComponentLayer, ComponentRegistry } from '@/components/ui/ui-builder/types';
import { getDefaultProps } from '@/lib/ui-builder/store/schema-utils';

/**
 * Recursively visits each layer in the layer tree and applies the provided visitor function to each layer.
 * The visitor function can modify the layer and its children as needed.
 *
 * @param layer - The current layer to visit.
 * @param visitor - A function that takes a layer and returns a modified layer.
 * @returns The modified layer after applying the visitor function.
 */
export const visitLayer = (layer: ComponentLayer, parentLayer: ComponentLayer | null, visitor: (layer: ComponentLayer, parentLayer: ComponentLayer | null) => ComponentLayer): ComponentLayer => {
    // Apply the visitor to the current layer
    const updatedLayer = visitor(layer, parentLayer);

    // Recursively traverse and update children if they exist
    if (hasLayerChildren(updatedLayer)) {
        const updatedChildren = updatedLayer.children.map((child) =>
            visitLayer(child, updatedLayer, visitor)
        );
        return { ...updatedLayer, children: updatedChildren };
    }

    return updatedLayer;
};

export const countLayers = (layers: ComponentLayer[] | string): number => {
    if (typeof layers === 'string') {
        return 0;
    }
    return layers.reduce((count, layer) => {
        if (hasLayerChildren(layer)) {
            return count + 1 + countLayers(layer.children);
        }
        return count + 1;
    }, 0);
};

export const addLayer = (layers: ComponentLayer[], newLayer: ComponentLayer, parentId?: string, parentPosition?: number): ComponentLayer[] => {
    const updatedPages = layers.map((page) =>
        visitLayer(page, null, (layer) => {
            if (layer.id === parentId) {
                // Handle both layers with existing children and those with undefined/null children
                let updatedChildren: ComponentLayer[] = [];

                if (hasLayerChildren(layer)) {
                    updatedChildren = [...layer.children];
                } else if (layer.children === undefined || layer.children === null || (Array.isArray(layer.children) && layer.children.length === 0)) {
                    // Initialize children array for layers with undefined/null children or empty arrays
                    updatedChildren = [];
                } else {
                    // For layers with string children or other non-array types, we can't add children
                    return layer;
                }

                if (parentPosition !== undefined) {
                    if (parentPosition < 0) {
                        // If parentPosition is negative, insert at the beginning
                        updatedChildren = [newLayer, ...updatedChildren];
                    } else if (parentPosition >= updatedChildren.length) {
                        // If parentPosition is greater than or equal to the length, append to the end
                        updatedChildren = [...updatedChildren, newLayer];
                    } else {
                        // Insert at the specified position
                        updatedChildren = [
                            ...updatedChildren.slice(0, parentPosition),
                            newLayer,
                            ...updatedChildren.slice(parentPosition)
                        ];
                    }
                } else {
                    // If parentPosition is undefined, append to the end
                    updatedChildren = [...updatedChildren, newLayer];
                }

                return { ...layer, children: updatedChildren };
            }

            return layer;
        })
    );
    return updatedPages;
}

export const findAllParentLayersRecursive = (layers: ComponentLayer[], layerId: string): ComponentLayer[] => {
    const parents: ComponentLayer[] = [];

    const findParents = (layers: ComponentLayer[], targetId: string): boolean => {
        for (const layer of layers) {
            if (hasLayerChildren(layer)) {
                if (layer.children.some(child => child.id === targetId)) {
                    parents.push(layer);
                    // Continue searching upwards
                    findParents(layers, layer.id);
                    return true;
                }

                if (findParents(layer.children, targetId)) {
                    parents.push(layer);
                    return true;
                }
            }
        }
        return false;
    };

    findParents(layers, layerId);
    return parents;
};

export const findLayerRecursive = (layers: ComponentLayer[], layerId: string): ComponentLayer | undefined => {
    for (const layer of layers) {
        if (layer.id === layerId) {
            return layer;
        }
        if (hasLayerChildren(layer)) {
            const foundInChildren = findLayerRecursive(layer.children, layerId);
            if (foundInChildren) {
                return foundInChildren;
            }
        }
    }
    return undefined;
};

export const duplicateWithNewIdsAndName = (layer: ComponentLayer, addCopySuffix: boolean = true): ComponentLayer => {
    const newLayer: ComponentLayer = { ...layer, id: createId() };
    if (layer.name) {
      newLayer.name = `${ layer.name }${ addCopySuffix ? ' (Copy)' : ''}`;
    }
    if (hasLayerChildren(newLayer) && hasLayerChildren(layer)) {
      newLayer.children = layer.children.map(child => duplicateWithNewIdsAndName(child, false));
    }
    return newLayer;
  };


export function createId(): string {
    const ALPHABET = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz';
    const ID_LENGTH = 7;
    let result = '';
    const alphabetLength = ALPHABET.length;

    for (let i = 0; i < ID_LENGTH; i++) {
        const randomIndex = Math.floor(Math.random() * alphabetLength);
        result += ALPHABET.charAt(randomIndex);
    }

    return result;
}

export const hasLayerChildren = (layer: ComponentLayer): layer is ComponentLayer & { children: ComponentLayer[] } => {
    return Array.isArray(layer.children) && typeof layer.children !== 'string';
};

export function migrateV1ToV2(persistedState: unknown): LayerStore {
    type TextLayer = {
        id: string;
        name?: string;
        type: '_text_';
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        props: Record<string, any>;
        text: string;
        textType: 'text' | 'markdown';
      };

      console.log("Migrating store", { persistedState, version: 1 });

      const migratedState = persistedState as LayerStore;

      // Utilize visitLayer to transform all layers recursively
      const transformLayer = (layer: ComponentLayer): ComponentLayer => {
        if (layer.type === "_text_") {
          const textLayer = layer as unknown as TextLayer;
          const transformedTextLayer: ComponentLayer = {
            type: textLayer.textType === "markdown" ? "Markdown" : "span",
            children: textLayer.text,
            id: textLayer.id,
            name: textLayer.name,
            props: textLayer.props,
          };
          console.log("Transformed text layer", transformedTextLayer);
          return transformedTextLayer;
        }

        return layer;
      };

      const migratedPages = migratedState.pages.map((page: ComponentLayer) => {
        return visitLayer(page, null, transformLayer) as ComponentLayer;
      }) satisfies ComponentLayer[];

      return {
        ...migratedState,
        pages: migratedPages,
      } satisfies LayerStore;
}

export function migrateV2ToV3(persistedState: unknown): LayerStore {


     type PageLayer = {
        id: string;
        name?: string;
        type: '_page_';
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        props: Record<string, any>;
        children: ComponentLayer[];
      }

      console.log("Migrating store", { persistedState, version: 1 });

      const migratedState = persistedState as LayerStore;

      const keysToMap = {   
        borderRadius: "data-border-radius",
        colorTheme: "data-color-theme",
        mode: "data-mode",
      };

      const migratedPages = migratedState.pages.map((page: ComponentLayer) => {
        if (page.type === "_page_") {
            const pageLayer = page as unknown as PageLayer;
            const transformedPageLayer: ComponentLayer = {
                type: "div",
                children: pageLayer.children,
                id: pageLayer.id,
                name: pageLayer.name,
                //map keys called borderRadius, colorTheme, mode to data-border-radius, data-color-theme, data-mode
                props: Object.fromEntries(Object.entries(pageLayer.props).map(([key, value]) => {
                    if (keysToMap[key as keyof typeof keysToMap]) {
                        return [keysToMap[key as keyof typeof keysToMap], value];
                    }
                    return [key, value];
                })),
              };
              console.log("Transformed page layer", transformedPageLayer);
              return transformedPageLayer;
        }
        return page;
      }) satisfies ComponentLayer[];


      return {
        ...migratedState,
        pages: migratedPages,
      } satisfies LayerStore;
}

/**
 * Creates a new component layer with default props and children initialized from the component registry.
 * This utility function consolidates the layer initialization logic used across the application.
 * 
 * @param layerType - The type of component to create
 * @param componentRegistry - The component registry containing component definitions
 * @param options - Optional configuration for the layer
 * @returns A new ComponentLayer with initialized props and children
 */
export const createComponentLayer = (
  layerType: string,
  componentRegistry: ComponentRegistry,
  options: {
    id?: string;
    name?: string;
    applyVariableBindings?: boolean;
    variables?: Array<{ id: string; defaultValue: any }>;
  } = {}
): ComponentLayer => {
  const { id, name, applyVariableBindings = false, variables = [] } = options;

  const componentDef = componentRegistry[layerType as keyof typeof componentRegistry];
  if (!componentDef) {
    throw new Error(`Component definition not found for type: ${layerType}`);
  }

  const schema = componentDef.schema;

  // Safely check if schema has shape property (ZodObject)
  const defaultProps = 'shape' in schema && schema.shape ? getDefaultProps(schema as any) : {};
  const defaultChildrenRaw = componentDef.defaultChildren;
  const defaultChildren = typeof defaultChildrenRaw === "string" 
    ? defaultChildrenRaw 
    : (defaultChildrenRaw?.map(child => duplicateWithNewIdsAndName(child, false)) || []);

  const initialProps = Object.entries(defaultProps).reduce((acc, [key, propDef]) => {
    if (key !== "children") {
      acc[key] = propDef;
    }
    return acc;
  }, {} as Record<string, any>);

  const newLayer: ComponentLayer = {
    id: id || createId(),
    type: layerType,
    name: name || layerType,
    props: initialProps,
    children: defaultChildren,
  };

  // Apply default variable bindings if requested
  if (applyVariableBindings) {
    const defaultVariableBindings = componentDef.defaultVariableBindings || [];

    for (const binding of defaultVariableBindings) {
      const variable = variables.find(v => v.id === binding.variableId);
      if (variable) {
        // Set the variable reference in the props
        newLayer.props[binding.propName] = { __variableRef: binding.variableId };
      }
    }
  }

  return newLayer;
};

