import { useState, useEffect } from 'react';

// prevents nextjs hydration error if using nextjs
export const useStore = <T, F>(
    store: (callback: (state: T) => unknown) => unknown,
    callback: (state: T) => F,
) => {
    const result = store(callback) as F
    const [data, setData] = useState<F>()

    useEffect(() => {
        setData(result)
    }, [result])

    return data
};

import { useEffect } from "react";

export interface KeyCombination {
  keys: {
    ctrlKey?: boolean;
    metaKey?: boolean;
    shiftKey?: boolean;
    altKey?: boolean;
  };
  key: string;
  handler: (event: KeyboardEvent) => void;
}

export function useKeyboardShortcuts(combinations: KeyCombination[]) {
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      combinations.forEach((combo) => {
        const { keys: { ctrlKey, metaKey, shiftKey, altKey }, key, handler } = combo;
        const isMatch =
          (ctrlKey === undefined || event.ctrlKey === ctrlKey) &&
          (metaKey === undefined || event.metaKey === metaKey) &&
          (shiftKey === undefined || event.shiftKey === shiftKey) &&
          (altKey === undefined || event.altKey === altKey) &&
          event.key.toLowerCase() === key.toLowerCase();

        if (isMatch) {
          event.preventDefault();
          handler(event);
        }
      });
    };

    window.addEventListener("keydown", handleKeyDown);

    // Cleanup event listener on unmount
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [combinations]);
}

import { useEffect, useState } from "react";

export function useDebounce<T>(value: T, delay?: number): T {
    const [debouncedValue, setDebouncedValue] = useState<T>(value);

    useEffect(() => {
        const timer = setTimeout(() => setDebouncedValue(value), delay || 500);

        return () => {
            clearTimeout(timer);
        };
    }, [value, delay]);

    return debouncedValue;
}

"use client";

import * as React from "react";

export interface useCopyToClipboardProps {
  timeout?: number;
}

export function useCopyToClipboard({
  timeout = 2000,
}: useCopyToClipboardProps) {
  const [isCopied, setIsCopied] = React.useState<boolean>(false);

  const copyToClipboard = (value: string) => {
    if (typeof window === "undefined" || !navigator.clipboard?.writeText) {
      return;
    }

    if (!value) {
      return;
    }

    navigator.clipboard.writeText(value).then(() => {
      setIsCopied(true);

      setTimeout(() => {
        setIsCopied(false);
      }, timeout);
    });
  };

  return { isCopied, copyToClipboard };
}

